   502→    /**
   503→     * Save changes to a specific version (like SOP saveVersion)
   504→     */
   505→    public function saveVersion(Request $request, BusinessProcessProcess $process, BusinessProcessCore $core, BusinessProcessContent $content, BusinessProcessContentVersion $version): RedirectResponse
   506→    {
   507→        $this->authorize('update', $core);
   508→
   509→        // Verify version belongs to this content
   510→        if ($version->content_id !== $content->id) {
   511→            abort(404, 'Version not found for this content');
   512→        }
   513→
   514→        $validated = $request->validate([
   515→            'content' => 'sometimes|required|string',
   516→            'title' => 'sometimes|string|max:255',
   517→        ]);
   518→
   519→        Log::info('saveVersion - Updating existing version', [
   520→            'content_id' => $content->id,
   521→            'version_id' => $version->id,
   522→            'content_length' => strlen($validated['content'] ?? ''),
   523→        ]);
   524→
   525→        // Update the specific version (working copy)
   526→        $updateData = [
   527→            'content' => $validated['content'] ?? $version->content,
   528→            'updated_by' => Auth::id(),
   529→        ];
   530→
   531→        $version->update($updateData);
   532→
   533→        // Update content metadata if title provided
   534→        if (isset($validated['title'])) {
   535→            $content->update([
   536→                'title' => $validated['title'],
   537→                'updated_by' => Auth::id(),
   538→            ]);
   539→        }
   540→
   541→        return back()->with('success', 'Version saved successfully.');
   542→    }
