   544→    /**
   545→     * Commit the current working copy as a new version (like SOP commit)
   546→     */
   547→    public function commit(Request $request, BusinessProcessProcess $process, BusinessProcessCore $core): RedirectResponse
   548→    {
   549→        $this->authorize('update', $core);
   550→
   551→        $validated = $request->validate([
   552→            'content_type' => 'required|string|in:sop,visual_diagram,swimlane',
   553→            'content' => 'required|string',
   554→            'title' => 'required|string|max:255',
   555→            'change_summary' => 'required|string',
   556→        ]);
   557→
   558→        Log::info('commit - Creating new version', [
   559→            'core_id' => $core->id,
   560→            'content_type' => $validated['content_type'],
   561→            'content_length' => strlen($validated['content']),
   562→        ]);
   563→
   564→        DB::transaction(function () use ($core, $validated) {
   565→            // Get or create content record
   566→            $content = $core->contents()->where('type', $validated['content_type'])->first();
   567→
   568→            if (!$content) {
   569→                $content = $core->contents()->create([
   570→                    'type' => $validated['content_type'],
   571→                    'title' => $validated['title'],
   572→                    'created_by' => Auth::id(),
   573→                    'updated_by' => Auth::id(),
   574→                ]);
   575→            } else {
   576→                $content->update([
