   674→    public function updateVersionStatus(Request $request, BusinessProcessProcess $process, BusinessProcessCore $core, BusinessProcessContent $content, BusinessProcessContentVersion $version): RedirectResponse
   675→    {
   676→        $this->authorize('update', $core);
   677→
   678→        // Verify version belongs to this content
   679→        if ($version->content_id !== $content->id) {
   680→            abort(404, 'Version not found for this content');
   681→        }
   682→
   683→        $validated = $request->validate([
   684→            'status' => 'required|integer|in:0,1,2',
   685→        ]);
   686→
   687→        $newStatus = $validated['status'];
   688→
   689→        DB::transaction(function () use ($version, $content, $newStatus) {
   690→            // Handle status constraints
   691→            if ($newStatus === 1) { // Current
   692→                // Set all other versions to draft (0)
   693→                $content->versions()->where('id', '!=', $version->id)->update([
   694→                    'status' => 0,
   695→                    'updated_by' => Auth::id()
   696→                ]);
   697→            } elseif ($newStatus === 2) { // Future
   698→                // Set all other future versions to draft (0)
   699→                $content->versions()
   700→                    ->where('id', '!=', $version->id)
   701→                    ->where('status', 2)
   702→                    ->update([
   703→                        'status' => 0,
   704→                        'updated_by' => Auth::id()
   705→                    ]);
   706→            }
   707→
   708→            // Update the target version
   709→            $version->update([
   710→                'status' => $newStatus,
   711→                'updated_by' => Auth::id()
   712→            ]);
   713→        });
