  1087→    private function findOrCreateCategory(BusinessProcessCore $core, string $categoryName): int
  1088→    {
  1089→        // Normalize input: trim whitespace
  1090→        $categoryName = trim($categoryName);
  1091→
  1092→        if (empty($categoryName)) {
  1093→            throw new \InvalidArgumentException('Category name cannot be empty');
  1094→        }
  1095→
  1096→        // Search for existing category (case-insensitive)
  1097→        $existingCategory = $core->activityCategories()
  1098→            ->whereRaw('LOWER(name) = ?', [strtolower($categoryName)])
  1099→            ->first();
  1100→
  1101→        if ($existingCategory) {
