import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';
import { BusinessProcessActivity, BusinessProcessActivityCategory, BusinessProcessCore } from '@/types';
import { router } from '@inertiajs/react';
import { ArrowRight, Eye } from 'lucide-react';
import { route } from 'ziggy-js';

interface VisualTabContentProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
        activities?: BusinessProcessActivity[];
        activityCategories?: BusinessProcessActivityCategory[];
        activity_categories?: BusinessProcessActivityCategory[]; // Laravel snake_case serialization
    };
}

// Category color mapping
const CATEGORY_COLORS = [
    { gradient: 'from-red-600 to-red-700', border: 'border-red-700', shadow: 'shadow-red-500/30' },
    { gradient: 'from-green-600 to-green-700', border: 'border-green-700', shadow: 'shadow-green-500/30' },
    { gradient: 'from-orange-600 to-orange-700', border: 'border-orange-700', shadow: 'shadow-orange-500/30' },
    { gradient: 'from-purple-600 to-purple-700', border: 'border-purple-700', shadow: 'shadow-purple-500/30' },
    { gradient: 'from-blue-600 to-blue-700', border: 'border-blue-700', shadow: 'shadow-blue-500/30' },
    { gradient: 'from-indigo-600 to-indigo-700', border: 'border-indigo-700', shadow: 'shadow-indigo-500/30' },
];

const getCategoryColor = (index: number) => {
    return CATEGORY_COLORS[index % CATEGORY_COLORS.length];
};

const parseRaciField = (field?: string): string[] => {
    return (
        field
            ?.split(',')
            .map((s) => s.trim())
            .filter((s) => s) || []
    );
};

const formatRaciAssignments = (activity: BusinessProcessActivity) => {
    const parts: string[] = [];

    const responsible = parseRaciField(activity.responsible);
    const accountable = parseRaciField(activity.accountable);
    const consulted = parseRaciField(activity.consulted);

    if (responsible.length > 0) {
        parts.push(`R: ${responsible.join(', ')}`);
    }
    if (accountable.length > 0) {
        parts.push(`A: ${accountable.join(', ')}`);
    }
    if (consulted.length > 0) {
        parts.push(`C: ${consulted.join(', ')}`);
    }

    return parts.join(' | ');
};

export function VisualTabContent({ core }: VisualTabContentProps) {
    const activities = core.activities || [];
    console.log('Activities:', activities);
    // Laravel converts camelCase to snake_case in serialization
    const categories = core.activityCategories || core.activity_categories || [];
    console.log('Categories:', categories);

    // Group activities by category
    const activitiesByCategory = activities.reduce(
        (acc, activity) => {
            const categoryId = activity.activity_category_id || 'uncategorized';
            if (!acc[categoryId]) {
                acc[categoryId] = [];
            }
            acc[categoryId].push(activity);
            return acc;
        },
        {} as Record<string | number, BusinessProcessActivity[]>,
    );

    // Sort activities within each category by position
    Object.keys(activitiesByCategory).forEach((categoryId) => {
        activitiesByCategory[categoryId].sort((a, b) => a.position - b.position);
    });

    const handleActivityClick = (activity: BusinessProcessActivity) => {
        // Navigate to blueprint page
        const blueprintRoute = route('business-processes.blueprints.show', {
            process: core.process.id,
            core: core.id,
            activity: activity.id,
        });
        router.visit(blueprintRoute);
    };

    const handleManageActivities = () => {
        // Switch to RACI tab to manage activities
        const currentUrl = new URL(window.location.href);
        currentUrl.searchParams.set('tab', 'raci');
        router.visit(currentUrl.toString());
    };

    if (activities.length === 0) {
        return (
            <div className="flex h-full flex-col">
                <div className="flex-1 overflow-auto">
                    <div className="h-full">
                        <div className="flex h-full flex-col bg-white">
                            <div className="flex h-full flex-1 items-center justify-center p-6">
                                <div className="text-center">
                                    <Eye className="mx-auto mb-4 h-16 w-16 text-gray-300" />
                                    <h3 className="mb-2 text-lg font-medium text-gray-900">Visual Diagram for {core.name}</h3>
                                    <p className="mx-auto max-w-md text-gray-500">
                                        No activities available. Add activities with RACI assignments to generate a visual process flow.
                                    </p>
                                    <Button onClick={handleManageActivities} className="mt-4">
                                        Manage Activities
                                    </Button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="flex flex-col">
            {/* Main content area with gradient background */}
            <div className="flex-1 bg-white">
                <div className="mx-auto max-w-7xl p-6">
                    <div className="rounded-2xl border border-gray-200 p-8">
                        <h2 className="mb-8 text-center text-3xl font-bold text-gray-800">{core.name}: Process Flow</h2>

                        {/* Process sections */}
                        {categories.length > 0 ? (
                            // Render categories with their activities
                            categories
                                .sort((a: BusinessProcessActivityCategory, b: BusinessProcessActivityCategory) => a.position - b.position)
                                .map((category: BusinessProcessActivityCategory, categoryIndex: number) => {
                                    const categoryActivities = activitiesByCategory[category.id] || [];
                                    if (categoryActivities.length === 0) return null;

                                    const colors = getCategoryColor(categoryIndex);

                                    return (
                                        <div key={category.id} className="space-y-20">
                                            <div className="group relative">
                                                <div
                                                    className={cn(
                                                        'relative cursor-pointer rounded-lg bg-gradient-to-r p-4 text-white shadow-lg transition-all hover:-translate-y-1',
                                                        colors.gradient,
                                                        colors.border,
                                                        colors.shadow,
                                                        'border-2',
                                                    )}
                                                >
                                                    <h3 className="text-xl font-bold">{category.name}</h3>
                                                </div>
                                            </div>

                                            <div className="mb-20 grid grid-cols-1 justify-items-center gap-8 md:grid-cols-2 lg:grid-cols-3 auto-rows-fr">
                                                {categoryActivities.map((activity, index) => (
                                                    <div key={activity.id} className="relative w-full h-full">
                                                        <div
                                                            onClick={() => handleActivityClick(activity)}
                                                            className="group relative w-full h-full min-h-[220px] cursor-pointer rounded-xl border-4 border-blue-600 bg-white p-5 shadow-lg transition-all hover:-translate-y-1 hover:shadow-xl flex flex-col"
                                                        >
                                                            <div className="absolute -top-3 left-4 rounded bg-blue-600 px-2 py-1 text-xs font-bold text-white">
                                                                QUICK GUIDE
                                                            </div>

                                                            <h3 className="mb-3 text-lg leading-tight font-bold text-gray-800">{activity.name}</h3>

                                                            {/* Activity details */}
                                                            <p className="mb-4 text-sm leading-relaxed text-gray-600">
                                                                Process step for {activity.name}
                                                            </p>

                                                            {/* RACI assignments */}
                                                            <div className="rounded-md border-l-4 border-blue-600 bg-gray-50 p-3 text-xs text-gray-700">
                                                                {formatRaciAssignments(activity)}
                                                            </div>

                                                            {/* Arrow indicator */}
                                                            {index < categoryActivities.length - 1 && (
                                                                <div className="absolute top-1/2 -right-6 z-10 -translate-y-1/2">
                                                                    <ArrowRight className="h-8 w-8 text-gray-400" />
                                                                </div>
                                                            )}
                                                        </div>
                                                    </div>
                                                ))}
                                            </div>
                                        </div>
                                    );
                                })
                        ) : (
                            // Render all activities in a single group if no categories
                            <div className="mb-10">
                                <div className="mb-6">
                                    <div className="relative rounded-lg bg-gradient-to-r from-blue-600 to-blue-700 p-4 text-white shadow-lg">
                                        <h2 className="text-xl font-bold">Process Activities</h2>
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 justify-items-center gap-8 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
                                    {activities
                                        .sort((a, b) => a.position - b.position)
                                        .map((activity, index) => (
                                            <div key={activity.id} className="relative">
                                                <div
                                                    onClick={() => handleActivityClick(activity)}
                                                    className="group relative w-full cursor-pointer rounded-xl border-4 border-blue-600 bg-white p-5 shadow-lg transition-all hover:-translate-y-1 hover:shadow-xl"
                                                >
                                                    <div className="absolute -top-3 left-4 rounded bg-blue-600 px-2 py-1 text-xs font-bold text-white">
                                                        QUICK GUIDE
                                                    </div>

                                                    <h3 className="mb-3 text-lg leading-tight font-bold text-gray-800">{activity.name}</h3>

                                                    <p className="mb-4 text-sm leading-relaxed text-gray-600">Process step for {activity.name}</p>

                                                    <div className="rounded-md border-l-4 border-blue-600 bg-gray-50 p-3 text-xs text-gray-700">
                                                        {formatRaciAssignments(activity)}
                                                    </div>

                                                    {index < activities.length - 1 && (
                                                        <div className="absolute top-1/2 -right-6 z-10 -translate-y-1/2">
                                                            <ArrowRight className="h-8 w-8 text-gray-400" />
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                        ))}
                                </div>
                            </div>
                        )}

                        {/* Process Flow Insights */}
                        <div className="mt-8 rounded-lg border-l-4 border-blue-600 bg-gray-50 p-6">
                            <h3 className="mb-4 text-xl font-bold text-gray-800">Process Flow Insights:</h3>
                            <ul className="space-y-2 text-gray-700">
                                <li className="flex items-start">
                                    <span className="mr-2 font-semibold">•</span>
                                    <span>
                                        <strong>Total Activities:</strong> {activities.length} process steps defined
                                    </span>
                                </li>
                                <li className="flex items-start">
                                    <span className="mr-2 font-semibold">•</span>
                                    <span>
                                        <strong>Categories:</strong>{' '}
                                        {categories.length > 0 ? `${categories.length} process phases` : 'No categories defined'}
                                    </span>
                                </li>
                                <li className="flex items-start">
                                    <span className="mr-2 font-semibold">•</span>
                                    <span>
                                        <strong>RACI Coverage:</strong> Clear accountability and responsibility assignments
                                    </span>
                                </li>
                                <li className="flex items-start">
                                    <span className="mr-2 font-semibold">•</span>
                                    <span>
                                        <strong>Process Flow:</strong> Sequential progression through defined activities
                                    </span>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

