   286→        // Navigate to pending URL if there was one
   287→        if (pendingNavigation) {
   288→            router.visit(pendingNavigation);
   289→        }
   290→    };
   291→
   292→    const handleSaveFromDialog = () => {
   293→        setShowUnsavedChangesDialog(false);
   294→        handleSave();
   295→
   296→        // Navigate to pending URL after saving
   297→        if (pendingNavigation) {
   298→            router.visit(pendingNavigation);
   299→        }
   300→    };
   301→
   302→    // Status change handlers
   303→    const handleStatusChange = (version: BaseVersion) => {
   304→        setSelectedVersionForStatus(version as ContentVersion);
   305→        setStatusData('status', version.status);
   306→        setShowStatusDialog(true);
   307→    };
   308→
   309→    const handleStatusUpdate = () => {
   310→        if (!selectedVersionForStatus) return;
   311→
   312→        const updateData = {
   313→            status: statusData.status,
   314→        };
   315→
   316→        const routeUrl = route('business-processes.contents.versions.update-status', [
   317→            core.process.id,
   318→            core.id,
   319→            content?.id,
   320→            selectedVersionForStatus.id,
   321→        ]);
   322→
   323→        router.put(routeUrl, updateData, {
   324→            preserveScroll: true,
   325→            preserveState: false, // Force data refresh to reflect status changes
   326→            onSuccess: () => {
   327→                setShowStatusDialog(false);
   328→                setSelectedVersionForStatus(null);
   329→                resetStatusData();
   330→            },
   331→            onError: () => {
   332→                toast.error('Failed to update status');
   333→            },
   334→        });
   335→    };
   336→
   337→    const getStatusOptions = () => [
   338→        { value: 0, label: 'Work in Progress' },
   339→        { value: 1, label: 'Current' },
   340→        { value: 2, label: 'Future' },
   341→    ];
   342→
   343→    return (
   344→        <div className="flex h-full overflow-hidden">
   345→            {/* Version History Sidebar */}
   346→            <div className={`flex flex-col bg-gray-50 transition-all duration-300 ${showVersionSidebar ? 'w-80' : 'w-10'}`}>
   347→                {showVersionSidebar ? (
   348→                    <>
   349→                        <div className="flex-shrink-0 p-4">
   350→                            <div className="mb-4 flex items-center justify-between">
   351→                                <h3 className="text-lg font-semibold text-gray-900">Version History</h3>
   352→                                <Button variant="ghost" size="sm" onClick={toggleVersionSidebar} className="h-8 w-8 p-0">
   353→                                    <ChevronLeft className="h-4 w-4" />
   354→                                </Button>
   355→                            </div>
   356→                        </div>
   357→
   358→                        <div className="flex-1 overflow-y-auto px-4 pb-4">
   359→                            <div className="space-y-3">
   360→                                {allVersions.map((version: ContentVersion) => (
   361→                                    <VersionCard
   362→                                        key={version.id}
   363→                                        version={version}
   364→                                        isCurrentVersion={version.status === 1}
   365→                                        isViewingThisVersion={version.id === viewingVersion?.id}
   366→                                        getStatusBadge={getStatusBadge}
   367→                                        href={route('business-processes.contents.versions.show', [core.process.id, core.id, content?.id, version.id])}
   368→                                        SafeLink={SafeLink}
   369→                                        onStatusChange={handleStatusChange}
   370→                                        showStatusButton={true}
   371→                                    />
   372→                                ))}
   373→                                {allVersions.length === 0 && (
   374→                                    <div className="py-8 text-center text-gray-500">
   375→                                        <FileText className="mx-auto mb-4 h-12 w-12 text-gray-300" />
   376→                                        <p>No versions yet</p>
   377→                                        <p className="text-sm">Create your first SOP version</p>
   378→                                    </div>
   379→                                )}
   380→                            </div>
   381→                        </div>
   382→                    </>
   383→                ) : (
   384→                    <div className="flex h-full items-center justify-center border-r border-gray-200">
   385→                        <Button
   386→                            variant="ghost"
   387→                            size="sm"
   388→                            onClick={toggleVersionSidebar}
   389→                            className="h-8 w-8 rotate-180 p-0"
   390→                            title="Show Version History"
   391→                        >
   392→                            <ChevronLeft className="h-4 w-4" />
   393→                        </Button>
   394→                    </div>
   395→                )}
   396→            </div>
   397→
   398→            {/* Main Content Area */}
   399→            <div className="flex flex-1 flex-col overflow-hidden">
   400→                {/* Header - Conditionally rendered */}
   401→                {showHeader && (
   402→                    <div className="flex-shrink-0 border-b border-gray-200 bg-white px-6 py-4">
   403→                        <div className="flex items-center justify-between">
   404→                            <div className="flex items-center gap-3">
   405→                                <FileText className="h-5 w-5 text-gray-500" />
   406→                                <div>
   407→                                    <h3 className="text-lg font-semibold text-gray-900">{core.name}</h3>
   408→                                    <p className="text-sm text-gray-500">
   409→                                        {viewingVersion ? `Version ${viewingVersion.version_number}` : 'New Version'}
   410→                                    </p>
   411→                                </div>
   412→                            </div>
   413→                            <div className="flex items-center gap-2">
   414→                                {viewingVersion && (
   415→                                    <Badge variant={getStatusBadge(viewingVersion.status).props.variant}>
   416→                                        {getStatusString(viewingVersion.status)}
   417→                                    </Badge>
   418→                                )}
   419→                                {hasUnsavedChanges && (
   420→                                    <Badge variant="outline" className="bg-yellow-50 text-yellow-700">
   421→                                        Unsaved changes
   422→                                    </Badge>
   423→                                )}
   424→                                <Button onClick={handleSave} disabled={mainFormProcessing} size="sm" variant="outline">
   425→                                    <Save className="mr-2 h-4 w-4" />
   426→                                    {!viewingVersion ? 'Create SOP' : mainFormProcessing ? 'Saving...' : 'Save Changes'}
   427→                                </Button>
   428→                                {viewingVersion && (
   429→                                    <Button onClick={handleCommit} disabled={mainFormProcessing} size="sm">
   430→                                        <GitCommit className="mr-2 h-4 w-4" />
   431→                                        New Version
   432→                                    </Button>
   433→                                )}
   434→                                <Button onClick={toggleMetadataSidebar} size="sm" variant="ghost">
   435→                                    <Settings className="h-4 w-4" />
   436→                                </Button>
   437→                            </div>
   438→                        </div>
   439→                    </div>
   440→                )}
   441→
   442→                {/* Content Body - Scrollable */}
   443→                <div className="min-h-0 flex-1 overflow-auto">
   444→                    <div className="h-full">
   445→                        {/* SOP Content */}
   446→                        <div className="flex h-full flex-col bg-white">
   447→                            {/* Content Area - Scrollable */}
   448→                            <div className="h-full flex-1">
   449→                                <RichTextEditor
   450→                                    ref={editorRef}
   451→                                    content={editorContent}
   452→                                    onChange={handleEditorChange}
   453→                                    placeholder="Start documenting your standard operating procedure..."
   454→                                    className="h-full"
   455→                                />
   456→                            </div>
   457→                        </div>
   458→                    </div>
   459→                </div>
   460→            </div>
   461→
   462→            {/* Floating Action Buttons - Only shown when header is hidden */}
   463→            {!showHeader && (
   464→                <div className="fixed right-6 bottom-6 z-50 flex flex-col gap-2">
   465→                    {hasUnsavedChanges && (
   466→                        <div className="flex items-center gap-2 rounded-lg border border-yellow-200 bg-yellow-50 px-3 py-2 text-sm text-yellow-800 shadow-lg">
   467→                            <div className="h-2 w-2 rounded-full bg-yellow-500"></div>
   468→                            Unsaved changes
   469→                        </div>
   470→                    )}
   471→                    <div className="flex gap-2">
   472→                        <Button onClick={handleSave} disabled={mainFormProcessing} size="sm" variant="outline" className="bg-white shadow-lg">
   473→                            <Save className="mr-2 h-4 w-4" />
   474→                            {!viewingVersion ? 'Create SOP' : mainFormProcessing ? 'Saving...' : 'Save Changes'}
   475→                        </Button>
   476→                        {viewingVersion && (
   477→                            <Button onClick={handleCommit} disabled={mainFormProcessing} size="sm" className="shadow-lg">
   478→                                <GitCommit className="mr-2 h-4 w-4" />
   479→                                New Version
   480→                            </Button>
   481→                        )}
   482→                    </div>
   483→                </div>
   484→            )}
   485→
   486→            {/* Commit Dialog */}
   487→            <Dialog open={showCommitDialog} onOpenChange={setShowCommitDialog}>
   488→                <DialogContent>
   489→                    <DialogHeader>
   490→                        <DialogTitle className="flex items-center">
   491→                            <GitCommit className="mr-2 h-5 w-5" />
   492→                            Commit New Version
   493→                        </DialogTitle>
   494→                        <DialogDescription>
   495→                            You're about to create a new version of this SOP. Please provide a summary of what changed.
   496→                        </DialogDescription>
   497→                    </DialogHeader>
   498→
   499→                    <div className="space-y-4">
   500→                        <div>
   501→                            <Label htmlFor="commit_summary">Change Summary *</Label>
   502→                            <Textarea
   503→                                id="commit_summary"
   504→                                ref={commitSummaryRef}
   505→                                onChange={() => {
   506→                                    const text = commitSummaryRef.current?.value?.trim() || '';
   507→                                    setIsCommitValid(text.length > 0);
   508→                                }}
   509→                                onBlur={() => {
   510→                                    const text = commitSummaryRef.current?.value?.trim() || '';
   511→                                    setIsCommitValid(text.length > 0);
   512→                                }}
   513→                                placeholder="Describe what changes were made..."
   514→                                rows={4}
   515→                                className="mt-1"
   516→                            />
   517→                        </div>
   518→
   519→                        <div className="rounded-lg bg-blue-50 p-3">
   520→                            <p className="text-sm text-blue-800">
   521→                                A new version will be created:{' '}
   522→                                <strong>v{Math.max(...allVersions.map((v: ContentVersion) => v.version_number), 0) + 1}</strong>
   523→                            </p>
   524→                        </div>
   525→                    </div>
   526→
   527→                    <DialogFooter>
   528→                        <Button type="button" variant="outline" onClick={handleCommitCancel}>
   529→                            Cancel
   530→                        </Button>
   531→                        <Button type="button" onClick={handleCommitConfirm} disabled={!isCommitValid || mainFormProcessing}>
   532→                            {mainFormProcessing ? 'Creating Version...' : 'Create Version'}
   533→                        </Button>
   534→                    </DialogFooter>
   535→                </DialogContent>
   536→            </Dialog>
   537→
   538→            {/* Unsaved Changes Dialog */}
   539→            <Dialog open={showUnsavedChangesDialog} onOpenChange={setShowUnsavedChangesDialog}>
   540→                <DialogContent>
   541→                    <DialogHeader>
   542→                        <DialogTitle className="flex items-center">
   543→                            <AlertTriangle className="mr-2 h-5 w-5" />
   544→                            Unsaved Changes
   545→                        </DialogTitle>
   546→                        <DialogDescription>You have unsaved changes. What would you like to do?</DialogDescription>
   547→                    </DialogHeader>
   548→
   549→                    <DialogFooter>
   550→                        <Button
   551→                            type="button"
   552→                            variant="outline"
   553→                            onClick={() => {
   554→                                setShowUnsavedChangesDialog(false);
   555→                                setPendingNavigation(null);
   556→                            }}
   557→                        >
   558→                            Cancel
   559→                        </Button>
   560→                        <Button type="button" variant="destructive" onClick={handleDiscardChanges}>
   561→                            Discard Changes
   562→                        </Button>
   563→                        <Button type="button" onClick={handleSaveFromDialog}>
   564→                            Save & Continue
   565→                        </Button>
   566→                    </DialogFooter>
   567→                </DialogContent>
   568→            </Dialog>
   569→
   570→            {/* Status Change Dialog */}
   571→            <Dialog open={showStatusDialog} onOpenChange={setShowStatusDialog}>
   572→                <DialogContent>
   573→                    <DialogHeader>
   574→                        <DialogTitle className="flex items-center">
   575→                            <Settings className="mr-2 h-5 w-5" />
   576→                            Change Version Status
   577→                        </DialogTitle>
   578→                        <DialogDescription>
   579→                            {selectedVersionForStatus && <>Change the status of Version {selectedVersionForStatus.version_number}</>}
   580→                        </DialogDescription>
   581→                    </DialogHeader>
   582→
   583→                    <div className="space-y-4">
   584→                        <div>
   585→                            <Label htmlFor="status">Status</Label>
   586→                            <Select value={statusData.status.toString()} onValueChange={(value) => setStatusData('status', parseInt(value))}>
   587→                                <SelectTrigger className="mt-1">
   588→                                    <SelectValue placeholder="Select status" />
   589→                                </SelectTrigger>
   590→                                <SelectContent>
   591→                                    {getStatusOptions().map((option) => (
   592→                                        <SelectItem key={option.value} value={option.value.toString()}>
   593→                                            {option.label}
   594→                                        </SelectItem>
   595→                                    ))}
   596→                                </SelectContent>
   597→                            </Select>
   598→                        </div>
   599→
   600→                        {statusData.status === 1 && (
   601→                            <div className="rounded-lg bg-yellow-50 p-3">
   602→                                <p className="text-sm text-yellow-800">
   603→                                    <strong>Note:</strong> Setting this version as "Current" will automatically change any other current version to
   604→                                    "Work in Progress".
   605→                                </p>
   606→                            </div>
   607→                        )}
   608→
   609→                        {statusData.status === 2 && (
   610→                            <div className="rounded-lg bg-purple-50 p-3">
   611→                                <p className="text-sm text-purple-800">
   612→                                    <strong>Note:</strong> Setting this version as "Future" will automatically change any other future version to
   613→                                    "Work in Progress".
   614→                                </p>
   615→                            </div>
   616→                        )}
   617→                    </div>
   618→
   619→                    <DialogFooter>
   620→                        <Button type="button" variant="outline" onClick={() => setShowStatusDialog(false)}>
   621→                            Cancel
   622→                        </Button>
   623→                        <Button type="button" onClick={handleStatusUpdate} disabled={statusProcessing}>
   624→                            {statusProcessing ? 'Updating...' : 'Update Status'}
   625→                        </Button>
   626→                    </DialogFooter>
   627→                </DialogContent>
   628→            </Dialog>
   629→
   630→            {/* Errors */}
   631→            {errors && Object.keys(errors).length > 0 && (
   632→                <div className="fixed right-4 bottom-4 z-50">
   633→                    <Card className="max-w-md border-red-200 bg-red-50">
   634→                        <CardContent className="p-4">
   635→                            <div className="text-sm text-red-600">
   636→                                {Object.entries(errors).map(([key, message]) => (
   637→                                    <p key={key} className="mb-1">
   638→                                        <strong>{key}:</strong> {message}
   639→                                    </p>
   640→                                ))}
   641→                            </div>
   642→                        </CardContent>
