import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { RichTextEditor, RichTextEditorRef } from '@/components/ui/rich-text-editor';
import { VersionCard, type BaseVersion } from '@/components/ui/version-card';
import { BusinessProcessCore } from '@/types';
import { router, useForm, Link } from '@inertiajs/react';
import { Save, GitCommit, AlertTriangle, Settings, ChevronLeft, FileText } from 'lucide-react';
import { useState, useEffect, useRef } from 'react';
import { toast } from 'sonner';
import React from 'react';

interface ContentVersion {
    id: number;
    content: string;
    title: string;
    version_number: number;
    version_label: string;
    status: number;
    change_summary?: string;
    created_at: string;
    updated_at: string;
    creator?: {
        id: number;
        name: string;
    };
    // Additional properties from backend
    currentVersion?: ContentVersion;
    allVersions?: ContentVersion[];
}

interface SOPTabContentProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
    };
    content?: ContentVersion;
    versions?: ContentVersion[];
    errors?: Record<string, string>;
    showHeader?: boolean;
}

const getStatusString = (status: number): string => {
    switch (status) {
        case 0:
            return 'Draft';
        case 1:
            return 'Active';
        case 2:
            return 'Archived';
        default:
            return 'Unknown';
    }
};

const getStatusBadge = (status: number) => {
    const statusConfig = {
        0: { label: 'WIP', variant: 'secondary' as const, className: 'bg-orange-100 text-orange-800' },
        1: { label: 'Current', variant: 'default' as const, className: 'bg-green-100 text-green-800' },
        2: { label: 'Future', variant: 'outline' as const, className: 'bg-blue-100 text-blue-800' },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig[0];

    return (
        <Badge variant={config.variant} className={config.className}>
            {config.label}
        </Badge>
    );
};

export function SOPTabContent({ core, content, versions = [], errors, showHeader = true }: SOPTabContentProps) {
    const editorRef = useRef<RichTextEditorRef>(null);
    const commitSummaryRef = useRef<HTMLTextAreaElement>(null);

    // Sidebar toggle state with localStorage persistence
    const [showVersionSidebar, setShowVersionSidebar] = useState(() => {
        if (typeof window !== 'undefined') {
            const saved = localStorage.getItem('sop-version-sidebar-open');
            return saved !== null ? JSON.parse(saved) : true;
        }
        return true;
    });

    // Save sidebar state to localStorage
    useEffect(() => {
        if (typeof window !== 'undefined') {
            localStorage.setItem('sop-version-sidebar-open', JSON.stringify(showVersionSidebar));
        }
    }, [showVersionSidebar]);

    // State management
    const [editorContent, setEditorContent] = useState(content?.content || '');
    const [showMetadataSidebar, setShowMetadataSidebar] = useState(false);
    const [showCommitDialog, setShowCommitDialog] = useState(false);
    const [showUnsavedChangesDialog, setShowUnsavedChangesDialog] = useState(false);
    const [showStatusDialog, setShowStatusDialog] = useState(false);
    const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
    const [pendingNavigation, setPendingNavigation] = useState<string | null>(null);
    const [isCommitValid, setIsCommitValid] = useState(false);
    const [selectedVersionForStatus, setSelectedVersionForStatus] = useState<ContentVersion | null>(null);

    // Processing state for save operations (like Blueprint)
    const [mainFormProcessing, setMainFormProcessing] = useState(false);

    // Form management
    const { data: mainFormData } = useForm({
        content: editorContent,
        title: content?.title || `${core.name} - SOP`,
    });

    const {
        data: statusData,
        setData: setStatusData,
        processing: statusProcessing,
        reset: resetStatusData,
    } = useForm({
        status: 0,
    });

    // Get current and viewing versions (like SOP)
    const allVersions = content?.allVersions || versions || (content ? [content] : []);
    const viewingVersion = content?.currentVersion || content || allVersions[0];

    // Toggle functions
    const toggleVersionSidebar = () => setShowVersionSidebar(!showVersionSidebar);
    const toggleMetadataSidebar = () => {
        setShowMetadataSidebar(!showMetadataSidebar);
    };

    // Handle navigation with unsaved changes check (like SOP)
    const handleNavigation = (url: string): boolean => {
        if (hasUnsavedChanges) {
            setPendingNavigation(url);
            setShowUnsavedChangesDialog(true);
            return false; // Prevent navigation
        }
        return true; // Allow navigation
    };

    // Custom Link component that checks for unsaved changes (like SOP)
    const SafeLink = ({ href, className, children, ...props }: { href: string; className?: string; children: React.ReactNode }) => {
        const handleClick = (e: React.MouseEvent) => {
            if (!handleNavigation(href)) {
                e.preventDefault();
            }
        };

        return (
            <Link href={href} className={className} onClick={handleClick} {...props}>
                {children}
            </Link>
        );
    };

    // Custom onChange handler
    const handleEditorChange = (newContent: string) => {
        setEditorContent(newContent);

        // Immediately check for unsaved changes when content changes
        const contentChanges = newContent !== (content?.content || '');
        const titleChanges = mainFormData.title !== (content?.title || `${core.name} - SOP`);
        const hasChanges = contentChanges || titleChanges;
        setHasUnsavedChanges(hasChanges);
    };

    // Effect to sync editor content when content prop changes (after page reload)
    useEffect(() => {
        if (content?.content) {
            setEditorContent(content.content);
        }
    }, [content?.content]);

    // Effect to track unsaved changes (content + form data)
    useEffect(() => {
        const contentChanges = editorContent !== (content?.content || '');
        const titleChanges = mainFormData.title !== (content?.title || `${core.name} - SOP`);
        const hasChanges = contentChanges || titleChanges;

        setHasUnsavedChanges(hasChanges);
    }, [editorContent, content?.content, mainFormData.title, content?.title, core.name]);

    // Event handlers - Match Blueprint flow exactly
    const handleSave = () => {
        // Get content directly from editor if available, fallback to state
        const editorRefContent = editorRef.current?.getContent();
        const currentContent = editorRefContent || editorContent;

        setMainFormProcessing(true);

        if (!viewingVersion) {
            // No version exists - create first version via commit (like Blueprint)
            const saveData = {
                content_type: 'sop',
                content: currentContent,
                title: mainFormData.title,
                change_summary: 'Initial SOP creation',
            };

            router.put(route('business-processes.contents.commit', [core.process.id, core.id]), saveData, {
                onSuccess: () => {
                    setHasUnsavedChanges(false);
                },
                onError: () => {
                    toast.error('Failed to create SOP');
                },
                onFinish: () => {
                    setMainFormProcessing(false);
                },
            });
        } else {
            // Version exists - update it (like Blueprint save)
            const saveData = {
                content: currentContent,
                change_summary: 'Content updated',
            };

            router.put(route('business-processes.contents.versions.save', [core.process.id, core.id, content?.id, viewingVersion.id]), saveData, {
                onSuccess: () => {
                    setHasUnsavedChanges(false);
                },
                onError: () => {
                    toast.error('Failed to save SOP');
                },
                onFinish: () => {
                    setMainFormProcessing(false);
                },
            });
        }
    };

    const handleCommit = () => {
        setShowCommitDialog(true);
    };

    const handleCommitCancel = () => {
        setShowCommitDialog(false);
        setIsCommitValid(false);
    };

    const handleCommitConfirm = () => {
        const changeSummary = commitSummaryRef.current?.value?.trim() || '';
        // Get content directly from editor if available, fallback to state
        const editorRefContent = editorRef.current?.getContent();
        const currentContent = editorRefContent || editorContent;

        const commitData = {
            content_type: 'sop',
            content: currentContent,
            title: mainFormData.title,
            change_summary: changeSummary,
        };

        setMainFormProcessing(true);
        // Always use commit route for creating new versions (like Blueprint)
        router.put(route('business-processes.contents.commit', [core.process.id, core.id]), commitData, {
            onSuccess: () => {
                setShowCommitDialog(false);
                setHasUnsavedChanges(false);
                setPendingNavigation(null);

                // Clear the commit textarea
                if (commitSummaryRef.current) {
                    commitSummaryRef.current.value = '';
                }
                setIsCommitValid(false);
            },
            onError: () => {
                toast.error('Failed to create new version');
            },
            onFinish: () => {
                setMainFormProcessing(false);
            },
        });
    };

    const handleDiscardChanges = () => {
        setEditorContent(content?.content || '');
        setShowUnsavedChangesDialog(false);
        setHasUnsavedChanges(false);
        setPendingNavigation(null);

        // Navigate to pending URL if there was one
        if (pendingNavigation) {
            router.visit(pendingNavigation);
        }
    };

    const handleSaveFromDialog = () => {
        setShowUnsavedChangesDialog(false);
        handleSave();

        // Navigate to pending URL after saving
        if (pendingNavigation) {
            router.visit(pendingNavigation);
        }
    };

    // Status change handlers
    const handleStatusChange = (version: BaseVersion) => {
        setSelectedVersionForStatus(version as ContentVersion);
        setStatusData('status', version.status);
        setShowStatusDialog(true);
    };

    const handleStatusUpdate = () => {
        if (!selectedVersionForStatus) return;

        const updateData = {
            status: statusData.status,
        };

        const routeUrl = route('business-processes.contents.versions.update-status', [
            core.process.id,
            core.id,
            content?.id,
            selectedVersionForStatus.id,
        ]);

        router.put(routeUrl, updateData, {
            preserveScroll: true,
            preserveState: false, // Force data refresh to reflect status changes
            onSuccess: () => {
                setShowStatusDialog(false);
                setSelectedVersionForStatus(null);
                resetStatusData();
            },
            onError: () => {
                toast.error('Failed to update status');
            },
        });
    };

    const getStatusOptions = () => [
        { value: 0, label: 'Work in Progress' },
        { value: 1, label: 'Current' },
        { value: 2, label: 'Future' },
    ];

    return (
        <div className="flex h-full overflow-hidden">
            {/* Version History Sidebar */}
            <div className={`flex flex-col bg-gray-50 transition-all duration-300 ${showVersionSidebar ? 'w-80' : 'w-10'}`}>
                {showVersionSidebar ? (
                    <>
                        <div className="flex-shrink-0 p-4">
                            <div className="mb-4 flex items-center justify-between">
                                <h3 className="text-lg font-semibold text-gray-900">Version History</h3>
                                <Button variant="ghost" size="sm" onClick={toggleVersionSidebar} className="h-8 w-8 p-0">
                                    <ChevronLeft className="h-4 w-4" />
                                </Button>
                            </div>
                        </div>

                        <div className="flex-1 overflow-y-auto px-4 pb-4">
                            <div className="space-y-3">
                                {allVersions.map((version: ContentVersion) => (
                                    <VersionCard
                                        key={version.id}
                                        version={version}
                                        isCurrentVersion={version.status === 1}
                                        isViewingThisVersion={version.id === viewingVersion?.id}
                                        getStatusBadge={getStatusBadge}
                                        href={route('business-processes.contents.versions.show', [core.process.id, core.id, content?.id, version.id])}
                                        SafeLink={SafeLink}
                                        onStatusChange={handleStatusChange}
                                        showStatusButton={true}
                                    />
                                ))}
                                {allVersions.length === 0 && (
                                    <div className="py-8 text-center text-gray-500">
                                        <FileText className="mx-auto mb-4 h-12 w-12 text-gray-300" />
                                        <p>No versions yet</p>
                                        <p className="text-sm">Create your first SOP version</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    </>
                ) : (
                    <div className="flex h-full items-center justify-center border-r border-gray-200">
                        <Button
                            variant="ghost"
                            size="sm"
                            onClick={toggleVersionSidebar}
                            className="h-8 w-8 rotate-180 p-0"
                            title="Show Version History"
                        >
                            <ChevronLeft className="h-4 w-4" />
                        </Button>
                    </div>
                )}
            </div>

            {/* Main Content Area */}
            <div className="flex flex-1 flex-col overflow-hidden">
                {/* Header - Conditionally rendered */}
                {showHeader && (
                    <div className="flex-shrink-0 border-b border-gray-200 bg-white px-6 py-4">
                        <div className="flex items-center justify-between">
                            <div className="flex items-center gap-3">
                                <FileText className="h-5 w-5 text-gray-500" />
                                <div>
                                    <h3 className="text-lg font-semibold text-gray-900">{core.name}</h3>
                                    <p className="text-sm text-gray-500">
                                        {viewingVersion ? `Version ${viewingVersion.version_number}` : 'New Version'}
                                    </p>
                                </div>
                            </div>
                            <div className="flex items-center gap-2">
                                {viewingVersion && (
                                    <Badge variant={getStatusBadge(viewingVersion.status).props.variant}>
                                        {getStatusString(viewingVersion.status)}
                                    </Badge>
                                )}
                                {hasUnsavedChanges && (
                                    <Badge variant="outline" className="bg-yellow-50 text-yellow-700">
                                        Unsaved changes
                                    </Badge>
                                )}
                                <Button onClick={handleSave} disabled={mainFormProcessing} size="sm" variant="outline">
                                    <Save className="mr-2 h-4 w-4" />
                                    {!viewingVersion ? 'Create SOP' : mainFormProcessing ? 'Saving...' : 'Save Changes'}
                                </Button>
                                {viewingVersion && (
                                    <Button onClick={handleCommit} disabled={mainFormProcessing} size="sm">
                                        <GitCommit className="mr-2 h-4 w-4" />
                                        New Version
                                    </Button>
                                )}
                                <Button onClick={toggleMetadataSidebar} size="sm" variant="ghost">
                                    <Settings className="h-4 w-4" />
                                </Button>
                            </div>
                        </div>
                    </div>
                )}

                {/* Content Body - Scrollable */}
                <div className="min-h-0 flex-1 overflow-auto">
                    <div className="h-full">
                        {/* SOP Content */}
                        <div className="flex h-full flex-col bg-white">
                            {/* Content Area - Scrollable */}
                            <div className="h-full flex-1">
                                <RichTextEditor
                                    ref={editorRef}
                                    content={editorContent}
                                    onChange={handleEditorChange}
                                    placeholder="Start documenting your standard operating procedure..."
                                    className="h-full"
                                />
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Floating Action Buttons - Only shown when header is hidden */}
            {!showHeader && (
                <div className="fixed right-6 bottom-6 z-50 flex flex-col gap-2">
                    {hasUnsavedChanges && (
                        <div className="flex items-center gap-2 rounded-lg border border-yellow-200 bg-yellow-50 px-3 py-2 text-sm text-yellow-800 shadow-lg">
                            <div className="h-2 w-2 rounded-full bg-yellow-500"></div>
                            Unsaved changes
                        </div>
                    )}
                    <div className="flex gap-2">
                        <Button onClick={handleSave} disabled={mainFormProcessing} size="sm" variant="outline" className="bg-white shadow-lg">
                            <Save className="mr-2 h-4 w-4" />
                            {!viewingVersion ? 'Create SOP' : mainFormProcessing ? 'Saving...' : 'Save Changes'}
                        </Button>
                        {viewingVersion && (
                            <Button onClick={handleCommit} disabled={mainFormProcessing} size="sm" className="shadow-lg">
                                <GitCommit className="mr-2 h-4 w-4" />
                                New Version
                            </Button>
                        )}
                    </div>
                </div>
            )}

            {/* Commit Dialog */}
            <Dialog open={showCommitDialog} onOpenChange={setShowCommitDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <GitCommit className="mr-2 h-5 w-5" />
                            Commit New Version
                        </DialogTitle>
                        <DialogDescription>
                            You're about to create a new version of this SOP. Please provide a summary of what changed.
                        </DialogDescription>
                    </DialogHeader>

                    <div className="space-y-4">
                        <div>
                            <Label htmlFor="commit_summary">Change Summary *</Label>
                            <Textarea
                                id="commit_summary"
                                ref={commitSummaryRef}
                                onChange={() => {
                                    const text = commitSummaryRef.current?.value?.trim() || '';
                                    setIsCommitValid(text.length > 0);
                                }}
                                onBlur={() => {
                                    const text = commitSummaryRef.current?.value?.trim() || '';
                                    setIsCommitValid(text.length > 0);
                                }}
                                placeholder="Describe what changes were made..."
                                rows={4}
                                className="mt-1"
                            />
                        </div>

                        <div className="rounded-lg bg-blue-50 p-3">
                            <p className="text-sm text-blue-800">
                                A new version will be created:{' '}
                                <strong>v{Math.max(...allVersions.map((v: ContentVersion) => v.version_number), 0) + 1}</strong>
                            </p>
                        </div>
                    </div>

                    <DialogFooter>
                        <Button type="button" variant="outline" onClick={handleCommitCancel}>
                            Cancel
                        </Button>
                        <Button type="button" onClick={handleCommitConfirm} disabled={!isCommitValid || mainFormProcessing}>
                            {mainFormProcessing ? 'Creating Version...' : 'Create Version'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Unsaved Changes Dialog */}
            <Dialog open={showUnsavedChangesDialog} onOpenChange={setShowUnsavedChangesDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <AlertTriangle className="mr-2 h-5 w-5" />
                            Unsaved Changes
                        </DialogTitle>
                        <DialogDescription>You have unsaved changes. What would you like to do?</DialogDescription>
                    </DialogHeader>

                    <DialogFooter>
                        <Button
                            type="button"
                            variant="outline"
                            onClick={() => {
                                setShowUnsavedChangesDialog(false);
                                setPendingNavigation(null);
                            }}
                        >
                            Cancel
                        </Button>
                        <Button type="button" variant="destructive" onClick={handleDiscardChanges}>
                            Discard Changes
                        </Button>
                        <Button type="button" onClick={handleSaveFromDialog}>
                            Save & Continue
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Status Change Dialog */}
            <Dialog open={showStatusDialog} onOpenChange={setShowStatusDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <Settings className="mr-2 h-5 w-5" />
                            Change Version Status
                        </DialogTitle>
                        <DialogDescription>
                            {selectedVersionForStatus && <>Change the status of Version {selectedVersionForStatus.version_number}</>}
                        </DialogDescription>
                    </DialogHeader>

                    <div className="space-y-4">
                        <div>
                            <Label htmlFor="status">Status</Label>
                            <Select value={statusData.status.toString()} onValueChange={(value) => setStatusData('status', parseInt(value))}>
                                <SelectTrigger className="mt-1">
                                    <SelectValue placeholder="Select status" />
                                </SelectTrigger>
                                <SelectContent>
                                    {getStatusOptions().map((option) => (
                                        <SelectItem key={option.value} value={option.value.toString()}>
                                            {option.label}
                                        </SelectItem>
                                    ))}
                                </SelectContent>
                            </Select>
                        </div>

                        {statusData.status === 1 && (
                            <div className="rounded-lg bg-yellow-50 p-3">
                                <p className="text-sm text-yellow-800">
                                    <strong>Note:</strong> Setting this version as "Current" will automatically change any other current version to
                                    "Work in Progress".
                                </p>
                            </div>
                        )}

                        {statusData.status === 2 && (
                            <div className="rounded-lg bg-purple-50 p-3">
                                <p className="text-sm text-purple-800">
                                    <strong>Note:</strong> Setting this version as "Future" will automatically change any other future version to
                                    "Work in Progress".
                                </p>
                            </div>
                        )}
                    </div>

                    <DialogFooter>
                        <Button type="button" variant="outline" onClick={() => setShowStatusDialog(false)}>
                            Cancel
                        </Button>
                        <Button type="button" onClick={handleStatusUpdate} disabled={statusProcessing}>
                            {statusProcessing ? 'Updating...' : 'Update Status'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Errors */}
            {errors && Object.keys(errors).length > 0 && (
                <div className="fixed right-4 bottom-4 z-50">
                    <Card className="max-w-md border-red-200 bg-red-50">
                        <CardContent className="p-4">
                            <div className="text-sm text-red-600">
                                {Object.entries(errors).map(([key, message]) => (
                                    <p key={key} className="mb-1">
                                        <strong>{key}:</strong> {message}
                                    </p>
                                ))}
                            </div>
                        </CardContent>
                    </Card>
                </div>
            )}
        </div>
    );
}

