import React, { useRef, useState, forwardRef, useImperativeHandle } from 'react';
import { Editor } from '@tinymce/tinymce-react';
import { cn } from '@/lib/utils';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';

// Import types
import type {
    TinyMCEEditor,
    RichTextEditorProps
} from '@/types/rich-text-editor';

// Import configuration
import { getTinyMCEConfig, TINYMCE_EDITOR_STYLES } from '@/lib/tinymce-config';

// Interface for the exposed ref methods
export interface RichTextEditorRef {
    getContent: () => string;
    getEditor: () => TinyMCEEditor | null;
}

// Interface for component props
export interface RichTextEditorProps {
    content?: string;
    value?: string;
    onChange?: (content: string) => void;
    placeholder?: string;
    className?: string;
    editable?: boolean;
    currentUser?: { id: string; name: string };
    onAutoSave?: () => void;
    height?: number;
    readOnly?: boolean;
}

export const RichTextEditor = forwardRef<RichTextEditorRef, RichTextEditorProps>(({
    content,
    placeholder = 'Start typing...',
    className,
    editable = true,
    currentUser = { id: '1', name: 'Current User' },
    onAutoSave,
}, ref) => {
    const editorRef = useRef<TinyMCEEditor | null>(null);

    // Comment dialog state
    const [showCommentDialog, setShowCommentDialog] = useState(false);

    // Reply dialog state
    const [showReplyDialog, setShowReplyDialog] = useState(false);
    const [replyToCommentId, setReplyToCommentId] = useState<string | null>(null);
    const [replyToAuthor, setReplyToAuthor] = useState<string>('');

    // Delete confirmation dialog state
    const [showDeleteDialog, setShowDeleteDialog] = useState(false);
    const [commentToDelete, setCommentToDelete] = useState<string | null>(null);

    // Use ref for comment text to avoid re-renders
    const commentTextRef = useRef<HTMLTextAreaElement>(null);
    const replyTextRef = useRef<HTMLTextAreaElement>(null);
    const [isCommentValid, setIsCommentValid] = useState(false);
    const [isReplyValid, setIsReplyValid] = useState(false);

    // Function to check if comment text is valid
    const checkCommentValidity = () => {
        const text = commentTextRef.current?.value?.trim() || '';
        setIsCommentValid(text.length > 0);
    };

    // Function to check if reply text is valid
    const checkReplyValidity = () => {
        const text = replyTextRef.current?.value?.trim() || '';
        setIsReplyValid(text.length > 0);
    };

    // Function to render comments HTML
    const renderCommentsHTML = (annotations: Record<string, Node[]>): string => {
        const commentCount = Object.keys(annotations).length;

        if (commentCount === 0) {
            return ``;
        }

        const threadsHTML = Object.keys(annotations).map(uid => {
            const nodes = annotations[uid];
            const firstNode = nodes[0] as HTMLElement;

            // Try to get thread data from annotation
            const threadDataStr = firstNode?.getAttribute('data-thread') || '';
            let threadData;

            try {
                threadData = threadDataStr ? JSON.parse(threadDataStr) : null;
            } catch {
                threadData = null;
            }

            // If no thread data, treat as legacy single comment
            if (!threadData) {
                const comment = firstNode?.getAttribute('data-comment') || 'No comment';
                const author = firstNode?.getAttribute('data-author') || 'Anonymous';
                threadData = {
                    rootComment: {
                        id: uid,
                        comment: comment,
                        author: author,
                        timestamp: Date.now()
                    },
                    replies: []
                };
            }

            const { rootComment, replies = [] } = threadData;

            // Helper function to render a single comment
            const renderComment = (commentData: {
                id: string;
                comment: string;
                author: string;
                timestamp: number;
            }, isReply = false) => {
                const avatarLetter = commentData.author.charAt(0).toUpperCase();
                const date = new Date(commentData.timestamp || Date.now());
                const currentTime = date.toLocaleDateString() + ' - ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
                const marginLeft = isReply ? '32px' : '0';

                return `
                    <div
                        id="comment-card-${commentData.id}"
                        style="
                            margin-bottom: 0px;
                            margin-left: ${marginLeft};
                            cursor: pointer;
                            padding: 0;
                        "
                        onclick="window.navigateToComment('${uid}')"
                    >
                        <!-- Inner padding wrapper -->
                        <div style="padding: 16px;">
                            <!-- Main comment container -->
                            <div style="display: flex; align-items: flex-start; gap: 12px;">
                                <!-- Avatar -->
                                <div style="
                                    width: 32px;
                                    height: 32px;
                                    border-radius: 50%;
                                    background: #f3f4f6;
                                    color: #1f2937;
                                    display: flex;
                                    align-items: center;
                                    justify-content: center;
                                    font-weight: 600;
                                    font-size: 14px;
                                    flex-shrink: 0;
                                ">
                                    ${avatarLetter}
                                </div>

                                <!-- Comment content -->
                                <div style="flex: 1; min-width: 0;">
                                    <!-- Header with name and time -->
                                    <div style="
                                        display: flex;
                                        align-items: center;
                                        justify-content: space-between;
                                        margin-bottom: 8px;
                                    ">
                                        <span style="
                                            font-weight: 600;
                                            font-size: 14px;
                                            color: #111827;
                                        ">${commentData.author}</span>
                                        <span style="
                                            font-size: 12px;
                                            color: #9ca3af;
                                        ">${currentTime}</span>
                                    </div>

                                    <!-- Comment text -->
                                    <div style="
                                        font-size: 14px;
                                        line-height: 1.5;
                                        color: #374151;
                                        margin-bottom: 12px;
                                        word-wrap: break-word;
                                    ">
                                        ${commentData.comment.replace(/\n/g, '<br>')}
                                    </div>

                                    <!-- Action buttons -->
                                    <div style="display: flex; gap: 16px;">
                                        <button
                                            onclick="event.stopPropagation(); window.replyToComment('${commentData.id}', '${commentData.author}', '${uid}')"
                                            style="
                                                background: none;
                                                border: none;
                                                color: #6b7280;
                                                font-size: 12px;
                                                cursor: pointer;
                                                padding: 0;
                                                font-weight: 500;
                                                transition: color 0.2s;
                                            "
                                            onmouseover="this.style.color='#374151'"
                                            onmouseout="this.style.color='#6b7280'"
                                        >
                                            Reply
                                        </button>
                                        <button
                                            onclick="event.stopPropagation(); window.deleteComment('${commentData.id}', '${uid}')"
                                            style="
                                                background: none;
                                                border: none;
                                                color: #dc2626;
                                                font-size: 12px;
                                                cursor: pointer;
                                                padding: 0;
                                                font-weight: 500;
                                                transition: color 0.2s;
                                            "
                                            onmouseover="this.style.color='#b91c1c'"
                                            onmouseout="this.style.color='#dc2626'"
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                `;
            };

            // Render root comment and replies
            const rootHTML = renderComment(rootComment, false);
            const repliesHTML = replies.map((reply: {
                id: string;
                comment: string;
                author: string;
                timestamp: number;
            }) => renderComment(reply, true)).join('');

            return `
                <div style="
                    margin-bottom: 0;
                    border-bottom: 1px solid #e5e7eb;
                    padding-bottom: 0;
                ">
                    ${rootHTML}
                    ${repliesHTML}
                </div>
            `;
        }).join('');

        return `
            <div style="
                height: 100%;
                display: flex;
                flex-direction: column;
            ">
                <div style="
                    padding: 12px;
                    font-weight: 600;
                    font-size: 14px;
                    color: #333;
                    border-bottom: 1px solid #e5e7eb;
                    flex-shrink: 0;
                ">
                    Comments (${commentCount})
                </div>
                <div style="
                    flex: 1;
                    overflow-y: auto;
                    padding: 0;
                ">
                    ${threadsHTML}
                </div>
            </div>
        `;
    };

    // Expose methods to parent component
    useImperativeHandle(ref, () => {
        return {
            getContent: () => {
                const content = editorRef.current?.getContent() || '';
                return content;
            },
            getEditor: () => {
                return editorRef.current;
            }
        };
    });

    const handleCommentSubmit = () => {
        const commentText = commentTextRef.current?.value?.trim() || '';

        if (!commentText) return;

        const typedEditor = editorRef.current;
        if (typedEditor && typedEditor.annotator) {
            const uid = `comment-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

            // Create thread data structure
            const threadData = {
                rootComment: {
                    id: uid,
                    comment: commentText,
                    author: currentUser.name,
                    timestamp: Date.now()
                },
                replies: []
            };


            typedEditor.annotator.annotate('comment', {
                uid: uid,
                comment: commentText,
                author: currentUser.name,
                thread: JSON.stringify(threadData)
            });

            // Check annotations after adding
            setTimeout(() => {
                const annotations = typedEditor.annotator?.getAll('comment');

                // Refresh sidebar to show the new comment
                const refreshSidebar = () => {
                    if (typedEditor.annotator) {
                        const sidebarElement = document.querySelector('.tox-sidebar .tox-sidebar__pane-container');
                        if (sidebarElement) {
                            const currentAnnotations = typedEditor.annotator.getAll('comment') || {};
                            const html = renderCommentsHTML(currentAnnotations);
                            (sidebarElement as HTMLElement).innerHTML = html;
                        }

                        // Only show sidebar if it's not currently visible and this is the first comment
                        const sidebarWrapper = document.querySelector('.tox-sidebar-wrap');
                        const isFirstComment = Object.keys(typedEditor.annotator?.getAll('comment') || {}).length === 1;
                        const isSidebarVisible = sidebarWrapper && sidebarWrapper.classList.contains('tox-sidebar-wrap--visible');

                        if (isFirstComment && !isSidebarVisible) {
                            (typedEditor as TinyMCEEditor & { execCommand: (command: string, ui: boolean, value: string) => void }).execCommand('ToggleSidebar', false, 'comments');
                        }
                    }
                };

                refreshSidebar();
            }, 300);

            // Auto-save after adding comment if onAutoSave is provided
            if (onAutoSave) {
            setTimeout(() => {
                    onAutoSave();
                }, 500);
            }
        }

        setShowCommentDialog(false);
        if (commentTextRef.current) {
            commentTextRef.current.value = ''; // Clear the textarea
        }
        setIsCommentValid(false);

        // Focus the editor after adding comment
        setTimeout(() => {
            if (typedEditor) {
                const editorBody = typedEditor.getContainer().querySelector('iframe');
                if (editorBody) {
                    (editorBody as HTMLIFrameElement).contentWindow?.focus();
                }
            }
        }, 200);
    };

    const handleReplySubmit = () => {
        const replyText = replyTextRef.current?.value?.trim() || '';


        if (!replyText || !replyToCommentId) {
            return;
        }

        const typedEditor = editorRef.current;
        if (typedEditor && typedEditor.annotator) {
            const annotations = typedEditor.annotator.getAll('comment');

            // Find the annotation that contains this comment thread
            let targetAnnotationUid = '';
            let threadData = null;

            Object.keys(annotations).forEach(uid => {
                const nodes = annotations[uid];
                const firstNode = nodes[0] as HTMLElement;
                const threadDataStr = firstNode?.getAttribute('data-thread') || '';


                try {
                    const parsedThread = threadDataStr ? JSON.parse(threadDataStr) : null;
                    if (parsedThread) {
                        // Check if this thread contains our target comment
                        if (parsedThread.rootComment.id === replyToCommentId ||
                            parsedThread.replies.some((reply: {
                                id: string;
                                comment: string;
                                author: string;
                                timestamp: number;
                            }) => reply.id === replyToCommentId)) {
                            targetAnnotationUid = uid;
                            threadData = parsedThread;
                        }
                    }
                } catch (error) {
                    // Error parsing thread data
                }
            });

            if (targetAnnotationUid && threadData) {
                // Create new reply
                const replyId = `reply-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
                const newReply = {
                    id: replyId,
                    comment: replyText,
                    author: currentUser.name,
                    timestamp: Date.now(),
                    parentId: replyToCommentId
                };

                // Add reply to thread - using type assertion to handle JSON parsed data
                const updatedThreadData = {
                    rootComment: (threadData as any).rootComment,
                    replies: [...((threadData as any).replies || []), newReply]
                };


                // Update the annotation with new thread data
                const nodes = annotations[targetAnnotationUid];
                const firstNode = nodes[0] as HTMLElement;
                firstNode.setAttribute('data-thread', JSON.stringify(updatedThreadData));


                // Refresh sidebar
                setTimeout(() => {
                    const sidebarElement = document.querySelector('.tox-sidebar .tox-sidebar__pane-container');
                    if (sidebarElement && typedEditor.annotator) {
                        const currentAnnotations = typedEditor.annotator.getAll('comment') || {};
                        const html = renderCommentsHTML(currentAnnotations);
                        (sidebarElement as HTMLElement).innerHTML = html;
                    }

                    // Auto-save after adding reply
                    if (onAutoSave) {
                        setTimeout(() => {
                            onAutoSave();
                        }, 200);
                    }
                }, 100);
            } else {
                // Could not find target annotation or thread data
            }
        }

        // Reset reply dialog state
        setShowReplyDialog(false);
        setReplyToCommentId(null);
        setReplyToAuthor('');
        if (replyTextRef.current) {
            replyTextRef.current.value = '';
        }
        setIsReplyValid(false);
    };

    const handleReplyCancel = () => {
        setShowReplyDialog(false);
        setReplyToCommentId(null);
        setReplyToAuthor('');
        if (replyTextRef.current) {
            replyTextRef.current.value = '';
        }
        setIsReplyValid(false);
    };

    const handleCommentCancel = () => {
        setShowCommentDialog(false);
        if (commentTextRef.current) {
            commentTextRef.current.value = ''; // Clear the textarea
        }
        setIsCommentValid(false);
    };

    // Function to handle confirmed deletion
    const handleConfirmedDelete = () => {
        if (!commentToDelete) return;


        const typedEditor = editorRef.current;
        if (typedEditor && typedEditor.annotator) {
            const annotations = typedEditor.annotator.getAll('comment');

            // Find which annotation contains this comment/reply
            let targetAnnotationUid = '';
            let threadData = null;
            let isRootComment = false;

            Object.keys(annotations).forEach(uid => {
                const nodes = annotations[uid];
                const firstNode = nodes[0] as HTMLElement;
                const threadDataStr = firstNode?.getAttribute('data-thread') || '';

                try {
                    const parsedThread = threadDataStr ? JSON.parse(threadDataStr) : null;
                    if (parsedThread) {
                        // Check if it's the root comment
                        if (parsedThread.rootComment.id === commentToDelete) {
                            targetAnnotationUid = uid;
                            threadData = parsedThread;
                            isRootComment = true;
                        }
                        // Check if it's a reply
                        else if (parsedThread.replies.some((reply: {
                            id: string;
                            comment: string;
                            author: string;
                            timestamp: number;
                        }) => reply.id === commentToDelete)) {
                            targetAnnotationUid = uid;
                            threadData = parsedThread;
                            isRootComment = false;
                        }
                    }
                } catch (error) {
                    // Error parsing thread data
                }
            });

            if (targetAnnotationUid && threadData) {
                if (isRootComment) {
                    // Delete entire annotation for root comments
                    const nodes = annotations[targetAnnotationUid];
                    const firstNode = nodes[0] as HTMLElement;
                    const range = typedEditor.selection.getRng();
                    range.selectNode(firstNode);
                    typedEditor.selection.setRng(range);
                    typedEditor.annotator.remove('comment');
                } else {
                    // Update thread data for reply deletion
                    const updatedThreadData = {
                        rootComment: (threadData as any).rootComment,
                        replies: (threadData as any).replies.filter((reply: any) => reply.id !== commentToDelete)
                    };

                    // Update the annotation with new thread data
                    const nodes = annotations[targetAnnotationUid];
                    const firstNode = nodes[0] as HTMLElement;
                    firstNode.setAttribute('data-thread', JSON.stringify(updatedThreadData));
                }


                // Wait a bit for the changes to process
                setTimeout(() => {
                    const remainingAnnotations = typedEditor.annotator?.getAll('comment') || {};
                    const remainingCount = Object.keys(remainingAnnotations).length;


                    // Always refresh sidebar content first
                    const sidebarElement = document.querySelector('.tox-sidebar .tox-sidebar__pane-container');
                    if (sidebarElement && typedEditor.annotator) {
                        const html = renderCommentsHTML(remainingAnnotations);
                        (sidebarElement as HTMLElement).innerHTML = html;
                    }

                    // Then decide whether to hide sidebar or not
                    if (remainingCount === 0) {
                        // Hide sidebar if no comments left
                        const sidebarWrapper = document.querySelector('.tox-sidebar-wrap');
                        if (sidebarWrapper && sidebarWrapper.classList.contains('tox-sidebar-wrap--visible')) {
                            (typedEditor as TinyMCEEditor & { execCommand: (command: string, ui: boolean, value: string) => void }).execCommand('ToggleSidebar', false, 'comments');
                        }
                    }

                    // Auto-save after deletion if onAutoSave is provided
                    if (onAutoSave) {
                        setTimeout(() => {
                            onAutoSave();
                        }, 200);
                    }
                }, 300);
            } else {
                // Comment not found for deletion
            }
        }

        // Close dialog and reset state
        setShowDeleteDialog(false);
        setCommentToDelete(null);
    };

    // Function to cancel deletion
    const handleCancelDelete = () => {
        setShowDeleteDialog(false);
        setCommentToDelete(null);
    };

    const setupTinyMCEEditor = (editor: unknown) => {
        const typedEditor = editor as TinyMCEEditor;

        // Add comment button
        typedEditor.ui.registry.addButton('addcomment', {
            text: 'Add Comment',
            icon: 'comment',
            tooltip: 'Add comment to selected text',
            onAction: () => {
                const selection = typedEditor.selection.getContent({ format: 'text' });

                if (!selection.trim()) {
                    typedEditor.notificationManager.open({
                        text: 'Please select some text to comment on.',
                        type: 'warning'
                    });
                    return;
                }

                setShowCommentDialog(true);
            }
        });

        // Function to navigate to comment in content
        const navigateToComment = (uid: string) => {
            if (typedEditor.annotator) {
                const annotations = typedEditor.annotator.getAll('comment');
                const nodes = annotations[uid];
                if (nodes && nodes.length > 0) {
                    const firstNode = nodes[0] as HTMLElement;
                    // Scroll to the annotation
                    firstNode.scrollIntoView({ behavior: 'smooth', block: 'center' });

                    // Temporarily highlight the annotation
                    const originalBackground = firstNode.style.backgroundColor;
                    firstNode.style.backgroundColor = '#3b82f6';
                    firstNode.style.color = 'white';

                    setTimeout(() => {
                        firstNode.style.backgroundColor = originalBackground;
                        firstNode.style.color = '';
                    }, 1000);
                }
            }
        };

        // Function to resolve comment (placeholder)
        const resolveComment = (uid: string) => {
            // TODO: Add resolve logic here
        };

        // Function to reply to comment
        const replyToComment = (commentId: string, authorName: string, threadUid: string) => {
            setReplyToCommentId(commentId);
            setReplyToAuthor(authorName);
            setShowReplyDialog(true);
        };

        // Function to delete comment
        const deleteComment = (commentId: string, threadUid: string) => {
            setCommentToDelete(commentId);
            setShowDeleteDialog(true);
        };

        // Function to highlight comment card when annotation is clicked
        const highlightCommentCard = (uid: string) => {
            const card = document.getElementById(`comment-card-${uid}`);
            if (card) {
                // Remove highlight from all cards
                document.querySelectorAll('[id^="comment-card-"]').forEach(c => {
                    (c as HTMLElement).style.background = 'white';
                    (c as HTMLElement).style.borderColor = '#e5e7eb';
                });

                // Highlight selected card
                card.style.background = '#dbeafe';
                card.style.borderColor = '#3b82f6';

                // Scroll card into view in sidebar
                card.scrollIntoView({ behavior: 'smooth', block: 'nearest' });

                // Remove highlight after 3 seconds
                setTimeout(() => {
                    card.style.background = 'white';
                    card.style.borderColor = '#e5e7eb';
                }, 3000);
            }
        };

        // Function to refresh the sidebar content
        const refreshSidebar = () => {
            if (typedEditor.annotator) {
                const sidebarElement = document.querySelector('.tox-sidebar .tox-sidebar__pane-container');
                if (sidebarElement) {
                    const annotations = typedEditor.annotator.getAll('comment') || {};
                    const html = renderCommentsHTML(annotations);
                    (sidebarElement as HTMLElement).innerHTML = html;
                }
            }
        };

        // Function to update sidebar visibility and show if comments exist
        const updateSidebarVisibility = () => {
            if (typedEditor.annotator) {
                const annotations = typedEditor.annotator.getAll('comment');
                const hasComments = Object.keys(annotations).length > 0;

                if (hasComments) {
                    setTimeout(() => {
                        (typedEditor as TinyMCEEditor & { execCommand: (command: string, ui: boolean, value: string) => void }).execCommand('ToggleSidebar', false, 'comments');
                        // Refresh sidebar content after showing
                        setTimeout(() => refreshSidebar(), 100);
                    }, 100);
                }
            }
        };

        // Expose functions to global scope for onclick handlers
        interface WindowWithCommentFunctions extends Window {
            navigateToComment: (uid: string) => void;
            resolveComment: (uid: string) => void;
            replyToComment: (commentId: string, authorName: string, threadUid: string) => void;
            deleteComment: (commentId: string, threadUid: string) => void;
            highlightCommentCard: (uid: string) => void;
        }

        const windowWithFunctions = window as unknown as WindowWithCommentFunctions;
        windowWithFunctions.navigateToComment = navigateToComment;
        windowWithFunctions.resolveComment = resolveComment;
        windowWithFunctions.replyToComment = replyToComment;
        windowWithFunctions.deleteComment = deleteComment;
        windowWithFunctions.highlightCommentCard = highlightCommentCard;

        // Always register the comments sidebar during setup
        typedEditor.ui.registry.addSidebar('comments', {
            tooltip: 'Comments',
            icon: 'comment',
            onSetup: () => {
                return () => {
                    // Cleanup if needed
                };
            },
            onShow: (api: { element: () => HTMLElement }) => {
                // Update when sidebar is shown
                const currentAnnotations = typedEditor.annotator?.getAll('comment') || {};
                const html = renderCommentsHTML(currentAnnotations);
                api.element().innerHTML = html;
                return () => {};
            },
            onHide: () => {
                // Called when sidebar is hidden
            }
        });

        // Wait for editor to be fully initialized
        typedEditor.on('init', () => {
            // Register comment annotation
            if (typedEditor.annotator) {
                typedEditor.annotator.register('comment', {
                    persistent: true,
                    decorate: (uid: string, data: Record<string, unknown>) => ({
                        attributes: {
                            'data-comment': data.comment ? String(data.comment) : '',
                            'data-author': data.author ? String(data.author) : 'anonymous',
                            'title': `${data.author || 'anonymous'}: ${data.comment || ''}`,
                            'data-uid': uid
                        },
                        classes: ['comment-annotation']
                    })
                });

                // Check for existing annotations and show sidebar if any exist
                setTimeout(() => {

                    if (typedEditor.annotator) {
                        const annotations = typedEditor.annotator.getAll('comment');

                        // Migrate existing comments to thread format
                        Object.keys(annotations).forEach(uid => {
                            const nodes = annotations[uid];
                            const firstNode = nodes[0] as HTMLElement;
                            const threadDataStr = firstNode?.getAttribute('data-thread') || '';

                            // If no thread data exists, migrate from legacy format
                            if (!threadDataStr) {
                                const comment = firstNode?.getAttribute('data-comment') || 'No comment';
                                const author = firstNode?.getAttribute('data-author') || 'Anonymous';

                                const threadData = {
                                    rootComment: {
                                        id: uid,
                                        comment: comment,
                                        author: author,
                                        timestamp: Date.now()
                                    },
                                    replies: []
                                };

                                // Add thread data to annotation
                                firstNode.setAttribute('data-thread', JSON.stringify(threadData));
                            }
                        });

                        // Update sidebar visibility based on comments
                        updateSidebarVisibility();


                        // Add click handler for annotations in content
                        setTimeout(() => {
                            // Use the iframe content document instead of getBody
                            const container = typedEditor.getContainer();
                            const iframe = container?.querySelector('iframe') as HTMLIFrameElement;

                            if (iframe && iframe.contentDocument) {
                                const editorBody = iframe.contentDocument.body;
                                if (editorBody) {
                                    editorBody.addEventListener('click', (event: Event) => {
                                        const target = event.target as HTMLElement;

                                        if (target && target.classList.contains('comment-annotation')) {

                                            // Try different ways to get the UID
                                            let uid = target.getAttribute('data-uid') ||
                                                     target.getAttribute('data-mce-annotation-uid') ||
                                                     target.getAttribute('data-annotation-id');


                                            // If no UID found in attributes, try to find it through TinyMCE's annotation system
                                            if (!uid && typedEditor.annotator) {
                                                const annotations = typedEditor.annotator.getAll('comment');

                                                // Find the annotation that contains this element
                                                Object.keys(annotations).forEach(annotationUid => {
                                                    const nodes = annotations[annotationUid];
                                                    if (nodes.includes(target)) {
                                                        uid = annotationUid;
                                                    }
                                                });
                                            }

                                            if (uid) {
                                                highlightCommentCard(uid);
                                            }
                                        }
                                    });
                                }
                            }

                            // Also add NodeChange handler as backup
                            typedEditor.on('click', (event?: { target?: HTMLElement }) => {
                                const target = event?.target;
                                if (target && target.classList && target.classList.contains('comment-annotation')) {
                                    const uid = target.getAttribute('data-uid');
                                    if (uid) {
                                        highlightCommentCard(uid);
                                    }
                                }
                            });
                        }, 1500); // Wait longer for iframe to be ready
                    }
                }, 1000);
            }

            // Remove all borders from the editor
            const container = typedEditor.getContainer();
            if (container) {
                const iframe = container.querySelector('iframe') as HTMLIFrameElement;
                const editorContainer = container.querySelector('.tox-editor-container') as HTMLElement;
                const editArea = container.querySelector('.tox-edit-area') as HTMLElement;

                // Remove borders from all editor elements
                if (iframe) {
                    iframe.style.border = 'none';
                    iframe.style.outline = 'none';
                }
                if (editorContainer) {
                    editorContainer.style.border = 'none';
                }
                if (editArea) {
                    editArea.style.border = 'none';
                }

                // Add custom CSS to remove all borders and style comments
                const style = document.createElement('style');
                style.textContent = TINYMCE_EDITOR_STYLES + `
                    .comment-annotation {
                        background-color: #fef3c7;
                        border-bottom: 2px solid #f59e0b;
                        cursor: help;
                    }
                    .comment-annotation:hover {
                        background-color: #fde68a;
                        border-bottom-color: #d97706;
                    }

                    /* Disable sidebar animations */
                    .tox-sidebar {
                        transition: none !important;
                        animation: none !important;
                    }
                    .tox-sidebar-wrap {
                        transition: none !important;
                        animation: none !important;
                    }
                    .tox-editor-container {
                        transition: none !important;
                        animation: none !important;
                    }
                `;
                document.head.appendChild(style);
            }
        });
    };

    const editorConfig = getTinyMCEConfig(placeholder, false); // Disable sidebar for now

    return (
        <div className={cn('flex flex-col h-full', className)}>
            <div className="flex-1 h-full">
                <Editor
                    tinymceScriptSrc="/tinymce/tinymce.min.js"
                    licenseKey="gpl"
                    onInit={(evt, editor) => editorRef.current = editor as unknown as TinyMCEEditor}
                    initialValue={content}
                    disabled={!editable}
                    init={{
                        ...editorConfig,
                        setup: setupTinyMCEEditor
                    }}
                />
            </div>

            {/* Comment Dialog */}
            <Dialog open={showCommentDialog} onOpenChange={setShowCommentDialog}>
                <DialogContent className="sm:max-w-[500px]">
                    <DialogHeader>
                        <DialogTitle>Add Comment</DialogTitle>
                    </DialogHeader>

                    <div className="space-y-4">
                        <Textarea
                            ref={commentTextRef}
                            onChange={() => checkCommentValidity()}
                            onBlur={() => checkCommentValidity()}
                            placeholder="Enter your comment..."
                            className="min-h-[80px] resize-none"
                            autoFocus
                        />
                    </div>

                    <DialogFooter className="flex justify-end space-x-2">
                        <Button
                            variant="outline"
                            onClick={handleCommentCancel}
                        >
                            Cancel
                        </Button>
                        <Button
                            onClick={handleCommentSubmit}
                            disabled={!isCommentValid}
                            className="bg-blue-600 hover:bg-blue-700"
                        >
                            Add Comment
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Reply Dialog */}
            <Dialog open={showReplyDialog} onOpenChange={setShowReplyDialog}>
                <DialogContent className="sm:max-w-[500px]">
                    <DialogHeader>
                        <DialogTitle>Reply to {replyToAuthor}</DialogTitle>
                    </DialogHeader>

                    <div className="space-y-4">
                        <Textarea
                            ref={replyTextRef}
                            onChange={() => checkReplyValidity()}
                            onBlur={() => checkReplyValidity()}
                            placeholder="Enter your reply..."
                            className="min-h-[80px] resize-none"
                            autoFocus
                        />
                    </div>

                    <DialogFooter className="flex justify-end space-x-2">
                        <Button
                            variant="outline"
                            onClick={handleReplyCancel}
                        >
                            Cancel
                        </Button>
                        <Button
                            onClick={handleReplySubmit}
                            disabled={!isReplyValid}
                            className="bg-blue-600 hover:bg-blue-700"
                        >
                            Add Reply
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Delete Confirmation Dialog */}
            <Dialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>
                <DialogContent className="sm:max-w-[500px]">
                    <DialogHeader>
                        <DialogTitle>Confirm Deletion</DialogTitle>
                    </DialogHeader>

                    <div className="space-y-4">
                        <p>Are you sure you want to delete this comment? This action cannot be undone.</p>
                    </div>

                    <DialogFooter className="flex justify-end space-x-2">
                        <Button
                            variant="outline"
                            onClick={handleCancelDelete}
                        >
                            Cancel
                        </Button>
                        <Button
                            onClick={handleConfirmedDelete}
                            className="bg-red-600 hover:bg-red-700"
                        >
                            Delete
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>
        </div>
    );
});

export default RichTextEditor;

