import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import RichTextEditor, { RichTextEditorRef } from '@/components/ui/rich-text-editor';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { VersionCard } from '@/components/ui/version-card';
import AppLayout from '@/layouts/app-layout';
import { BlueprintVersion, BreadcrumbItem, BusinessProcessActivity, BusinessProcessBlueprint, PageProps } from '@/types';
import { Head, Link, router, useForm } from '@inertiajs/react';
import { AlertTriangle, CheckCircle, ChevronLeft, ChevronRight, FileText, GitCommit, Save, Settings, User, Users } from 'lucide-react';
import React, { useEffect, useRef, useState } from 'react';

interface BlueprintShowProps extends PageProps {
    process: {
        id: number;
        name: string;
        category?: { id: number; name: string };
    };
    core: {
        id: number;
        name: string;
        process_id: number;
    };
    activity: BusinessProcessActivity;
    blueprint?: BusinessProcessBlueprint;
    currentVersion?: BlueprintVersion;
    versions?: BlueprintVersion[];
    errors?: Record<string, string>;
}




const getStatusBadge = (status: number) => {
    const statusConfig = {
        0: { variant: 'secondary' as const, label: 'Draft' }, // Draft
        1: { variant: 'default' as const, label: 'Current', className: 'bg-green-600 hover:bg-green-700' }, // Current
        2: {
            className: 'bg-blue-100 text-blue-800 hover:bg-blue-100',
            label: 'Future',
        }, // Future
    };

    const config = statusConfig[status as keyof typeof statusConfig];

    if (!config) {
        return <Badge variant="secondary">Unknown</Badge>;
    }

    if ('className' in config) {
        return <Badge className={config.className}>{config.label}</Badge>;
    }

    return <Badge variant={config.variant}>{config.label}</Badge>;
};

const formatDate = (dateString?: string) => {
    if (!dateString) return 'Not set';
    return dateString;
};

export default function BlueprintShow({ process, core, activity, blueprint, currentVersion, versions = [] }: BlueprintShowProps) {
    // Debug logging
    console.log('Blueprint show page data:', { process, core, activity, blueprint, currentVersion, versions });

    const [showCommitDialog, setShowCommitDialog] = useState(false);
    const [showUnsavedChangesDialog, setShowUnsavedChangesDialog] = useState(false);
    const [showStatusDialog, setShowStatusDialog] = useState(false);
    const [selectedVersionForStatus, setSelectedVersionForStatus] = useState<BlueprintVersion | null>(null);
    const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
    const [pendingNavigation, setPendingNavigation] = useState<string | null>(null);

    // Reference to the rich text editor
    const editorRef = useRef<RichTextEditorRef>(null);

    // Reference to commit dialog textarea to avoid re-renders
    const commitSummaryRef = useRef<HTMLTextAreaElement>(null);
    const [isCommitValid, setIsCommitValid] = useState(false);

    // Sidebar toggle states with localStorage persistence
    const [showVersionSidebar, setShowVersionSidebar] = useState(() => {
        if (typeof window !== 'undefined') {
            const saved = localStorage.getItem('blueprint-version-sidebar-open');
            return saved !== null ? JSON.parse(saved) : true;
        }
        return true;
    });

    const [showMetadataSidebar, setShowMetadataSidebar] = useState(() => {
        if (typeof window !== 'undefined') {
            const saved = localStorage.getItem('blueprint-metadata-sidebar-open');
            return saved !== null ? JSON.parse(saved) : true;
        }
        return true;
    });

    // Save sidebar states to localStorage
    useEffect(() => {
        if (typeof window !== 'undefined') {
            localStorage.setItem('blueprint-version-sidebar-open', JSON.stringify(showVersionSidebar));
        }
    }, [showVersionSidebar]);

    useEffect(() => {
        if (typeof window !== 'undefined') {
            localStorage.setItem('blueprint-metadata-sidebar-open', JSON.stringify(showMetadataSidebar));
        }
    }, [showMetadataSidebar]);

    // Toggle functions
    const toggleVersionSidebar = () => setShowVersionSidebar(!showVersionSidebar);
    const toggleMetadataSidebar = () => setShowMetadataSidebar(!showMetadataSidebar);

    // Processing state for save operations
    const [mainFormProcessing, setMainFormProcessing] = useState(false);

    // Status change form
    const {
        data: statusData,
        setData: setStatusData,
        processing: statusProcessing,
        reset: resetStatusData,
    } = useForm({
        status: 'draft',
    });

    // Versions are already sorted on the backend
    const allVersions = React.useMemo(() => {
        return Array.isArray(versions) ? versions : [];
    }, [versions]);

    // Track changes to detect unsaved changes (disabled for now since we're not editing title)
    // The RichTextEditor handles its own change detection for content

    // Handle navigation with unsaved changes check
    const handleNavigation = (url: string): boolean => {
        if (hasUnsavedChanges) {
            setPendingNavigation(url);
            setShowUnsavedChangesDialog(true);
            return false; // Prevent navigation
        }
        return true; // Allow navigation
    };

    // Custom Link component that checks for unsaved changes
    const SafeLink = ({ href, className, children, ...props }: { href: string; className?: string; children: React.ReactNode }) => {
        const handleClick = (e: React.MouseEvent) => {
            if (!handleNavigation(href)) {
                e.preventDefault();
            }
        };

        return (
            <Link href={href} className={className} onClick={handleClick} {...props}>
                {children}
            </Link>
        );
    };

    const handleSave = () => {
        // Get content directly from TinyMCE editor
        const currentContent = editorRef.current?.getContent() || '';

        setMainFormProcessing(true);

        if (!currentVersion) {
            // No version exists - create first version via commit
            const saveData = {
                content: currentContent,
                change_summary: 'Initial blueprint creation',
            };

            router.put(route('business-processes.blueprints.commit', [process.id, core.id, activity.id]), saveData, {
                onSuccess: () => {
                    setHasUnsavedChanges(false);
                },
                onFinish: () => {
                    setMainFormProcessing(false);
                },
            });
        } else {
            // Version exists - update it
            const saveData = {
                content: currentContent,
                change_summary: 'Content updated',
            };

            router.put(route('business-processes.blueprints.versions.save', [process.id, core.id, activity.id, currentVersion.id]), saveData, {
                onSuccess: () => {
                    setHasUnsavedChanges(false);
                },
                onFinish: () => {
                    setMainFormProcessing(false);
                },
            });
        }
    };

    const handleCommit = () => {
        setShowCommitDialog(true);
    };

    const handleCommitCancel = () => {
        setShowCommitDialog(false);
        // Clear the textarea
        if (commitSummaryRef.current) {
            commitSummaryRef.current.value = '';
        }
        setIsCommitValid(false);
    };

    const handleCommitConfirm = () => {
        // Get content directly from TinyMCE editor
        const currentContent = editorRef.current?.getContent() || '';
        const changeSummary = commitSummaryRef.current?.value?.trim() || '';

        const commitData = {
            content: currentContent,
            change_summary: changeSummary,
        };

        setMainFormProcessing(true);
        router.put(route('business-processes.blueprints.commit', [process.id, core.id, activity.id]), commitData, {
            onSuccess: () => {
                setShowCommitDialog(false);
                setHasUnsavedChanges(false);
                setPendingNavigation(null);
                // Clear the commit textarea
                if (commitSummaryRef.current) {
                    commitSummaryRef.current.value = '';
                }
                setIsCommitValid(false);
            },
            onFinish: () => {
                setMainFormProcessing(false);
            },
        });
    };

    const handleDiscardChanges = () => {
        setShowUnsavedChangesDialog(false);
        setHasUnsavedChanges(false);
        setPendingNavigation(null);

        // Navigate to pending URL if there was one
        if (pendingNavigation) {
            router.visit(pendingNavigation);
        }
    };

    const handleSaveFromDialog = () => {
        setShowUnsavedChangesDialog(false);
        handleSave();

        // Navigate to pending URL after saving
        if (pendingNavigation) {
            router.visit(pendingNavigation);
        }
    };

    // Status change handlers
    const handleStatusChange = (version: BlueprintVersion) => {
        setSelectedVersionForStatus(version);
        setStatusData('status', version.status.toString());
        setShowStatusDialog(true);
    };

    const handleStatusUpdate = () => {
        if (!selectedVersionForStatus) return;

        router.put(
            route('business-processes.blueprints.versions.update-status', [process.id, core.id, activity.id, selectedVersionForStatus.id]),
            {
                status: statusData.status,
            },
            {
                onSuccess: () => {
                    setShowStatusDialog(false);
                    setSelectedVersionForStatus(null);
                    resetStatusData();
                },
            },
        );
    };

    const getStatusOptions = () => [
        { value: 0, label: 'Draft' },
        { value: 1, label: 'Current' },
        { value: 2, label: 'Future' },
    ];

    // Define breadcrumbs
    const breadcrumbs: BreadcrumbItem[] = [
        {
            title: 'Dashboard',
            href: route('dashboard'),
        },
        {
            title: 'Business Processes',
            href: route('business-processes'),
        },
        ...(process.category
            ? [
                  {
                      title: process.category.name,
                      href: '#', // Could link to category view if needed
                  },
              ]
            : []),
        {
            title: process.name || 'Process',
            href: route('business-processes.cores.index', [process.id]),
        },
        {
            title: core.name || 'Core Process',
            href: route('business-processes.cores.show', [process.id, core.id]),
        },
        {
            title: `${activity.name} - Blueprint`,
            href: route('business-processes.blueprints.show', [process.id, core.id, activity.id]),
        },
    ];

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title={`Blueprint: ${activity.name}`} />

            <div className="flex h-[calc(100vh-4rem)] flex-col lg:flex-row">
                {/* Left Sidebar - Version History */}
                <div
                    className={`bg-gray-50 transition-all duration-300 lg:flex lg:flex-col ${showVersionSidebar ? 'w-full lg:w-80' : 'w-10 lg:w-10'}`}
                >
                    {showVersionSidebar ? (
                        <>
                            <div className="flex-shrink-0 p-4">
                                <div className="mb-4 flex items-center justify-between">
                                    <h2 className="text-lg font-semibold text-gray-900">Version History</h2>
                                    <Button variant="ghost" size="sm" onClick={toggleVersionSidebar} className="h-8 w-8 p-0">
                                        <ChevronLeft className="h-4 w-4" />
                                    </Button>
                                </div>
                            </div>

                            {/* Scrollable version list */}
                            <div className="flex-1 overflow-y-auto px-4 pb-4">
                                {allVersions.length > 0 ? (
                                    /* All Versions (newest to oldest) */
                                    <div className="space-y-2">
                                        {allVersions.map((version) => {
                                            const isCurrentVersion = version.status === 1; // Current status
                                            const isViewingThisVersion = version.version_number === currentVersion?.version_number;

                                            return (
                                                <VersionCard
                                                    key={version.id}
                                                    version={version}
                                                    isCurrentVersion={isCurrentVersion}
                                                    isViewingThisVersion={isViewingThisVersion}
                                                    getStatusBadge={getStatusBadge}
                                                    href={route('business-processes.blueprints.versions.show', [process.id, core.id, activity.id, version.id])}
                                                    SafeLink={SafeLink}
                                                    onStatusChange={handleStatusChange}
                                                    showStatusButton={true}
                                                />
                                            );
                                        })}
                                    </div>
                                ) : (
                                    /* Empty State */
                                    <div className="flex h-full flex-col items-center justify-center py-12">
                                        <div className="rounded-full bg-gray-100 p-3">
                                            <FileText className="h-6 w-6 text-gray-400" />
                                        </div>
                                        <h3 className="mt-4 text-sm font-medium text-gray-900">No versions yet</h3>
                                        <p className="mt-2 text-center text-sm text-gray-500">
                                            Get started by creating your first blueprint version.
                                        </p>
                                    </div>
                                )}
                            </div>
                        </>
                    ) : (
                        <div className="flex h-full items-center justify-center border-r border-gray-200">
                            <Button
                                variant="ghost"
                                size="sm"
                                onClick={toggleVersionSidebar}
                                className="h-8 w-8 rotate-180 p-0"
                                title="Show Version History"
                            >
                                <ChevronLeft className="h-4 w-4" />
                            </Button>
                        </div>
                    )}
                </div>

                {/* Main Content Area */}
                <div className="flex min-h-0 flex-1 flex-col xl:flex-row">
                    {/* Content Panel */}
                    <div className="flex min-h-0 flex-1 flex-col">
                        {/* Header - Fixed */}
                        <div className="flex-shrink-0 border-b border-gray-200 bg-white px-6 py-2">
                            <div className="flex items-center justify-between">
                                <h1 className="text-xl font-semibold text-gray-900">{activity.name} - Blueprint</h1>

                                <div className="flex items-center space-x-2">
                                    {hasUnsavedChanges && (
                                        <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">
                                            Unsaved Changes
                                        </Badge>
                                    )}
                                    <Button
                                        variant="ghost"
                                        onClick={() => router.visit(route('business-processes.cores.show', [process.id, core.id]) + '?tab=raci')}
                                        className="text-gray-600 hover:text-gray-900"
                                    >
                                        <Users className="mr-2 h-4 w-4" />
                                        Back to RACI
                                    </Button>
                                    <Button variant="outline" onClick={handleSave} disabled={mainFormProcessing}>
                                        <Save className="mr-2 h-4 w-4" />
                                        {!currentVersion ? 'Create Blueprint' : 'Save'}
                                    </Button>
                                    {currentVersion && (
                                        <Button onClick={handleCommit} disabled={mainFormProcessing}>
                                            <GitCommit className="mr-2 h-4 w-4" />
                                            Commit
                                        </Button>
                                    )}
                                </div>
                            </div>
                        </div>

                        {/* Content Body - Scrollable */}
                        <div className="min-h-0 flex-1 overflow-auto">
                            <div className="h-full">
                                {/* Blueprint Content */}
                                <div className="flex h-full flex-col bg-white">
                                    {/* Content Area - Scrollable */}
                                    <div className="h-full flex-1">
                                        <RichTextEditor
                                            ref={editorRef}
                                            content={currentVersion?.content || ''}
                                            currentUser={{
                                                id: String(currentVersion?.creator?.id || '1'),
                                                name: currentVersion?.creator?.name || 'Current User',
                                            }}
                                            onAutoSave={handleSave}
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Metadata Panel */}
                    <div
                        className={`bg-gray-50 transition-all duration-300 xl:flex xl:flex-col ${
                            showMetadataSidebar ? 'w-full xl:w-96' : 'w-10 xl:w-10'
                        }`}
                    >
                        {showMetadataSidebar ? (
                            <>
                                {/* Metadata Header - Fixed */}
                                <div className="flex-shrink-0 p-4 sm:p-6">
                                    <div className="flex items-center justify-between">
                                        <h3 className="text-lg font-semibold text-gray-900">Metadata</h3>
                                        <Button variant="ghost" size="sm" onClick={toggleMetadataSidebar} className="h-8 w-8 p-0">
                                            <ChevronRight className="h-4 w-4" />
                                        </Button>
                                    </div>
                                </div>

                                {/* Metadata Content - Scrollable */}
                                <div className="flex-1 overflow-y-auto px-4 pb-4 sm:px-6 sm:pb-6">
                                    <div className="space-y-4">
                                        {/* Version Information */}
                                        <div className="rounded-lg border border-gray-200 bg-white p-4">
                                            <div className="mb-3 flex items-center">
                                                <CheckCircle className="mr-2 h-4 w-4 text-gray-500" />
                                                <span className="font-medium">Version Info</span>
                                            </div>
                                            <div className="space-y-2 text-sm text-gray-600">
                                                <div className="flex justify-between">
                                                    <span>Version:</span>
                                                    <span>{currentVersion?.version_number || '1.0'}</span>
                                                </div>
                                                <div className="flex justify-between">
                                                    <span>Created:</span>
                                                    <span>{formatDate(currentVersion?.created_at)}</span>
                                                </div>
                                                <div className="flex justify-between">
                                                    <span>Created by:</span>
                                                    <span>{currentVersion?.creator?.name || 'Unknown'}</span>
                                                </div>
                                            </div>
                                        </div>

                                        {/* Change Summary */}
                                        {currentVersion?.change_summary && (
                                            <div className="rounded-lg border border-gray-200 bg-white p-4">
                                                <div className="mb-3 flex items-center">
                                                    <CheckCircle className="mr-2 h-4 w-4 text-blue-500" />
                                                    <span className="font-medium">Change Summary</span>
                                                </div>
                                                <div className="text-sm text-gray-700">{currentVersion.change_summary}</div>
                                            </div>
                                        )}

                                        {/* Activity Information */}
                                        <div className="rounded-lg border border-gray-200 bg-white p-4">
                                            <div className="mb-3 flex items-center">
                                                <FileText className="mr-2 h-4 w-4 text-purple-500" />
                                                <span className="font-medium">Activity Details</span>
                                            </div>
                                            <div className="space-y-2 text-sm text-gray-600">
                                                <div className="flex justify-between">
                                                    <span>Process:</span>
                                                    <span>{process.name}</span>
                                                </div>
                                                <div className="flex justify-between">
                                                    <span>Core:</span>
                                                    <span>{core.name}</span>
                                                </div>
                                                <div className="flex justify-between">
                                                    <span>Activity:</span>
                                                    <span>{activity.name}</span>
                                                </div>
                                            </div>
                                        </div>

                                        {/* RACI Information */}
                                        <div className="rounded-lg border border-gray-200 bg-white p-4">
                                            <div className="mb-3 flex items-center">
                                                <User className="mr-2 h-4 w-4 text-green-500" />
                                                <span className="font-medium">RACI Matrix</span>
                                            </div>
                                            <div className="space-y-2 text-sm text-gray-600">
                                                <div className="flex justify-between">
                                                    <span>Responsible:</span>
                                                    <span>{activity.responsible || 'Not assigned'}</span>
                                                </div>
                                                <div className="flex justify-between">
                                                    <span>Accountable:</span>
                                                    <span>{activity.accountable || 'Not assigned'}</span>
                                                </div>
                                                <div className="flex justify-between">
                                                    <span>Consulted:</span>
                                                    <span>{activity.consulted || 'Not assigned'}</span>
                                                </div>
                                                <div className="flex justify-between">
                                                    <span>Informed:</span>
                                                    <span>{activity.informed || 'Not assigned'}</span>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </>
                        ) : (
                            <div className="flex h-full items-center justify-center border-l border-gray-200">
                                <Button
                                    variant="ghost"
                                    size="sm"
                                    onClick={toggleMetadataSidebar}
                                    className="h-8 w-8 rotate-180 p-0"
                                    title="Show Metadata"
                                >
                                    <ChevronRight className="h-4 w-4" />
                                </Button>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Commit Dialog */}
            <Dialog open={showCommitDialog} onOpenChange={setShowCommitDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <GitCommit className="mr-2 h-5 w-5" />
                            Commit New Version
                        </DialogTitle>
                        <DialogDescription>
                            You're about to create a new version of this blueprint. Please provide a summary of what changed.
                        </DialogDescription>
                    </DialogHeader>

                    <div className="space-y-4">
                        <div>
                            <Label htmlFor="commit_summary">Change Summary *</Label>
                            <Textarea
                                id="commit_summary"
                                ref={commitSummaryRef}
                                onChange={() => {
                                    const text = commitSummaryRef.current?.value?.trim() || '';
                                    setIsCommitValid(text.length > 0);
                                }}
                                onBlur={() => {
                                    const text = commitSummaryRef.current?.value?.trim() || '';
                                    setIsCommitValid(text.length > 0);
                                }}
                                placeholder="Describe what changes were made..."
                                rows={4}
                                className="mt-1"
                            />
                        </div>

                        <div className="rounded-lg bg-blue-50 p-3">
                            <p className="text-sm text-blue-800">
                                A new version will be created:{' '}
                                <strong>v{Math.max(...allVersions.map((v) => parseFloat(v.version_number)), 0) + 1}</strong>
                            </p>
                        </div>
                    </div>

                    <DialogFooter>
                        <Button type="button" variant="outline" onClick={handleCommitCancel}>
                            Cancel
                        </Button>
                        <Button type="button" onClick={handleCommitConfirm} disabled={!isCommitValid || mainFormProcessing}>
                            {mainFormProcessing ? 'Creating Version...' : 'Create Version'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Unsaved Changes Dialog */}
            <Dialog open={showUnsavedChangesDialog} onOpenChange={setShowUnsavedChangesDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <AlertTriangle className="mr-2 h-5 w-5" />
                            Unsaved Changes
                        </DialogTitle>
                        <DialogDescription>You have unsaved changes. What would you like to do?</DialogDescription>
                    </DialogHeader>

                    <DialogFooter>
                        <Button
                            type="button"
                            variant="outline"
                            onClick={() => {
                                setShowUnsavedChangesDialog(false);
                                setPendingNavigation(null);
                            }}
                        >
                            Cancel
                        </Button>
                        <Button type="button" variant="destructive" onClick={handleDiscardChanges}>
                            Discard Changes
                        </Button>
                        <Button type="button" onClick={handleSaveFromDialog}>
                            Save & Continue
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Status Change Dialog */}
            <Dialog open={showStatusDialog} onOpenChange={setShowStatusDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <Settings className="mr-2 h-5 w-5" />
                            Change Version Status
                        </DialogTitle>
                        <DialogDescription>
                            {selectedVersionForStatus && <>Change the status of Version {selectedVersionForStatus.version_number}</>}
                        </DialogDescription>
                    </DialogHeader>

                    <div className="space-y-4">
                        <div>
                            <Label htmlFor="status">Status</Label>
                            <Select value={statusData.status} onValueChange={(value) => setStatusData('status', value)}>
                                <SelectTrigger className="mt-1">
                                    <SelectValue placeholder="Select status" />
                                </SelectTrigger>
                                <SelectContent>
                                    {getStatusOptions().map((option) => (
                                        <SelectItem key={option.value} value={option.value.toString()}>
                                            {option.label}
                                        </SelectItem>
                                    ))}
                                </SelectContent>
                            </Select>
                        </div>

                        {statusData.status === 'current' && (
                            <div className="rounded-lg bg-yellow-50 p-3">
                                <p className="text-sm text-yellow-800">
                                    <strong>Note:</strong> Setting this version as "Current" will automatically change any other current version to
                                    "Draft".
                                </p>
                            </div>
                        )}

                        {statusData.status === 'archived' && (
                            <div className="rounded-lg bg-purple-50 p-3">
                                <p className="text-sm text-purple-800">
                                    <strong>Note:</strong> Setting this version as "Archived" will prevent further editing.
                                </p>
                            </div>
                        )}
                    </div>

                    <DialogFooter>
                        <Button type="button" variant="outline" onClick={() => setShowStatusDialog(false)}>
                            Cancel
                        </Button>
                        <Button type="button" onClick={handleStatusUpdate} disabled={statusProcessing}>
                            {statusProcessing ? 'Updating...' : 'Update Status'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>
        </AppLayout>
    );
}

