import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { PageProps } from '@/types';
import { Link, usePage } from '@inertiajs/react';
import { ArrowRight, FileText, Layers, Puzzle } from 'lucide-react';

interface BusinessProcessQuickAccessProps {
    totalProcesses: number;
    totalCategories: number;
    totalDiagrams: number;
}

const StatCard = ({
    title,
    value,
    description,
    icon: Icon,
    iconColorClass = 'text-blue-500',
}: {
    title: string;
    value: number | string;
    description: string;
    icon: React.ElementType;
    iconColorClass?: string;
}) => (
    <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">{title}</CardTitle>
            <Icon className={`h-5 w-5 ${iconColorClass}`} />
        </CardHeader>
        <CardContent>
            <div className="text-2xl font-bold">{value}</div>
            <p className="text-muted-foreground text-xs">{description}</p>
        </CardContent>
    </Card>
);

const ProcessQuickAccessCard = () => (
    <Card className="border-blue-200 bg-gradient-to-br from-blue-50 to-indigo-100 transition-shadow duration-200 hover:shadow-lg">
        <CardContent className="px-8 py-6">
            <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                    <div className="rounded-lg bg-blue-500 p-4 text-white">
                        <Layers className="h-8 w-8" />
                    </div>
                    <div>
                        <h3 className="text-xl font-bold text-blue-900">Business Processes</h3>
                        <p className="mt-1 text-blue-700">Manage and visualize your organization's processes</p>
                    </div>
                </div>
                <Button className="bg-blue-600 text-white hover:bg-blue-700" asChild>
                    <Link href="/business-processes" className="flex items-center gap-2">
                        View Processes
                        <ArrowRight className="h-4 w-4" />
                    </Link>
                </Button>
            </div>
        </CardContent>
    </Card>
);

export default function BusinessProcessQuickAccess({ totalProcesses, totalCategories, totalDiagrams }: BusinessProcessQuickAccessProps) {
    const { props } = usePage<PageProps>();
    const features = props.features || {};

    console.log('BusinessProcessQuickAccess - Features:', features);
    console.log('BusinessProcessQuickAccess - Business Process canShow:', features['business-processes']?.canShow);

    // Only show if business-processes feature can be shown
    const canShow = features['business-processes']?.canShow;

    if (!canShow) {
        console.log('BusinessProcessQuickAccess - Cannot show, returning null');
        return null;
    }

    return (
        <div className="mb-8">
            <h2 className="mb-4 text-xl font-semibold text-gray-900 dark:text-white">Business Process Management</h2>

            {/* Quick Access Card */}
            <div className="mb-6">
                <ProcessQuickAccessCard />
            </div>

            {/* Process Stats */}
            <div className="grid gap-4 md:grid-cols-3">
                <StatCard
                    title="Process Categories"
                    value={totalCategories}
                    description="Strategic, Core, Enabling"
                    icon={Puzzle}
                    iconColorClass="text-orange-500"
                />
                <StatCard
                    title="Total Processes"
                    value={totalProcesses}
                    description="Across all categories"
                    icon={Layers}
                    iconColorClass="text-sky-500"
                />
                <StatCard
                    title="Total Diagrams"
                    value={totalDiagrams}
                    description="In all processes"
                    icon={FileText}
                    iconColorClass="text-emerald-500"
                />
            </div>
        </div>
    );
}

