                <Button className="bg-green-600 text-white hover:bg-green-700" asChild>
                    <Link href="/assessments" className="flex items-center gap-2">
                        Go to Assessments
                        <ArrowRight className="h-4 w-4" />
                    </Link>
                </Button>
            </div>
        </CardContent>
    </Card>
);

export default function AssessmentQuickAccess() {
    const { props } = usePage<PageProps>();
    const features = props.features || {};

    console.log('AssessmentQuickAccess - Features:', features);
    console.log('AssessmentQuickAccess - Assessment canShow:', features['assessments']?.canShow);

    // Only show if assessments feature can be shown
    const canShow = features['assessments']?.canShow;

    if (!canShow) {
        console.log('AssessmentQuickAccess - Cannot show, returning null');
        return null;
    }

    return (
        <div className="mb-8">
