import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useBusinessData } from '@/hooks/useBusinessData';
import AppLayout from '@/layouts/app-layout';
import { BreadcrumbItem } from '@/types';
import { AssessmentTemplate, UserAssessment } from '@/types/assessment';
import {
    generateExecutiveSummary,
    generateFocusAreas,
    calculateOwnerStrategicPositioning,
    type AssessmentQuestion,
    type GroupedQuestion,
} from '@/lib/assessment';
// Import the new S2D prompt generator
import { generateS2DPromptText, downloadPromptAsFile } from '@/lib/assessment/prompts';
// Import shared utilities
import {
    getAssessmentConfig,
    groupQuestionsByArea,
    calculateSubAreaScores,
    calculateOverallScores as calculateOverallScoresShared,
    getSelectedOptionText,
} from '@/lib/assessment/shared-utils';
import type { SubAreaScore } from '@/lib/assessment/types';
// Import reusable UI components
import { OverallScoresCard, KeyFindingsCard, QuestionGroupDisplay } from '@/lib/assessment/ui-components';
import { Head, Link } from '@inertiajs/react';
import { BarChart3, ChevronLeft, FileText, Trophy } from 'lucide-react';
import { useMemo } from 'react';

interface S2DResultsPageProps {
    assessment: UserAssessment;
    template: AssessmentTemplate;
    questions: AssessmentQuestion[];
    s2dScores: {
        s2d_processMaturityScore: number;
        s2d_ownerIndependenceScore: number;
        s2d_customerExperienceScore: number;
        s2d_growthConnectionScore: number;
        s2d_measurementRetentionScore: number;
        s2d_processMaturityPercentage: number;
        s2d_ownerIndependencePercentage: number;
        s2d_detailedAnswers: Record<string, string | number>;
        s2d_ownerStrategicPositioning: {
            areasForDelegation: string[];
            areasForActiveManagement: string[];
        };
    };
    profileAssessment: UserAssessment | null;
    previousAssessment?: AssessmentTemplate;
    nextAssessment?: AssessmentTemplate;
    canAccessNext: boolean;
}

export default function S2DResultsPage({
    assessment,
    template,
    questions,
    s2dScores,
    profileAssessment,
    previousAssessment,
    nextAssessment,
    canAccessNext,
}: S2DResultsPageProps) {
    // Debug logging
    console.log('S2D Results Debug:', {
        questions: questions.slice(0, 2), // First 2 questions for debugging
        assessment_answers: assessment.answers,
        s2dScores,
    });

    const breadcrumbs: BreadcrumbItem[] = useMemo(
        () => [
            {
                title: 'Assessments',
                href: '/assessments',
            },
            {
                title: template.name,
                href: `/assessments/${template.slug}`,
            },
            {
                title: 'Results',
                href: `/assessments/results/${template.slug}`,
            },
        ],
        [template.name, template.slug],
    );

    // Get business data using the reusable hook
    const { businessData } = useBusinessData(profileAssessment, assessment);

    // Get configuration and use shared utilities
    const config = getAssessmentConfig('s2d');
    const groupedQuestions = useMemo(() => groupQuestionsByArea(questions, config), [questions, config]);
    const subAreaScores = useMemo(() => calculateSubAreaScores(groupedQuestions, config), [groupedQuestions, config]);
    const calculateOverallScores = useMemo(() => calculateOverallScoresShared(questions, config), [questions, config]);

    // Get product revenue specifically (not general business revenue)
    const getProductRevenue = (): number => {
        // Helper function to extract value from answer object if needed
        const extractAnswerValue = (answer: unknown): string => {
            if (typeof answer === 'object' && answer !== null && 'value' in answer) {
                return String((answer as { value: unknown }).value || '');
            }
            return String(answer || '');
        };

        const productRevenue =
            extractAnswerValue(profileAssessment?.answers?.['s2d_productRevenue']) ||
            extractAnswerValue(assessment?.answers?.['s2d_productRevenue']) ||
            '0';
        return Number(productRevenue) || 0;
    };

    // Format revenue (already available in businessData.formattedRevenue, but keeping function for other uses)
    const formatRevenue = (revenue: number) => {
        return `$${revenue.toLocaleString()}`;
    };

    // Calculate owner strategic positioning
    const ownerStrategicPositioning = useMemo(() => {
        return calculateOwnerStrategicPositioning(groupedQuestions);
    }, [groupedQuestions]);

    // Generate executive summary
    const executiveSummary = useMemo(() => {
        return generateExecutiveSummary(
            's2d',
            businessData.businessName || 'A name',
            calculateOverallScores.processMaturity.actual,
            calculateOverallScores.processMaturity.max,
            calculateOverallScores.ownerIndependence.actual,
            calculateOverallScores.ownerIndependence.max,
            [subAreaScores.customerExperience.score, subAreaScores.growthConnection.score, subAreaScores.measurementRetention.score],
        );
    }, [businessData.businessName, calculateOverallScores, subAreaScores]);

    // Generate focus areas
    const focusAreas = useMemo(() => {
        // Pass sub-area scores for conditional recommendations
        const subAreaScoreValues = [
            subAreaScores.customerExperience.score,
            subAreaScores.growthConnection.score,
            subAreaScores.measurementRetention.score,
        ];

        return generateFocusAreas(
            's2d',
            calculateOverallScores.processMaturity.actual,
            calculateOverallScores.ownerIndependence.actual,
            subAreaScoreValues,
        );
    }, [calculateOverallScores, subAreaScores]);

    // Handle Generate Prompt button click
    const handleGeneratePrompt = () => {
        try {
            const promptText = generateS2DPromptText(questions, subAreaScores, calculateOverallScores, ownerStrategicPositioning);
            downloadPromptAsFile(promptText, 's2d', businessData.businessName || 'Assessment');
        } catch (error) {
            console.error('Error generating prompt:', error);
            alert('Error generating prompt file. Please try again.');
        }
    };

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title={`Results for: ${template.name}`} />

            <div className="min-h-screen bg-gray-50">
                {/* Header Section */}
                <div className="border-b bg-white">
                    <div className="mx-auto max-w-7xl px-4 py-6 sm:px-6 lg:px-8">
                        <div className="flex items-center justify-between">
                            <div>
                                <h1 className="text-2xl font-bold text-gray-900">Results for: {template.name}</h1>
                            </div>
                            <div>
                                <Button variant="outline" onClick={handleGeneratePrompt} className="flex items-center gap-2">
                                    <FileText className="h-4 w-4" />
                                    Generate Prompt (.txt)
                                </Button>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Main Content */}
                <div className="py-8">
                    <div className="mx-auto max-w-6xl px-4 sm:px-6 lg:px-8">
                        <Tabs defaultValue="assessment" className="space-y-6">
                            <TabsList className="grid w-full grid-cols-2">
                                <TabsTrigger value="assessment" className="flex items-center gap-2">
                                    <FileText className="h-4 w-4" />
                                    Assessment
                                </TabsTrigger>
                                <TabsTrigger value="report" className="flex items-center gap-2">
                                    <Trophy className="h-4 w-4" />
                                    Report
                                </TabsTrigger>
                            </TabsList>

                            <TabsContent value="assessment" className="space-y-6">
                                {/* Product/Service Overview - S2D specific section */}
                                <Card>
                                    <CardHeader>
                                        <CardTitle className="flex items-center gap-2">
                                            <div className="h-3 w-3 rounded-full bg-blue-500"></div>
                                            Product/Service Overview
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <div className="space-y-2">
                                            <div>
                                                <span className="font-medium">Business Name:</span>
                                                <span className="ml-2 text-gray-600">{businessData.businessName || 'No business name provided'}</span>
                                            </div>
                                            <div>
                                                <span className="font-medium">Description:</span>
                                                <span className="ml-2 text-gray-600">{businessData.businessDescription}</span>
                                            </div>
                                            <div>
                                                <span className="font-medium">Product Revenue:</span>
                                                <span className="ml-2 text-gray-600">{formatRevenue(getProductRevenue())}</span>
                                            </div>
                                        </div>
                                    </CardContent>
                                </Card>

                                {/* Overall S2D Scores - Using reusable component */}
                                <OverallScoresCard
                                    title="Overall S2D Scores"
                                    processScore={calculateOverallScores.processMaturity}
                                    ownerScore={calculateOverallScores.ownerIndependence}
                                    assessmentType="s2d"
                                />

                                {/* Detailed S2D Breakdown */}
                                <Card>
                                    <CardHeader>
                                        <CardTitle className="flex items-center gap-2">
                                            <BarChart3 className="h-5 w-5" />
                                            Detailed S2D Breakdown
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent className="space-y-6">
                                        {Object.values(subAreaScores).map((section: SubAreaScore, index) => (
                                            <div key={index} className="space-y-4">
                                                <div className="flex items-center justify-between">
                                                    <h4 className="text-lg font-semibold">
                                                        {section.title}: {section.score} / {section.maxScore} Points
                                                    </h4>
                                                </div>

                                                <div className="space-y-4">
                                                    {section.questions.map((questionGroup: GroupedQuestion, qIndex: number) => (
                                                        <QuestionGroupDisplay
                                                            key={qIndex}
                                                            questionGroup={questionGroup}
                                                            getSelectedOptionText={getSelectedOptionText}
                                                        />
                                                    ))}
                                                </div>
                                            </div>
                                        ))}
                                    </CardContent>
                                </Card>
                            </TabsContent>

                            <TabsContent value="report" className="space-y-6">
                                {/* Executive Summary */}
                                <Card>
                                    <CardHeader>
                                        <CardTitle className="flex items-center gap-2">
                                            <FileText className="h-5 w-5" />
                                            Executive Summary
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <div className="whitespace-pre-line text-gray-700">{executiveSummary}</div>
                                    </CardContent>
                                </Card>

                                {/* Key Findings & Strategic Positioning - Using reusable component */}
                                <KeyFindingsCard
                                    processScore={calculateOverallScores.processMaturity}
                                    ownerScore={calculateOverallScores.ownerIndependence}
                                    assessmentType="s2d"
                                    ownerStrategicPositioning={ownerStrategicPositioning}
                                />

                                {/* Focus Areas & Next Steps */}
                                <Card>
                                    <CardHeader>
                                        <CardTitle className="flex items-center gap-2">
                                            <Trophy className="h-5 w-5" />
                                            Focus Areas & Next Steps (Conceptual)
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                        <ul className="list-inside list-disc space-y-2 text-gray-700">
                                            {focusAreas.map((item, index) => (
                                                <li key={index}>{item}</li>
                                            ))}
                                        </ul>
                                    </CardContent>
                                </Card>
                            </TabsContent>
                        </Tabs>

                        {/* Navigation Buttons */}
                        <div className="mt-8 flex items-center justify-between border-t pt-8">
                            <div>
                                {previousAssessment && (
                                    <Button variant="outline" asChild>
                                        <Link href={`/assessments/form/${previousAssessment.slug}`}>
                                            <ChevronLeft className="mr-2 h-4 w-4" />
                                            Previous: {previousAssessment.name}
                                        </Link>
                                    </Button>
                                )}
                            </div>

                            <div>
                                {nextAssessment && (
                                    <Button disabled={!canAccessNext} asChild={canAccessNext}>
                                        {canAccessNext ? (
                                            <Link
                                                href={`/assessments/form/${nextAssessment.slug}`}
                                                className="bg-blue-600 text-white hover:bg-blue-700"
                                            >
                                                Continue to Next Section â†’
                                            </Link>
                                        ) : (
                                            <>Continue to Next Section â†’</>
                                        )}
                                    </Button>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </AppLayout>
    );
}

