import { DependentOption, NaicsSector, QuestionProps, QuestionType } from '@/types/assessment';
import { memo } from 'react';
import DependentSelectQuestion from './questions/dependent-select-question';
import EmailQuestion from './questions/email-question';
import MultipleChoiceQuestion from './questions/multiple-choice-question';
import NumberQuestion from './questions/number-question';
import SelectQuestion from './questions/select-question';
import TextQuestion from './questions/text-question';
import TextareaQuestion from './questions/textarea-question';
import { QuestionReviewCheckbox } from './question-review-checkbox';

// Re-export types for convenience
export type { QuestionProps, QuestionType } from '@/types/assessment';

interface QuestionRendererProps {
    question: QuestionProps['question'];
    value: string;
    onChange: (value: string) => void;
    error?: string;
    disabled?: boolean;
    parentValue?: string; // For dependent questions
    dependentOptions?: DependentOption[]; // For dependent questions
    naicsSectors?: NaicsSector[]; // For industry sector questions
    // Review-related props
    reviewChecked?: boolean;
    onReviewChange?: (checked: boolean) => void;
    onAutoSave: () => void;
}

function QuestionRenderer({
    question,
    value,
    onChange,
    error,
    disabled,
    parentValue,
    dependentOptions,
    naicsSectors,
    reviewChecked = false,
    onReviewChange,
    onAutoSave,
}: QuestionRendererProps) {
    const props: QuestionProps = {
        question,
        value,
        onChange,
        error,
        disabled,
        onAutoSave,
    };

    const renderQuestionComponent = () => {
        // Handle special case for industry subsector question (dependent on sector)
        if (question.question_id === 'industrySubSector' && question.question_type === QuestionType.SELECT_DEPENDENT && naicsSectors) {
            // Use our enhanced SelectQuestion component that has context integration
            console.log('ðŸŽ¯ Routing industrySubSector to enhanced SelectQuestion with context');
            return <SelectQuestion {...props} naicsSectors={naicsSectors} />;
        }

        switch (question.question_type) {
            case QuestionType.MCQ:
                return <MultipleChoiceQuestion {...props} />;
            case QuestionType.NUMBER:
                return <NumberQuestion {...props} />;
            case QuestionType.TEXT:
                return <TextQuestion {...props} />;
            case QuestionType.TEXTAREA:
                return <TextareaQuestion {...props} />;
            case QuestionType.EMAIL:
                return <EmailQuestion {...props} />;
            case QuestionType.SELECT:
                // Handle industry sector question specially
                if (question.question_id === 'industrySector' && naicsSectors) {
                    return <SelectQuestion {...props} naicsSectors={naicsSectors} />;
                }
                return <SelectQuestion {...props} />;
            case QuestionType.SELECT_DEPENDENT:
                return <DependentSelectQuestion {...props} parentValue={parentValue} dependentOptions={dependentOptions} />;
            default:
                return (
                    <div className="rounded-md border border-yellow-200 bg-yellow-50 p-4">
                        <p className="text-sm text-yellow-800">Unsupported question type: {question.question_type}</p>
                    </div>
                );
        }
    };

    return (
        <div className="space-y-3">
            {/* Mobile: Review button on top */}
            {onReviewChange && (
                <div className="flex md:hidden">
                    <QuestionReviewCheckbox
                        checked={reviewChecked}
                        onChange={onReviewChange}
                        className="relative top-auto right-auto"
                        onAutoSave={onAutoSave}
                    />
                </div>
            )}

            {/* Desktop: Question with review button positioned absolutely */}
            <div className="relative">
                {/* Desktop: Review button on the right */}
                {onReviewChange && (
                    <div className="hidden md:block">
                        <QuestionReviewCheckbox checked={reviewChecked} onChange={onReviewChange} onAutoSave={onAutoSave} />
                    </div>
                )}
                {renderQuestionComponent()}
            </div>
        </div>
    );
}

// Memoize the component to prevent unnecessary re-renders
export default memo(QuestionRenderer);

