// Theme management with persistence
export function useAppearance() {
  const [appearance, setAppearance] = useState<'light' | 'dark' | 'system'>('system')

  const updateAppearance = useCallback((newAppearance: AppearanceMode) => {
    setAppearance(newAppearance)

    // Persist to backend
    router.patch('/settings/appearance', { appearance: newAppearance }, {
      preserveState: true,
