import { render, screen, fireEvent } from '@testing-library/react'
import ProcessActivitiesCard from '@/components/ProcessActivitiesCard'

describe('ProcessActivitiesCard', () => {
  const mockActivities = [
    { id: '1', name: 'Activity 1', position: 1 },
    { id: '2', name: 'Activity 2', position: 2 },
  ]

  test('renders activities in correct order', () => {
    render(<ProcessActivitiesCard activities={mockActivities} />)

    const activities = screen.getAllByTestId('activity-item')
    expect(activities).toHaveLength(2)
    expect(activities[0]).toHaveTextContent('Activity 1')
  })

  test('handles drag and drop reordering', async () => {
