```tsx
// Reusable version card for SOP, Blueprint, and other content types
interface VersionCardProps {
  version: BaseVersion
  isCurrentVersion: boolean
  isViewingThisVersion: boolean
  getStatusBadge: (status: number) => React.JSX.Element
  href?: string
  SafeLink?: React.ComponentType<{ href: string; className?: string; children: React.ReactNode }>
  onStatusChange?: (version: BaseVersion) => void
  showStatusButton?: boolean
}

export function VersionCard({ 
  version, 
  isCurrentVersion, 
  isViewingThisVersion, 
  getStatusBadge, 
  href, 
  SafeLink, 
  onStatusChange, 
  showStatusButton = false 
}: VersionCardProps) {
  // Unified card design for consistent version management
  const cardContent = (
    <div className={cn(
      "rounded-lg border p-4 transition-colors hover:bg-muted/50",
      isViewingThisVersion && "border-primary bg-primary/5"
    )}>
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <span className="font-semibold">{version.version_label}</span>
          {getStatusBadge(version.status)}
        </div>
        {showStatusButton && (
          <Button
            variant="ghost"
            size="sm"
            onClick={() => onStatusChange?.(version)}
          >
            Set Status
          </Button>
        )}
      </div>
      {version.change_summary && (
        <p className="mt-2 text-sm text-muted-foreground">{version.change_summary}</p>
      )}
    </div>
  )

  // Support both regular links and SafeLink components
  return href && SafeLink ? (
    <SafeLink href={href}>{cardContent}</SafeLink>
  ) : href ? (
    <a href={href}>{cardContent}</a>
  ) : cardContent
}
