 18	/**
 19	 * JWTPro class for interacting with WordPress JWT Authentication and REST API.
 20	 */
 21	export class JWTPro {
 22	  private readonly config: Required<Omit<JWTProConfig, 'tokenStorageStrategy' | 'storageKeys' | 'logger'>> & { 
 23	    tokenStorageStrategy: ITokenStorageStrategy | 'localStorage' | 'sessionStorage' | 'memory';
 24	    storageKeys: StorageKeys;
 25	    logger: ILogger;
 26	  };
 27	  private accessToken: string | null = null;
 28	  private refreshTokenVal: string | null = null;
 29	  private userData: UserData | null = null;
 30	  private tokenStorage!: ITokenStorageStrategy;
 31	  private isTokensLoaded = false;
 32	
 33	  /**
 34	   * Event emitter for state changes
 35	   */
 36	  public events: JWTProEventEmitter = new JWTProEventEmitter();
 37	
 38	  /**
 39	   * Interceptors for requests and responses
 40	   */
 41	  public interceptors: InterceptorManager = new InterceptorManager();
 42	
 43	  /**
 44	   * Creates an instance of JWTPro.
 45	   * @param {JWTProConfig} config - The configuration options for the client.
 46	   */
 47	  constructor(config: JWTProConfig) {
 48	    const defaultStorageKeys: StorageKeys = {
 49	      accessToken: 'jwt_access_token',
 50	      refreshToken: 'jwt_refresh_token',
 51	      userData: 'jwt_user_data'
 52	    };
 53	
 54	    const mergedStorageKeys: StorageKeys = {
 55	      accessToken: config.storageKeys?.accessToken || defaultStorageKeys.accessToken,
 56	      refreshToken: config.storageKeys?.refreshToken || defaultStorageKeys.refreshToken,
 57	      userData: config.storageKeys?.userData || defaultStorageKeys.userData
 58	    };
 59	
 60	    const { storageKeys: _, logger: __, ...restConfig } = config;
 61	    
 62	    this.config = {
 63	      authNamespace: 'jwt-auth/v1',
 64	      wpApiNamespace: 'wp/v2',
 65	      tokenStorageStrategy: isBrowser ? 'localStorage' : 'memory',
 66	      autoRefreshToken: false,
 67	      refreshThreshold: 300,
 68	      storageKeys: mergedStorageKeys,
 69	      logger: config.logger || createDefaultLogger(),
 70	      ...restConfig,
 71	    };
 72	    this.initializeTokenStorage();
 73	  }
 74	
 75	  private initializeTokenStorage(): void {
 76	    this.tokenStorage = initializeTokenStorage(this.config.tokenStorageStrategy, this.config.storageKeys);
 77	  }
 78	
 79	  private async saveTokensToStorage(accessToken: string, refreshToken: string, userData: UserData): Promise<void> {
 80	    this.accessToken = accessToken;
 81	    this.refreshTokenVal = refreshToken;
 82	    this.userData = userData;
 83	    this.isTokensLoaded = true;
 84	    
 85	    try {
 86	      await this.tokenStorage.setItem(this.config.storageKeys.accessToken, accessToken);
 87	      await this.tokenStorage.setItem(this.config.storageKeys.refreshToken, refreshToken);
 88	      await this.tokenStorage.setItem(this.config.storageKeys.userData, JSON.stringify(userData));
 89	    } catch (error) {
 90	      this.config.logger.error('Failed to save tokens to storage', error);
 91	      throw new Error('Failed to save authentication tokens');
 92	    }
 93	  }
 94	
 95	  private async loadTokensFromStorage(): Promise<void> {
 96	    if (this.isTokensLoaded) return;
 97	    
 98	    try {
 99	      this.accessToken = await this.tokenStorage.getItem(this.config.storageKeys.accessToken);
100	      this.refreshTokenVal = await this.tokenStorage.getItem(this.config.storageKeys.refreshToken);
101	      const userDataStr = await this.tokenStorage.getItem(this.config.storageKeys.userData);
102	      
103	      if (userDataStr) {
104	        try {
105	          this.userData = JSON.parse(userDataStr);
106	        } catch (e) {
107	          this.config.logger.error('Error parsing user data from storage', e);
108	          this.userData = null;
109	          await this.tokenStorage.removeItem(this.config.storageKeys.userData);
110	          this.events.emit({
111	            type: 'error',
112	            error: new Error('Failed to parse user data from storage')
113	          });
114	        }
115	      }
116	      
117	      this.isTokensLoaded = true;
118	    } catch (error) {
119	      this.config.logger.error('Failed to load tokens from storage', error);
120	      throw new Error('Failed to load authentication tokens');
121	    }
122	  }
123	
124	  private async clearTokensFromStorage(): Promise<void> {
125	    this.accessToken = null;
126	    this.refreshTokenVal = null;
127	    this.userData = null;
128	    this.isTokensLoaded = false;
129	    
130	    try {
131	      if (this.tokenStorage.clearAll) {
132	        await this.tokenStorage.clearAll();
133	      } else {
134	        await this.tokenStorage.removeItem(this.config.storageKeys.accessToken);
135	        await this.tokenStorage.removeItem(this.config.storageKeys.refreshToken);
136	        await this.tokenStorage.removeItem(this.config.storageKeys.userData);
137	      }
138	    } catch (error) {
139	      this.config.logger.error('Failed to clear tokens from storage', error);
140	      throw new Error('Failed to clear authentication tokens');
141	    }
142	  }
