144	  private async _fetch(url: string, options: RequestInit): Promise<FetchResponse> {
145	    const fetchFn = await getFetch();
146	
147	    // Prepare the request for interceptors
148	    const originalRequest = { ...options, url };
149	
150	    try {
151	      // Apply request interceptors
152	      const processedRequest = await this.interceptors.processRequest(originalRequest);
153	
154	      // Convert browser RequestInit to node-fetch RequestInit if needed
155	      const fetchOptions: FetchRequestInit = {
156	        ...processedRequest,
157	        body: processedRequest.body ? String(processedRequest.body) : undefined,
158	        headers: processedRequest.headers
159	      };
160	
161	      // Extract URL from processed request
162	      const processedUrl = processedRequest.url;
163	
164	      // Execute the fetch request
165	      const response = await fetchFn(processedUrl, fetchOptions);
166	
167	      // Apply response interceptors
168	      return await this.interceptors.processResponse(response, originalRequest);
169	    } catch (error) {
170	      // Apply error interceptors
171	      const processedError = await this.interceptors.processError(error, originalRequest);
172	      throw processedError;
173	    }
174	  }
