176	  /**
177	   * Logs in a user with username and password.
178	   * @param {string} username - The user's username.
179	   * @param {string} password - The user's password.
180	   * @returns {Promise<UserData>} The user data upon successful login.
181	   */
182	  async login(username: string, password: string): Promise<UserData> {
183	    const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/token`;
184	    try {
185	      const response = await this._fetch(endpoint, {
186	        method: 'POST',
187	        headers: {
188	          'Content-Type': 'application/json',
189	        },
190	        body: JSON.stringify({ username, password }),
191	      });
192	      if (!response.ok) {
193	        let errorData;
194	        try {
195	          errorData = await response.json();
196	        } catch (e) {
197	          errorData = { message: `Request failed with status ${response.status}` };
198	        }
199	        const error = {
200	            message: `API Error: ${response.status} - ${response.statusText}`,
201	            status: response.status,
202	            data: errorData
203	        };
204	
205	        this.config.logger.error('Login failed', error);
206	        this.events.emit({
207	          type: 'error',
208	          error: new Error(error.message),
209	          data: error
210	        });
211	
212	        throw error;
213	      }
214	      const data = await response.json() as TokenResponse;
215	      const userData: UserData = {
216	        id: data.user_id,
217	        email: data.user_email,
218	        nicename: data.user_nicename,
219	        displayName: data.user_display_name,
220	      };
221	      await this.saveTokensToStorage(data.token, data.refresh_token, userData);
222	
223	      this.events.emit({
224	        type: 'login',
225	        data: { userData }
226	      });
227	
228	      return userData;
229	    } catch (error) {
230	      this.config.logger.error('Login failed', error);
231	      throw error;
232	    }
233	  }
234	
235	  /**
236	   * Logs out the current user by clearing stored tokens.
237	   * @returns {Promise<void>}
238	   */
239	  async logout(): Promise<void> {
240	    await this.clearTokensFromStorage();
241	
242	    this.events.emit({
243	      type: 'logout'
244	    });
245	
246	    this.config.logger.info('Logout successful. Tokens cleared.');
247	  }
248	
249	
250	
251	  /**
252	   * Retrieves the current refresh token.
253	   * @returns {Promise<string | null>} The refresh token or null if not available.
254	   */
255	  async getRefreshToken(): Promise<string | null> {
256	    if (!this.refreshTokenVal) {
257	        await this.loadTokensFromStorage();
258	    }
259	    return this.refreshTokenVal;
260	  }
261	
262	
263	
264	
265	
266	  /**
267	   * Attaches event listeners. Convenience method for events.on().
268	   * @param eventType Event type to listen for
269	   * @param listener Callback function
270	   * @returns Function to remove the listener
271	   */
272	  on(eventType: JWTProEventType, listener: (event: any) => void): () => void {
273	    return this.events.on(eventType, listener);
274	  }
275	
276	  /**
277	   * Clean up resources when the client is no longer needed
278	   */
279	  destroy(): void {
280	    this.events.removeAllListeners();
281	    this.isTokensLoaded = false;
282	    this.accessToken = null;
283	    this.refreshTokenVal = null;
284	    this.userData = null;
285	  }
286	
287	  // Modern API methods - shorter, cleaner interface
288	  
289	  /**
290	   * Checks if user is currently authenticated
291	   * @returns {Promise<boolean>} True if user is authenticated
292	   */
293	  async check(): Promise<boolean> {
294	    if (!this.accessToken) {
295	      await this.loadTokensFromStorage();
296	    }
297	    return !!this.accessToken;
298	  }
299	
300	  /**
301	   * Validates the current token with the server
302	   * @returns {Promise<boolean>} True if token is valid
303	   */
304	  async validate(): Promise<boolean> {
305	    if (!this.accessToken) {
306	      await this.loadTokensFromStorage();
307	      if (!this.accessToken) {
308	        this.config.logger.warn('No access token found to validate');
309	        return false;
310	      }
311	    }
312	    try {
313	      const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/validate`;
314	      const response = await this._fetch(endpoint, {
315	        method: 'POST',
316	        headers: {
317	          'Content-Type': 'application/json',
318	          'Authorization': `Bearer ${this.accessToken}`
319	        },
320	      });
321	      if (!response.ok) {
322	        this.config.logger.warn(`Token validation failed with status ${response.status}: ${response.statusText}`);
323	        return false;
324	      }
325	      const data = await response.json() as ValidateApiResponse;
326	      return data.code === 'jwt_auth_valid_token' && data.data.status === 200;
327	    } catch (error) {
328	      // If validation fails due to an API error (e.g. 401, 403), it's an invalid token.
329	      this.config.logger.warn('Token validation failed via API', error);
330	      return false;
331	    }
332	  }
333	
334	  /**
335	   * Refreshes the current access token
336	   * @returns {Promise<TokenResponse>} New token response
337	   */
338	  async refresh(): Promise<TokenResponse> {
339	    if (!this.refreshTokenVal) {
340	      await this.loadTokensFromStorage();
341	      if (!this.refreshTokenVal) {
342	        const error = new Error('No refresh token available for refreshing.');
343	        this.events.emit({
344	          type: 'error',
345	          error
346	        });
347	        throw error;
348	      }
349	    }
350	    const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/refresh`;
351	    try {
352	      const response = await this._fetch(endpoint, {
353	        method: 'POST',
354	        headers: {
355	          'Content-Type': 'application/json',
356	        },
357	        body: JSON.stringify({ refresh_token: this.refreshTokenVal }),
358	      });
359	      if (!response.ok) {
360	        this.config.logger.warn(`Token refresh failed with status ${response.status}: ${response.statusText}`);
361	        await this.clearTokensFromStorage(); // Clear tokens if refresh fails, forcing re-login
362	
363	        const error = new Error('Token refresh failed.');
364	        this.events.emit({
365	          type: 'error',
366	          error,
367	          data: { status: response.status, statusText: response.statusText }
368	        });
369	
370	        throw error;
371	      }
372	      const data = await response.json() as TokenResponse;
373	      const userData: UserData = {
374	        id: data.user_id,
375	        email: data.user_email,
376	        nicename: data.user_nicename,
377	        displayName: data.user_display_name,
378	      };
379	      await this.saveTokensToStorage(data.token, data.refresh_token, userData);
380	
381	      this.events.emit({
382	        type: 'tokenRefresh',
383	        data: { userData }
384	      });
385	
386	      this.config.logger.info('Token refreshed successfully');
387	      return data;
388	    } catch (error) {
389	      this.config.logger.error('Token refresh failed', error);
390	      await this.clearTokensFromStorage(); // Clear tokens if refresh fails, forcing re-login
391	      throw error;
392	    }
393	  }
394	
395	  /**
396	   * Retrieves the current access token
397	   * @returns {Promise<string | null>} The access token
398	   */
399	  async token(): Promise<string | null> {
400	    if (!this.accessToken) {
401	      await this.loadTokensFromStorage();
402	    }
403	    return this.accessToken;
404	  }
405	
406	  /**
407	   * Retrieves the current user data
408	   * @returns {Promise<UserData | null>} The user data
409	   */
410	  async user(): Promise<UserData | null> {
411	    if (!this.userData) {
412	      await this.loadTokensFromStorage();
413	    }
414	    return this.userData;
415	  }
416	
417	  /**
418	   * Makes API requests with automatic authentication
419	   * @template T The expected response type
420	   * @param {string} method The HTTP method
421	   * @param {string} path The API path
422	   * @param {Record<string, any>} [data] The data payload
423	   * @param {Record<string, string>} [customHeaders] Custom headers
424	   * @returns {Promise<T>} The API response
425	   */
426	  async api<T = any>(method: string, path: string, data?: Record<string, any>, customHeaders?: Record<string, string>): Promise<T> {
427	    const headers: HeadersInit = {
428	      'Content-Type': 'application/json',
429	      ...(customHeaders || {}),
430	    };
431	
432	    // Ensure accessToken is loaded for the request
433	    if (!this.accessToken) await this.loadTokensFromStorage();
434	    if (this.accessToken) {
435	      headers['Authorization'] = `Bearer ${this.accessToken}`;
436	    }
437	
438	    let fullUrl = `${this.config.baseUrl.replace(/\/$/, '')}/${path.replace(/^\/+|\/+$/g, '')}`;
439	    const fetchOptions: RequestInit = {
440	      method: method.toUpperCase(),
441	      headers,
442	    };
443	
444	    if (data && (method.toUpperCase() === 'POST' || method.toUpperCase() === 'PUT' || method.toUpperCase() === 'PATCH')) {
445	      fetchOptions.body = JSON.stringify(data);
446	    }
447	
448	    if (data && method.toUpperCase() === 'GET' && Object.keys(data).length > 0) {
449	        const queryString = new URLSearchParams(data as any).toString();
450	        fullUrl += `?${queryString}`;
451	    }
452	
453	    try {
454	      const response = await this._fetch(fullUrl, fetchOptions);
455	      if (!response.ok) {
456	        let errorData;
457	        try {
458	          errorData = await response.json();
459	        } catch (e) {
460	          errorData = { message: `Request failed with status ${response.status}` };
461	        }
462	
463	        const error = {
464	            message: `API Error: ${response.status} - ${response.statusText}`,
465	            status: response.status,
466	            data: errorData
467	        };
468	
469	        this.config.logger.error('API request failed', error);
470	        this.events.emit({
471	          type: 'error',
472	          error: new Error(error.message),
473	          data: error
474	        });
475	
476	        throw error;
477	      }
478	
479	      // Handle cases where response might be empty (e.g., 204 No Content)
480	      const contentType = response.headers.get("content-type");
481	      if (contentType && contentType.indexOf("application/json") !== -1) {
482	        return await response.json() as T;
483	      }
484	      return undefined as unknown as T; // Or handle as per API spec for non-JSON responses
485	    } catch (error) {
486	      this.config.logger.error(`Error during API call to ${fullUrl}`, error);
487	      throw error;
488	    }
489	  }
490	}
