import {
  JWTProConfig,
  ITokenStorageStrategy,
  TokenResponse,
  ValidateApiResponse,
  RefreshResponse,
  UserData,
  ILogger,
  StorageKeys
} from './types';
import { initializeTokenStorage } from './storage';
import { environmentFetch, getFetch, FetchType, FetchResponse, FetchRequestInit } from './utils/fetch';
import { isBrowser } from './utils/environment';
import { JWTProEventEmitter, JWTProEventType } from './utils/events';
import { InterceptorManager } from './utils/interceptors';
import { createDefaultLogger } from './utils/logger';

/**
 * JWTPro class for interacting with WordPress JWT Authentication and REST API.
 */
export class JWTPro {
  private readonly config: Required<Omit<JWTProConfig, 'tokenStorageStrategy' | 'storageKeys' | 'logger'>> & { 
    tokenStorageStrategy: ITokenStorageStrategy | 'localStorage' | 'sessionStorage' | 'memory';
    storageKeys: StorageKeys;
    logger: ILogger;
  };
  private accessToken: string | null = null;
  private refreshTokenVal: string | null = null;
  private userData: UserData | null = null;
  private tokenStorage!: ITokenStorageStrategy;
  private isTokensLoaded = false;

  /**
   * Event emitter for state changes
   */
  public events: JWTProEventEmitter = new JWTProEventEmitter();

  /**
   * Interceptors for requests and responses
   */
  public interceptors: InterceptorManager = new InterceptorManager();

  /**
   * Creates an instance of JWTPro.
   * @param {JWTProConfig} config - The configuration options for the client.
   */
  constructor(config: JWTProConfig) {
    const defaultStorageKeys: StorageKeys = {
      accessToken: 'jwt_access_token',
      refreshToken: 'jwt_refresh_token',
      userData: 'jwt_user_data'
    };

    const mergedStorageKeys: StorageKeys = {
      accessToken: config.storageKeys?.accessToken || defaultStorageKeys.accessToken,
      refreshToken: config.storageKeys?.refreshToken || defaultStorageKeys.refreshToken,
      userData: config.storageKeys?.userData || defaultStorageKeys.userData
    };

    const { storageKeys: _, logger: __, ...restConfig } = config;

    this.config = {
      authNamespace: 'jwt-auth/v1',
      wpApiNamespace: 'wp/v2',
      tokenStorageStrategy: isBrowser ? 'localStorage' : 'memory',
      autoRefreshToken: false,
      refreshThreshold: 300,
      storageKeys: mergedStorageKeys,
      logger: config.logger || createDefaultLogger(),
      ...restConfig,
    };
    this.initializeTokenStorage();
  }

  private initializeTokenStorage(): void {
    this.tokenStorage = initializeTokenStorage(this.config.tokenStorageStrategy, this.config.storageKeys);
  }

  private async saveTokensToStorage(accessToken: string, refreshToken: string, userData: UserData): Promise<void> {
    this.accessToken = accessToken;
    this.refreshTokenVal = refreshToken;
    this.userData = userData;
    this.isTokensLoaded = true;

    try {
      await this.tokenStorage.setItem(this.config.storageKeys.accessToken, accessToken);
      await this.tokenStorage.setItem(this.config.storageKeys.refreshToken, refreshToken);
      await this.tokenStorage.setItem(this.config.storageKeys.userData, JSON.stringify(userData));
    } catch (error) {
      this.config.logger.error('Failed to save tokens to storage', error);
      throw new Error('Failed to save authentication tokens');
    }
  }

  private async loadTokensFromStorage(): Promise<void> {
    if (this.isTokensLoaded) return;

    try {
      this.accessToken = await this.tokenStorage.getItem(this.config.storageKeys.accessToken);
      this.refreshTokenVal = await this.tokenStorage.getItem(this.config.storageKeys.refreshToken);
      const userDataStr = await this.tokenStorage.getItem(this.config.storageKeys.userData);

      if (userDataStr) {
        try {
          this.userData = JSON.parse(userDataStr);
        } catch (e) {
          this.config.logger.error('Error parsing user data from storage', e);
          this.userData = null;
          await this.tokenStorage.removeItem(this.config.storageKeys.userData);
          this.events.emit({
            type: 'error',
            error: new Error('Failed to parse user data from storage')
          });
        }
      }

      this.isTokensLoaded = true;
    } catch (error) {
      this.config.logger.error('Failed to load tokens from storage', error);
      throw new Error('Failed to load authentication tokens');
    }
  }

  private async clearTokensFromStorage(): Promise<void> {
    this.accessToken = null;
    this.refreshTokenVal = null;
    this.userData = null;
    this.isTokensLoaded = false;

    try {
      if (this.tokenStorage.clearAll) {
        await this.tokenStorage.clearAll();
      } else {
        await this.tokenStorage.removeItem(this.config.storageKeys.accessToken);
        await this.tokenStorage.removeItem(this.config.storageKeys.refreshToken);
        await this.tokenStorage.removeItem(this.config.storageKeys.userData);
      }
    } catch (error) {
      this.config.logger.error('Failed to clear tokens from storage', error);
      throw new Error('Failed to clear authentication tokens');
    }
  }

  private async _fetch(url: string, options: RequestInit): Promise<FetchResponse> {
    const fetchFn = await getFetch();

    // Prepare the request for interceptors
    const originalRequest = { ...options, url };

    try {
      // Apply request interceptors
      const processedRequest = await this.interceptors.processRequest(originalRequest);

      // Convert browser RequestInit to node-fetch RequestInit if needed
      const fetchOptions: FetchRequestInit = {
        ...processedRequest,
        body: processedRequest.body ? String(processedRequest.body) : undefined,
        headers: processedRequest.headers
      };

      // Extract URL from processed request
      const processedUrl = processedRequest.url;

      // Execute the fetch request
      const response = await fetchFn(processedUrl, fetchOptions);

      // Apply response interceptors
      return await this.interceptors.processResponse(response, originalRequest);
    } catch (error) {
      // Apply error interceptors
      const processedError = await this.interceptors.processError(error, originalRequest);
      throw processedError;
    }
  }

  /**
   * Logs in a user with username and password.
   * @param {string} username - The user's username.
   * @param {string} password - The user's password.
   * @returns {Promise<UserData>} The user data upon successful login.
   */
  async login(username: string, password: string): Promise<UserData> {
    const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/token`;
    try {
      const response = await this._fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });
      if (!response.ok) {
        let errorData;
        try {
          errorData = await response.json();
        } catch (e) {
          errorData = { message: `Request failed with status ${response.status}` };
        }
        const error = {
            message: `API Error: ${response.status} - ${response.statusText}`,
            status: response.status,
            data: errorData
        };

        this.config.logger.error('Login failed', error);
        this.events.emit({
          type: 'error',
          error: new Error(error.message),
          data: error
        });

        throw error;
      }
      const data = await response.json() as TokenResponse;
      const userData: UserData = {
        id: data.user_id,
        email: data.user_email,
        nicename: data.user_nicename,
        displayName: data.user_display_name,
      };
      await this.saveTokensToStorage(data.token, data.refresh_token, userData);

      this.events.emit({
        type: 'login',
        data: { userData }
      });

      return userData;
    } catch (error) {
      this.config.logger.error('Login failed', error);
      throw error;
    }
  }

  /**
   * Logs out the current user by clearing stored tokens.
   * @returns {Promise<void>}
   */
  async logout(): Promise<void> {
    await this.clearTokensFromStorage();

    this.events.emit({
      type: 'logout'
    });

    this.config.logger.info('Logout successful. Tokens cleared.');
  }



  /**
   * Retrieves the current refresh token.
   * @returns {Promise<string | null>} The refresh token or null if not available.
   */
  async getRefreshToken(): Promise<string | null> {
    if (!this.refreshTokenVal) {
        await this.loadTokensFromStorage();
    }
    return this.refreshTokenVal;
  }




  /**
   * Attaches event listeners. Convenience method for events.on().
   * @param eventType Event type to listen for
   * @param listener Callback function
   * @returns Function to remove the listener
   */
  on(eventType: JWTProEventType, listener: (event: any) => void): () => void {
    return this.events.on(eventType, listener);
  }

  /**
   * Clean up resources when the client is no longer needed
   */
  destroy(): void {
    this.events.removeAllListeners();
    this.isTokensLoaded = false;
    this.accessToken = null;
    this.refreshTokenVal = null;
    this.userData = null;
  }

  // Modern API methods - shorter, cleaner interface

  /**
   * Checks if user is currently authenticated
   * @returns {Promise<boolean>} True if user is authenticated
   */
  async check(): Promise<boolean> {
    if (!this.accessToken) {
      await this.loadTokensFromStorage();
    }
    return !!this.accessToken;
  }

  /**
   * Validates the current token with the server
   * @returns {Promise<boolean>} True if token is valid
   */
  async validate(): Promise<boolean> {
    if (!this.accessToken) {
      await this.loadTokensFromStorage();
      if (!this.accessToken) {
        this.config.logger.warn('No access token found to validate');
        return false;
      }
    }
    try {
      const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/validate`;
      const response = await this._fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.accessToken}`
        },
      });
      if (!response.ok) {
        this.config.logger.warn(`Token validation failed with status ${response.status}: ${response.statusText}`);
        return false;
      }
      const data = await response.json() as ValidateApiResponse;
      return data.code === 'jwt_auth_valid_token' && data.data.status === 200;
    } catch (error) {
      // If validation fails due to an API error (e.g. 401, 403), it's an invalid token.
      this.config.logger.warn('Token validation failed via API', error);
      return false;
    }
  }

  /**
   * Refreshes the current access token
   * @returns {Promise<TokenResponse>} New token response
   */
  async refresh(): Promise<TokenResponse> {
    if (!this.refreshTokenVal) {
      await this.loadTokensFromStorage();
      if (!this.refreshTokenVal) {
        const error = new Error('No refresh token available for refreshing.');
        this.events.emit({
          type: 'error',
          error
        });
        throw error;
      }
    }
    const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/refresh`;
    try {
      const response = await this._fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ refresh_token: this.refreshTokenVal }),
      });
      if (!response.ok) {
        this.config.logger.warn(`Token refresh failed with status ${response.status}: ${response.statusText}`);
        await this.clearTokensFromStorage(); // Clear tokens if refresh fails, forcing re-login

        const error = new Error('Token refresh failed.');
        this.events.emit({
          type: 'error',
          error,
          data: { status: response.status, statusText: response.statusText }
        });

        throw error;
      }
      const data = await response.json() as TokenResponse;
      const userData: UserData = {
        id: data.user_id,
        email: data.user_email,
        nicename: data.user_nicename,
        displayName: data.user_display_name,
      };
      await this.saveTokensToStorage(data.token, data.refresh_token, userData);

      this.events.emit({
        type: 'tokenRefresh',
        data: { userData }
      });

      this.config.logger.info('Token refreshed successfully');
      return data;
    } catch (error) {
      this.config.logger.error('Token refresh failed', error);
      await this.clearTokensFromStorage(); // Clear tokens if refresh fails, forcing re-login
      throw error;
    }
  }

  /**
   * Retrieves the current access token
   * @returns {Promise<string | null>} The access token
   */
  async token(): Promise<string | null> {
    if (!this.accessToken) {
      await this.loadTokensFromStorage();
    }
    return this.accessToken;
  }

  /**
   * Retrieves the current user data
   * @returns {Promise<UserData | null>} The user data
   */
  async user(): Promise<UserData | null> {
    if (!this.userData) {
      await this.loadTokensFromStorage();
    }
    return this.userData;
  }

  /**
   * Makes API requests with automatic authentication
   * @template T The expected response type
   * @param {string} method The HTTP method
   * @param {string} path The API path
   * @param {Record<string, any>} [data] The data payload
   * @param {Record<string, string>} [customHeaders] Custom headers
   * @returns {Promise<T>} The API response
   */
  async api<T = any>(method: string, path: string, data?: Record<string, any>, customHeaders?: Record<string, string>): Promise<T> {
    const headers: HeadersInit = {
      'Content-Type': 'application/json',
      ...(customHeaders || {}),
    };

    // Ensure accessToken is loaded for the request
    if (!this.accessToken) await this.loadTokensFromStorage();
    if (this.accessToken) {
      headers['Authorization'] = `Bearer ${this.accessToken}`;
    }

    let fullUrl = `${this.config.baseUrl.replace(/\/$/, '')}/${path.replace(/^\/+|\/+$/g, '')}`;
    const fetchOptions: RequestInit = {
      method: method.toUpperCase(),
      headers,
    };

    if (data && (method.toUpperCase() === 'POST' || method.toUpperCase() === 'PUT' || method.toUpperCase() === 'PATCH')) {
      fetchOptions.body = JSON.stringify(data);
    }

    if (data && method.toUpperCase() === 'GET' && Object.keys(data).length > 0) {
        const queryString = new URLSearchParams(data as any).toString();
        fullUrl += `?${queryString}`;
    }

    try {
      const response = await this._fetch(fullUrl, fetchOptions);
      if (!response.ok) {
        let errorData;
        try {
          errorData = await response.json();
        } catch (e) {
          errorData = { message: `Request failed with status ${response.status}` };
        }

        const error = {
            message: `API Error: ${response.status} - ${response.statusText}`,
            status: response.status,
            data: errorData
        };

        this.config.logger.error('API request failed', error);
        this.events.emit({
          type: 'error',
          error: new Error(error.message),
          data: error
        });

        throw error;
      }

      // Handle cases where response might be empty (e.g., 204 No Content)
      const contentType = response.headers.get("content-type");
      if (contentType && contentType.indexOf("application/json") !== -1) {
        return await response.json() as T;
      }
      return undefined as unknown as T; // Or handle as per API spec for non-JSON responses
    } catch (error) {
      this.config.logger.error(`Error during API call to ${fullUrl}`, error);
      throw error;
    }
  }
}

