import { createMemoryStorage } from './memory';
import { createBrowserStorage } from './browser';
import { ITokenStorageStrategy, StorageKeys } from '../types';
import { isBrowser } from '../utils/environment';

/**
 * Initializes a token storage strategy based on the provided configuration.
 * @param strategy The storage strategy to use
 * @param storageKeys Optional custom storage keys
 * @returns An initialized ITokenStorageStrategy instance
 */
export function initializeTokenStorage(
  strategy: ITokenStorageStrategy | 'localStorage' | 'sessionStorage' | 'memory' = isBrowser ? 'localStorage' : 'memory',
  storageKeys?: StorageKeys
): ITokenStorageStrategy {
  if (typeof strategy === 'string') {
    switch (strategy) {
      case 'localStorage':
        return createBrowserStorage('localStorage', storageKeys);
      case 'sessionStorage':
        return createBrowserStorage('sessionStorage', storageKeys);
      case 'memory':
      default:
        return createMemoryStorage(storageKeys);
    }
  }
  return strategy;
}

export {
  createMemoryStorage,
  createBrowserStorage
};

