      await storage.setItem('json-key', jsonData);
      const value = await storage.getItem('json-key');
      expect(value).toBe(jsonData);
    });

    it('should handle empty strings', async () => {
      await storage.setItem('empty-key', '');
      const value = await storage.getItem('empty-key');
      expect(value).toBe('');
    });
  });

  describe('Edge Cases', () => {
    it('should handle removing non-existent keys', async () => {
      await expect(storage.removeItem('non-existent')).resolves.toBeUndefined();
    });

    it('should handle clearing empty storage', async () => {
      await expect(storage.clearAll()).resolves.toBeUndefined();
