import { describe, it, expect, beforeEach, vi } from 'vitest';
import { createBrowserStorage } from '../../../src/storage/browser';

describe('Browser Storage', () => {
  beforeEach(() => {
    // Clear all mocks before each test
    vi.clearAllMocks();
  });

  describe('LocalStorage', () => {
    let storage: ReturnType<typeof createBrowserStorage>;

    beforeEach(() => {
      storage = createBrowserStorage('localStorage');
    });

    it('should set and get items from localStorage', async () => {
      (window.localStorage.getItem as any).mockReturnValue('test-value');

      await storage.setItem('test-key', 'test-value');
      const value = await storage.getItem('test-key');

      expect(window.localStorage.setItem).toHaveBeenCalledWith('test-key', 'test-value');
      expect(window.localStorage.getItem).toHaveBeenCalledWith('test-key');
      expect(value).toBe('test-value');
    });

    it('should return null for non-existent keys', async () => {
      (window.localStorage.getItem as any).mockReturnValue(null);

      const value = await storage.getItem('non-existent');
      expect(value).toBeNull();
    });

    it('should remove items from localStorage', async () => {
      await storage.removeItem('test-key');
      expect(window.localStorage.removeItem).toHaveBeenCalledWith('test-key');
    });

    it('should clear all items from localStorage', async () => {
      await storage.clearAll();
      expect(window.localStorage.clear).toHaveBeenCalled();
    });

    it('should handle localStorage exceptions gracefully', async () => {
      (window.localStorage.setItem as any).mockImplementation(() => {
        throw new Error('localStorage is full');
      });

      await expect(storage.setItem('key', 'value')).rejects.toThrow('localStorage is full');
    });
  });

  describe('SessionStorage', () => {
    let storage: ReturnType<typeof createBrowserStorage>;

    beforeEach(() => {
      storage = createBrowserStorage('sessionStorage');
    });

    it('should set and get items from sessionStorage', async () => {
      (window.sessionStorage.getItem as any).mockReturnValue('test-value');

      await storage.setItem('test-key', 'test-value');
      const value = await storage.getItem('test-key');

      expect(window.sessionStorage.setItem).toHaveBeenCalledWith('test-key', 'test-value');
      expect(window.sessionStorage.getItem).toHaveBeenCalledWith('test-key');
      expect(value).toBe('test-value');
    });

    it('should return null for non-existent keys', async () => {
      (window.sessionStorage.getItem as any).mockReturnValue(null);

      const value = await storage.getItem('non-existent');
      expect(value).toBeNull();
    });

    it('should remove items from sessionStorage', async () => {
      await storage.removeItem('test-key');
      expect(window.sessionStorage.removeItem).toHaveBeenCalledWith('test-key');
    });

    it('should clear all items from sessionStorage', async () => {
      await storage.clearAll();
      expect(window.sessionStorage.clear).toHaveBeenCalled();
    });

    it('should handle sessionStorage exceptions gracefully', async () => {
      (window.sessionStorage.setItem as any).mockImplementation(() => {
        throw new Error('sessionStorage is full');
      });

      await expect(storage.setItem('key', 'value')).rejects.toThrow('sessionStorage is full');
    });
  });

  describe('Custom Storage Keys', () => {
    it('should work with custom storage keys for localStorage', async () => {
      const storage = createBrowserStorage('localStorage', {
        accessToken: 'custom_access_token',
        refreshToken: 'custom_refresh_token',
        userData: 'custom_user_data'
      });

      (window.localStorage.getItem as any).mockReturnValue('token-value');

      await storage.setItem('custom_access_token', 'token-value');
      const value = await storage.getItem('custom_access_token');

      expect(window.localStorage.setItem).toHaveBeenCalledWith('custom_access_token', 'token-value');
      expect(value).toBe('token-value');
    });

    it('should work with custom storage keys for sessionStorage', async () => {
      const storage = createBrowserStorage('sessionStorage', {
        accessToken: 'custom_access_token',
        refreshToken: 'custom_refresh_token',
        userData: 'custom_user_data'
      });

      (window.sessionStorage.getItem as any).mockReturnValue('token-value');

      await storage.setItem('custom_access_token', 'token-value');
      const value = await storage.getItem('custom_access_token');

      expect(window.sessionStorage.setItem).toHaveBeenCalledWith('custom_access_token', 'token-value');
      expect(value).toBe('token-value');
    });
  });

  describe('Data Persistence', () => {
    it('should handle JSON data correctly', async () => {
      const storage = createBrowserStorage('localStorage');
      const jsonData = JSON.stringify({ id: 1, name: 'test' });

      (window.localStorage.getItem as any).mockReturnValue(jsonData);

      await storage.setItem('json-key', jsonData);
      const value = await storage.getItem('json-key');

      expect(value).toBe(jsonData);
    });

    it('should handle empty strings', async () => {
      const storage = createBrowserStorage('localStorage');

      (window.localStorage.getItem as any).mockReturnValue('');

      await storage.setItem('empty-key', '');
      const value = await storage.getItem('empty-key');

      expect(value).toBe('');
    });
  });

  describe('Edge Cases', () => {
    it('should handle storage being unavailable', async () => {
      // Mock localStorage being unavailable
      Object.defineProperty(window, 'localStorage', {
        value: undefined,
        writable: true
      });

      expect(() => createBrowserStorage('localStorage')).toThrow();
    });

    it('should handle storage throwing on access', async () => {
      Object.defineProperty(window, 'localStorage', {
        get() {
          throw new Error('localStorage not available');
        }
      });

      expect(() => createBrowserStorage('localStorage')).toThrow();
    });
  });
});

