describe('Environment Detection', () => {
  beforeEach(() => {
    // Reset modules to get fresh imports
    vi.resetModules();
  });

  afterEach(() => {
    vi.restoreAllMocks();
  });

  describe('Browser Environment', () => {
    it('should detect browser environment correctly', async () => {
      // Mock browser environment
      Object.defineProperty(global, 'window', {
        value: {
          document: {},
          localStorage: {},
          sessionStorage: {}
        },
        writable: true
      });

      Object.defineProperty(global, 'document', {
        value: {},
        writable: true
      });

      // Re-import to get fresh evaluation
      const { isBrowser, isNode } = await import('../../../src/utils/environment');

      expect(isBrowser).toBe(true);
      expect(isNode).toBe(false);
    });

    it('should detect browser environment with partial window object', async () => {
      // Mock minimal browser environment
      Object.defineProperty(global, 'window', {
        value: {},
        writable: true
      });

      Object.defineProperty(global, 'document', {
        value: undefined,
        writable: true
      });

      const { isBrowser, isNode } = await import('../../../src/utils/environment');

      expect(isBrowser).toBe(true);
      expect(isNode).toBe(false);
    });
  });

  describe('Node.js Environment', () => {
    it('should detect Node.js environment correctly', async () => {
      // Mock Node.js environment
      Object.defineProperty(global, 'window', {
        value: undefined,
        writable: true
      });

      Object.defineProperty(global, 'document', {
        value: undefined,
        writable: true
      });

      Object.defineProperty(global, 'process', {
        value: {
          versions: {
            node: '16.0.0'
          }
        },
        writable: true
      });

      const { isBrowser, isNode } = await import('../../../src/utils/environment');

      expect(isBrowser).toBe(false);
      expect(isNode).toBe(true);
    });

    it('should detect Node.js environment without process.versions', async () => {
      Object.defineProperty(global, 'window', {
        value: undefined,
        writable: true
      });

      Object.defineProperty(global, 'document', {
        value: undefined,
        writable: true
      });

      Object.defineProperty(global, 'process', {
        value: {},
        writable: true
      });

      const { isBrowser, isNode } = await import('../../../src/utils/environment');

      expect(isBrowser).toBe(false);
      expect(isNode).toBe(true);
    });
  });

  describe('Edge Cases', () => {
    it('should handle environment with both window and process', async () => {
      // Some environments like Electron have both
      Object.defineProperty(global, 'window', {
        value: { document: {} },
        writable: true
      });

      Object.defineProperty(global, 'document', {
        value: {},
        writable: true
      });

      Object.defineProperty(global, 'process', {
        value: {
          versions: { node: '16.0.0' }
        },
        writable: true
      });

      const { isBrowser, isNode } = await import('../../../src/utils/environment');

      // Browser detection takes precedence
      expect(isBrowser).toBe(true);
      expect(isNode).toBe(false);
    });

    it('should handle completely unknown environment', async () => {
      Object.defineProperty(global, 'window', {
        value: undefined,
        writable: true
      });

      Object.defineProperty(global, 'document', {
        value: undefined,
        writable: true
      });

      Object.defineProperty(global, 'process', {
        value: undefined,
        writable: true
      });

      const { isBrowser, isNode } = await import('../../../src/utils/environment');

      // Should default to Node.js-like behavior
      expect(isBrowser).toBe(false);
      expect(isNode).toBe(false);
    });
  });

  describe('Consistency', () => {
    it('should ensure isBrowser and isNode are mutually exclusive', async () => {
      const { isBrowser, isNode } = await import('../../../src/utils/environment');

      // They should never both be true
      expect(isBrowser && isNode).toBe(false);
    });

    it('should be deterministic across multiple imports', async () => {
      const { isBrowser: isBrowser1, isNode: isNode1 } = await import('../../../src/utils/environment');
      const { isBrowser: isBrowser2, isNode: isNode2 } = await import('../../../src/utils/environment');

      expect(isBrowser1).toBe(isBrowser2);
      expect(isNode1).toBe(isNode2);
    });
  });
});

