import { describe, it, expect, vi, beforeEach, afterEach } from 'vitest';

// Mock node-fetch
const mockNodeFetch = vi.fn();
vi.mock('node-fetch', () => ({
  default: mockNodeFetch
}));

describe('Fetch Utilities', () => {
  beforeEach(() => {
    vi.clearAllMocks();
    vi.resetModules();
  });

  afterEach(() => {
    vi.restoreAllMocks();
  });

  describe('getFetch', () => {
    it('should return browser fetch when available', async () => {
      // Mock browser environment with fetch
      global.fetch = vi.fn();
      Object.defineProperty(global, 'window', {
        value: { fetch: global.fetch },
        writable: true
      });

      const { getFetch } = await import('../../../src/utils/fetch');
      const fetchFn = await getFetch();

      expect(fetchFn).toBe(global.fetch);
    });

    it('should return node-fetch in Node.js environment', async () => {
      // Remove browser fetch
      delete (global as any).fetch;
      Object.defineProperty(global, 'window', {
        value: undefined,
        writable: true
      });

      const { getFetch } = await import('../../../src/utils/fetch');
      const fetchFn = await getFetch();

      expect(fetchFn).toBe(mockNodeFetch);
    });

    it('should cache fetch function on subsequent calls', async () => {
      global.fetch = vi.fn();
      Object.defineProperty(global, 'window', {
        value: { fetch: global.fetch },
        writable: true
      });

      const { getFetch } = await import('../../../src/utils/fetch');
      const fetchFn1 = await getFetch();
      const fetchFn2 = await getFetch();

      expect(fetchFn1).toBe(fetchFn2);
    });
  });

  describe('environmentFetch', () => {
    it('should use browser fetch when available', async () => {
      const mockResponse = { ok: true, json: () => Promise.resolve({}) };
      global.fetch = vi.fn().mockResolvedValue(mockResponse);

      Object.defineProperty(global, 'window', {
        value: { fetch: global.fetch },
        writable: true
      });

      const { environmentFetch } = await import('../../../src/utils/fetch');
      const response = await environmentFetch('https://example.com', {});

      expect(global.fetch).toHaveBeenCalledWith('https://example.com', {});
      expect(response).toBe(mockResponse);
    });

    it('should use node-fetch in Node.js environment', async () => {
      delete (global as any).fetch;
      Object.defineProperty(global, 'window', {
        value: undefined,
        writable: true
      });

      const mockResponse = { ok: true, json: () => Promise.resolve({}) };
      mockNodeFetch.mockResolvedValue(mockResponse);

      const { environmentFetch } = await import('../../../src/utils/fetch');
      const response = await environmentFetch('https://example.com', {});

      expect(mockNodeFetch).toHaveBeenCalledWith('https://example.com', {});
      expect(response).toBe(mockResponse);
    });

    it('should handle fetch errors gracefully', async () => {
      const fetchError = new Error('Network error');
      global.fetch = vi.fn().mockRejectedValue(fetchError);

      Object.defineProperty(global, 'window', {
        value: { fetch: global.fetch },
        writable: true
      });

      const { environmentFetch } = await import('../../../src/utils/fetch');

      await expect(environmentFetch('https://example.com', {})).rejects.toThrow('Network error');
    });
  });

  describe('Request Options Handling', () => {
    it('should pass through request options correctly', async () => {
      global.fetch = vi.fn().mockResolvedValue({ ok: true });
      Object.defineProperty(global, 'window', {
        value: { fetch: global.fetch },
        writable: true
      });

      const { environmentFetch } = await import('../../../src/utils/fetch');

      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ test: 'data' })
      };

      await environmentFetch('https://example.com', requestOptions);

      expect(global.fetch).toHaveBeenCalledWith('https://example.com', requestOptions);
    });

    it('should handle undefined options', async () => {
      global.fetch = vi.fn().mockResolvedValue({ ok: true });
      Object.defineProperty(global, 'window', {
        value: { fetch: global.fetch },
        writable: true
      });

      const { environmentFetch } = await import('../../../src/utils/fetch');
      await environmentFetch('https://example.com');

      expect(global.fetch).toHaveBeenCalledWith('https://example.com', undefined);
    });
  });

  describe('Error Handling', () => {
    it('should handle fetch initialization errors', async () => {
      delete (global as any).fetch;
      Object.defineProperty(global, 'window', {
        value: undefined,
        writable: true
      });

      // Mock node-fetch import failure
      vi.doMock('node-fetch', () => {
        throw new Error('node-fetch not available');
      });

      const { getFetch } = await import('../../../src/utils/fetch');

      await expect(getFetch()).rejects.toThrow();
    });

    it('should handle network timeouts', async () => {
      const timeoutError = new Error('Request timeout');
      timeoutError.name = 'AbortError';

      global.fetch = vi.fn().mockRejectedValue(timeoutError);
      Object.defineProperty(global, 'window', {
        value: { fetch: global.fetch },
        writable: true
      });

      const { environmentFetch } = await import('../../../src/utils/fetch');

      await expect(environmentFetch('https://example.com', {})).rejects.toThrow('Request timeout');
    });
  });

  describe('Response Type Compatibility', () => {
    it('should return compatible response object', async () => {
      const mockResponse = {
        ok: true,
        status: 200,
        statusText: 'OK',
        headers: new Map([['content-type', 'application/json']]),
        json: vi.fn().mockResolvedValue({ data: 'test' }),
        text: vi.fn().mockResolvedValue('test'),
        blob: vi.fn().mockResolvedValue(new Blob())
      };

      global.fetch = vi.fn().mockResolvedValue(mockResponse);
      Object.defineProperty(global, 'window', {
        value: { fetch: global.fetch },
        writable: true
      });

      const { environmentFetch } = await import('../../../src/utils/fetch');
      const response = await environmentFetch('https://example.com', {});

      expect(response.ok).toBe(true);
      expect(response.status).toBe(200);
      expect(response.statusText).toBe('OK');
      expect(typeof response.json).toBe('function');
      expect(typeof response.text).toBe('function');
    });
  });
});

