        const originalError = { message: 'Original error' };

        const interceptor1 = vi.fn((error) => ({
          ...error,
          field1: 'first'
        }));

        const interceptor2 = vi.fn((error) => ({
          ...error,
          field2: 'second'
        }));

        interceptorManager.addErrorInterceptor(interceptor1);
        interceptorManager.addErrorInterceptor(interceptor2);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        const processedError = await interceptorManager.processError(originalError, originalRequest);

        expect(processedError.field1).toBe('first');
        expect(processedError.field2).toBe('second');
      });

