        await interceptorManager.processError(originalError, originalRequest);
        expect(interceptor).toHaveBeenCalledTimes(1);

        // Remove interceptor
        removeInterceptor();

        // Should not be called after removal
        await interceptorManager.processError(originalError, originalRequest);
