        const interceptor2 = vi.fn((request) => request);

        interceptorManager.addRequestInterceptor(interceptor1);
        interceptorManager.addRequestInterceptor(interceptor2);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET',
          headers: { 'Content-Type': 'application/json' }
        };

        await interceptorManager.processRequest(originalRequest);

        // Both interceptors should have been called
        expect(interceptor1).toHaveBeenCalled();
        expect(interceptor2).toHaveBeenCalled();

        // Second interceptor should receive the result of the first
        const firstCallResult = interceptor1.mock.results[0].value;
        const secondCallArg = interceptor2.mock.calls[0][0];
        expect(secondCallArg).toEqual(firstCallResult);
      });
    });

    describe('Edge Cases', () => {
      it('should handle empty interceptor arrays', async () => {
        const request = { url: 'https://example.com', method: 'GET', headers: {} };
        const response = { ok: true, status: 200 } as any;
        const error = new Error('Test error');

        const processedRequest = await interceptorManager.processRequest(request);
        const processedResponse = await interceptorManager.processResponse(response, request);
        const processedError = await interceptorManager.processError(error, request);

        expect(processedRequest).toBe(request);
        expect(processedResponse).toBe(response);
        expect(processedError).toBe(error);
      });

      it('should handle removing non-existent interceptors', () => {
        const removeFunction = interceptorManager.addRequestInterceptor(() => ({}));

        // Remove once
        removeFunction();

        // Remove again - should not throw
        expect(() => removeFunction()).not.toThrow();
      });

      it('should handle null/undefined values in interceptors', async () => {
        const nullInterceptor = vi.fn().mockReturnValue(null);
        const undefinedInterceptor = vi.fn().mockReturnValue(undefined);

        interceptorManager.addRequestInterceptor(nullInterceptor);
        interceptorManager.addErrorInterceptor(undefinedInterceptor);

        const request = { url: 'https://example.com', method: 'GET', headers: {} };
        const error = new Error('Test error');

        // These should handle null/undefined gracefully
        await expect(interceptorManager.processRequest(request)).resolves.toBeDefined();
        await expect(interceptorManager.processError(error, request)).resolves.toBeDefined();
      });
    });
  });
});

