import { ITokenStorageStrategy, StorageKeys } from '../types';

/**
 * Creates an in-memory storage strategy for tokens.
 * This works in both browser and Node.js environments.
 * @param storageKeys Optional custom storage keys
 */
export const createMemoryStorage = (storageKeys?: StorageKeys): ITokenStorageStrategy => {
  const memoryStore: Record<string, string> = {};
  const keys = storageKeys || {
    accessToken: 'jwt_access_token',
    refreshToken: 'jwt_refresh_token',
    userData: 'jwt_user_data'
  };

  return {
    getItem: async (key: string) => memoryStore[key] || null,
    setItem: async (key: string, value: string) => { memoryStore[key] = value; },
    removeItem: async (key: string) => { delete memoryStore[key]; },
    clearAll: async () => {
      delete memoryStore[keys.accessToken];
      delete memoryStore[keys.refreshToken];
      delete memoryStore[keys.userData];
    }
  };
};

