import { ITokenStorageStrategy, StorageKeys } from '../types';
import { isBrowser } from '../utils/environment';

/**
 * Creates a browser storage strategy using either localStorage or sessionStorage.
 * @param type The type of browser storage to use ('localStorage' or 'sessionStorage')
 * @param storageKeys Optional custom storage keys
 * @throws Error if running in a non-browser environment
 */
export const createBrowserStorage = (
  type: 'localStorage' | 'sessionStorage',
  storageKeys?: StorageKeys
): ITokenStorageStrategy => {
  if (!isBrowser) {
    throw new Error(`${type} is not available in this environment`);
  }

  const storage = type === 'localStorage' ? window.localStorage : window.sessionStorage;
  const keys = storageKeys || {
    accessToken: 'jwt_access_token',
    refreshToken: 'jwt_refresh_token',
    userData: 'jwt_user_data'
  };

  return {
    getItem: async (key: string) => storage.getItem(key),
    setItem: async (key: string, value: string) => storage.setItem(key, value),
    removeItem: async (key: string) => storage.removeItem(key),
    clearAll: async () => {
      storage.removeItem(keys.accessToken);
      storage.removeItem(keys.refreshToken);
      storage.removeItem(keys.userData);
    }
  };
};

