      }));

      // Re-import the function to get the mocked environment
      const { createBrowserStorage: mockedCreateBrowserStorage } = await import('../../../src/storage/browser');

      expect(() => mockedCreateBrowserStorage('localStorage')).toThrow('localStorage is not available in this environment');
    });

    it('should handle storage throwing on access', async () => {
