vi.mock('../../../src/utils/environment', () => ({
  isBrowser: false // Default to Node.js environment
}));

describe('Storage Initialization', () => {
  describe('String Strategy Initialization', () => {
    it('should initialize memory strategy', () => {
      const storage = initializeTokenStorage('memory');
      expect(storage).toBeDefined();
      expect(typeof storage.getItem).toBe('function');
      expect(typeof storage.setItem).toBe('function');
      expect(typeof storage.removeItem).toBe('function');
      expect(typeof storage.clearAll).toBe('function');
    });

    it('should default to memory strategy for invalid string', () => {
      const storage = initializeTokenStorage('invalid' as any);
      expect(storage).toBeDefined();
    });

    it('should have working storage interface', async () => {
      const storage = initializeTokenStorage('memory');

      await storage.setItem('test', 'value');
      const value = await storage.getItem('test');
      expect(value).toBe('value');

      await storage.removeItem('test');
      const removedValue = await storage.getItem('test');
      expect(removedValue).toBeNull();
    });
  });

  describe('Custom Strategy Initialization', () => {
    it('should use custom storage strategy', () => {
      const customStorage = {
        getItem: vi.fn(),
        setItem: vi.fn(),
        removeItem: vi.fn(),
        clearAll: vi.fn()
      };

      const storage = initializeTokenStorage(customStorage);
      expect(storage).toBe(customStorage);
    });
  });

  describe('Default Strategy Selection', () => {
    it('should default to memory in Node.js environment', () => {
      const storage = initializeTokenStorage();
      expect(storage).toBeDefined();
      // In our mocked environment, isBrowser is false, so it should use memory
    });
  });

  describe('Custom Storage Keys', () => {
    it('should pass custom storage keys to storage adapters', () => {
      const customKeys = {
        accessToken: 'custom_access_token',
        refreshToken: 'custom_refresh_token',
        userData: 'custom_user_data'
      };

      const storage = initializeTokenStorage('memory', customKeys);
      expect(storage).toBeDefined();
    });

    it('should work with localStorage and custom keys', () => {
      const customKeys = {
        accessToken: 'custom_access_token',
        refreshToken: 'custom_refresh_token',
        userData: 'custom_user_data'
      };

      const storage = initializeTokenStorage('localStorage', customKeys);
      expect(storage).toBeDefined();
    });

    it('should work with sessionStorage and custom keys', () => {
      const customKeys = {
        accessToken: 'custom_access_token',
        refreshToken: 'custom_refresh_token',
        userData: 'custom_user_data'
      };

      const storage = initializeTokenStorage('sessionStorage', customKeys);
      expect(storage).toBeDefined();
    });
  });

  describe('Interface Compliance', () => {
    it('should return storage with correct interface for memory', async () => {
      const storage = initializeTokenStorage('memory');

      // Test all required methods exist and are callable
      await storage.setItem('test', 'value');
      const value = await storage.getItem('test');
      await storage.removeItem('test');
      await storage.clearAll();

      expect(value).toBe('value');
    });

    it('should return storage with correct interface for localStorage', async () => {
      const storage = initializeTokenStorage('localStorage');

      // Test all required methods exist
      expect(typeof storage.getItem).toBe('function');
      expect(typeof storage.setItem).toBe('function');
      expect(typeof storage.removeItem).toBe('function');
      expect(typeof storage.clearAll).toBe('function');
    });

    it('should return storage with correct interface for sessionStorage', async () => {
      const storage = initializeTokenStorage('sessionStorage');

      // Test all required methods exist
      expect(typeof storage.getItem).toBe('function');
      expect(typeof storage.setItem).toBe('function');
      expect(typeof storage.removeItem).toBe('function');
      expect(typeof storage.clearAll).toBe('function');
    });
  });
});

