import { describe, it, expect, beforeEach, vi, beforeAll, afterEach, afterAll } from 'vitest';
import { JWTPro } from '../../src/JWTPro';
import { server } from '../mocks/server';

beforeAll(() => server.listen());
afterEach(() => server.resetHandlers());
afterAll(() => server.close());

describe('Storage Integration Tests', () => {
  const baseUrl = 'https://test-wordpress.com';

  describe('Memory Storage Integration', () => {
    let client: JWTPro;

    beforeEach(() => {
      client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });
    });

    it('should persist tokens across method calls within same instance', async () => {
      // Login to store tokens
      await client.login('testuser', 'testpass');

      // Verify tokens are accessible
      const token = await client.token();
      const refreshToken = await client.getRefreshToken();
      const userData = await client.user();

      expect(token).toBe('test-access-token');
      expect(refreshToken).toBe('test-refresh-token');
      expect(userData).toEqual({
        id: 1,
        email: 'test@example.com',
        nicename: 'testuser',
        displayName: 'Test User'
      });
    });

    it('should clear tokens on logout', async () => {
      await client.login('testuser', 'testpass');
      await client.logout();

      const token = await client.token();
      const refreshToken = await client.getRefreshToken();
      const userData = await client.user();

      expect(token).toBeNull();
      expect(refreshToken).toBeNull();
      expect(userData).toBeNull();
    });

    it('should update tokens after refresh', async () => {
      await client.login('testuser', 'testpass');
      await client.refresh();

      const token = await client.token();
      const refreshToken = await client.getRefreshToken();

      expect(token).toBe('new-access-token');
      expect(refreshToken).toBe('new-refresh-token');
    });

    it('should maintain separate storage for different instances', async () => {
      const client1 = new JWTPro({ baseUrl, tokenStorageStrategy: 'memory' });
      const client2 = new JWTPro({ baseUrl, tokenStorageStrategy: 'memory' });

      await client1.login('testuser', 'testpass');

      const client1Token = await client1.token();
      const client2Token = await client2.token();

      expect(client1Token).toBe('test-access-token');
      expect(client2Token).toBeNull();
    });
  });

  describe('Browser Storage Integration', () => {
    beforeEach(() => {
      vi.clearAllMocks();
    });

    describe('localStorage', () => {
      let client: JWTPro;

      beforeEach(() => {
        client = new JWTPro({
          baseUrl,
          tokenStorageStrategy: 'localStorage'
        });
      });

      it('should interact with localStorage correctly', async () => {
        (window.localStorage.getItem as any)
          .mockReturnValueOnce(null) // Initial load
          .mockReturnValueOnce('test-access-token')
          .mockReturnValueOnce('test-refresh-token')
          .mockReturnValueOnce(JSON.stringify({
            id: 1,
            email: 'test@example.com',
            nicename: 'testuser',
            displayName: 'Test User'
          }));

        await client.login('testuser', 'testpass');

        // Should store tokens in localStorage
        expect(window.localStorage.setItem).toHaveBeenCalledWith('jwt_access_token', 'test-access-token');
        expect(window.localStorage.setItem).toHaveBeenCalledWith('jwt_refresh_token', 'test-refresh-token');
        expect(window.localStorage.setItem).toHaveBeenCalledWith('jwt_user_data', JSON.stringify({
          id: 1,
          email: 'test@example.com',
          nicename: 'testuser',
          displayName: 'Test User'
        }));
      });

      it('should load tokens from localStorage on subsequent calls', async () => {
        (window.localStorage.getItem as any)
          .mockReturnValueOnce('existing-token')
          .mockReturnValueOnce('existing-refresh-token')
          .mockReturnValueOnce(JSON.stringify({
            id: 2,
            email: 'existing@example.com',
            nicename: 'existing',
            displayName: 'Existing User'
          }));

        const token = await client.token();
        const refreshToken = await client.getRefreshToken();
        const userData = await client.user();

        expect(window.localStorage.getItem).toHaveBeenCalledWith('jwt_access_token');
        expect(window.localStorage.getItem).toHaveBeenCalledWith('jwt_refresh_token');
        expect(window.localStorage.getItem).toHaveBeenCalledWith('jwt_user_data');

        expect(token).toBe('existing-token');
        expect(refreshToken).toBe('existing-refresh-token');
        expect(userData).toEqual({
          id: 2,
          email: 'existing@example.com',
          nicename: 'existing',
          displayName: 'Existing User'
        });
      });

      it('should clear localStorage on logout', async () => {
        await client.logout();

        expect(window.localStorage.clear).toHaveBeenCalled();
      });
    });

    describe('sessionStorage', () => {
      let client: JWTPro;

      beforeEach(() => {
        client = new JWTPro({
          baseUrl,
          tokenStorageStrategy: 'sessionStorage'
        });
      });

      it('should interact with sessionStorage correctly', async () => {
        await client.login('testuser', 'testpass');

        expect(window.sessionStorage.setItem).toHaveBeenCalledWith('jwt_access_token', 'test-access-token');
        expect(window.sessionStorage.setItem).toHaveBeenCalledWith('jwt_refresh_token', 'test-refresh-token');
      });

      it('should clear sessionStorage on logout', async () => {
        await client.logout();

        expect(window.sessionStorage.clear).toHaveBeenCalled();
      });
    });
  });

  describe('Custom Storage Keys Integration', () => {
    it('should use custom storage keys with memory storage', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory',
        storageKeys: {
          accessToken: 'custom_access_token',
          refreshToken: 'custom_refresh_token',
          userData: 'custom_user_data'
        }
      });

      await client.login('testuser', 'testpass');

      const token = await client.token();
      expect(token).toBe('test-access-token');
    });

    it('should use custom storage keys with localStorage', async () => {
      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage',
        storageKeys: {
          accessToken: 'custom_access_token',
          refreshToken: 'custom_refresh_token',
          userData: 'custom_user_data'
        }
      });

      await client.login('testuser', 'testpass');

      expect(window.localStorage.setItem).toHaveBeenCalledWith('custom_access_token', 'test-access-token');
      expect(window.localStorage.setItem).toHaveBeenCalledWith('custom_refresh_token', 'test-refresh-token');
    });
  });

  describe('Storage Error Handling', () => {
    it('should handle storage errors gracefully', async () => {
      (window.localStorage.setItem as any).mockImplementation(() => {
        throw new Error('Storage quota exceeded');
      });

      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage'
      });

      await expect(client.login('testuser', 'testpass')).rejects.toThrow('Failed to save authentication tokens');
    });

    it('should handle corrupted user data in storage', async () => {
      (window.localStorage.getItem as any)
        .mockReturnValueOnce('test-access-token')
        .mockReturnValueOnce('test-refresh-token')
        .mockReturnValueOnce('invalid-json{');

      const client = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage'
      });

      const userData = await client.user();
      expect(userData).toBeNull();
      expect(window.localStorage.removeItem).toHaveBeenCalledWith('jwt_user_data');
    });
  });

  describe('Storage Strategy Switching', () => {
    it('should allow switching storage strategies between instances', async () => {
      // First client with memory storage
      const memoryClient = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'memory'
      });

      await memoryClient.login('testuser', 'testpass');
      const memoryToken = await memoryClient.token();
      expect(memoryToken).toBe('test-access-token');

      // Second client with localStorage (different storage)
      (window.localStorage.getItem as any).mockReturnValue(null);

      const localStorageClient = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage'
      });

      const localStorageToken = await localStorageClient.token();
      expect(localStorageToken).toBeNull(); // Different storage, no token
    });
  });

  describe('Cross-Instance Storage Sharing', () => {
    it('should share localStorage between different instances', async () => {
      const client1 = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage'
      });

      const client2 = new JWTPro({
        baseUrl,
        tokenStorageStrategy: 'localStorage'
      });

      // Login with first client
      await client1.login('testuser', 'testpass');

      // Mock localStorage to return the stored token
      (window.localStorage.getItem as any)
        .mockReturnValueOnce('test-access-token')
        .mockReturnValueOnce('test-refresh-token')
        .mockReturnValueOnce(JSON.stringify({
          id: 1,
          email: 'test@example.com',
          nicename: 'testuser',
          displayName: 'Test User'
        }));

      // Second client should have access to the same tokens
      const token = await client2.token();
      expect(token).toBe('test-access-token');
    });
  });
});

