import { describe, it, expect, beforeEach, vi, beforeAll, afterEach, afterAll } from 'vitest';
import { JWTPro } from '../../src/JWTPro';
import { server } from '../mocks/server';

beforeAll(() => server.listen());
afterEach(() => server.resetHandlers());
afterAll(() => server.close());

describe('Events Integration Tests', () => {
  const baseUrl = 'https://test-wordpress.com';
  let client: JWTPro;

  beforeEach(() => {
    client = new JWTPro({
      baseUrl,
      tokenStorageStrategy: 'memory'
    });
  });

  describe('Authentication Event Flow', () => {
    it('should emit events in correct order during login flow', async () => {
      const events: string[] = [];

      client.on('login', () => events.push('login'));
      client.on('error', () => events.push('error'));

      await client.login('testuser', 'testpass');

      expect(events).toEqual(['login']);
    });

    it('should emit error event on failed login', async () => {
      const errorEvents: any[] = [];

      client.on('error', (event) => errorEvents.push(event));

      try {
        await client.login('invalid', 'invalid');
      } catch (error) {
        // Expected to throw
      }

      expect(errorEvents).toHaveLength(1);
      expect(errorEvents[0].type).toBe('error');
      expect(errorEvents[0].error).toBeInstanceOf(Error);
    });

    it('should emit logout event when logging out', async () => {
      const logoutEvents: any[] = [];

      client.on('logout', (event) => logoutEvents.push(event));

      await client.login('testuser', 'testpass');
      await client.logout();

      expect(logoutEvents).toHaveLength(1);
      expect(logoutEvents[0].type).toBe('logout');
    });

    it('should emit tokenRefresh event when refreshing tokens', async () => {
      const refreshEvents: any[] = [];

      client.on('tokenRefresh', (event) => refreshEvents.push(event));

      await client.login('testuser', 'testpass');
      await client.refresh();

      expect(refreshEvents).toHaveLength(1);
      expect(refreshEvents[0].type).toBe('tokenRefresh');
      expect(refreshEvents[0].data.userData).toBeDefined();
    });
  });

  describe('Event Data Integrity', () => {
    it('should provide correct user data in login event', async () => {
      let loginEventData: any = null;

      client.on('login', (event) => {
        loginEventData = event.data;
      });

      await client.login('testuser', 'testpass');

      expect(loginEventData).toEqual({
        userData: {
          id: 1,
          email: 'test@example.com',
          nicename: 'testuser',
          displayName: 'Test User'
        }
      });
    });

    it('should provide error details in error events', async () => {
      let errorEventData: any = null;

      client.on('error', (event) => {
        errorEventData = event;
      });

      try {
        await client.login('invalid', 'invalid');
      } catch (error) {
        // Expected to throw
      }

      expect(errorEventData.error).toBeInstanceOf(Error);
      expect(errorEventData.data).toBeDefined();
      expect(errorEventData.data.status).toBe(401);
    });

    it('should provide updated user data in tokenRefresh event', async () => {
      let refreshEventData: any = null;

      client.on('tokenRefresh', (event) => {
        refreshEventData = event.data;
      });

      await client.login('testuser', 'testpass');
      await client.refresh();

      expect(refreshEventData.userData).toEqual({
        id: 1,
        email: 'test@example.com',
        nicename: 'testuser',
        displayName: 'Test User'
      });
    });
  });

  describe('Multiple Event Listeners', () => {
    it('should call all registered listeners for an event', async () => {
      const listener1 = vi.fn();
      const listener2 = vi.fn();
      const listener3 = vi.fn();

      client.on('login', listener1);
      client.on('login', listener2);
      client.on('login', listener3);

      await client.login('testuser', 'testpass');

      expect(listener1).toHaveBeenCalledTimes(1);
      expect(listener2).toHaveBeenCalledTimes(1);
      expect(listener3).toHaveBeenCalledTimes(1);
    });

    it('should handle listener removal correctly', async () => {
      const listener1 = vi.fn();
      const listener2 = vi.fn();

      const remove1 = client.on('login', listener1);
      client.on('login', listener2);

      await client.login('testuser', 'testpass');
      expect(listener1).toHaveBeenCalledTimes(1);
      expect(listener2).toHaveBeenCalledTimes(1);

      // Reset and remove first listener
      listener1.mockClear();
      listener2.mockClear();
      remove1();

      await client.logout();
      await client.login('testuser', 'testpass');

      expect(listener1).not.toHaveBeenCalled();
      expect(listener2).toHaveBeenCalledTimes(1);
    });
  });

  describe('Event Listener Error Handling', () => {
    it('should continue executing other listeners if one throws', async () => {
      const workingListener = vi.fn();
      const errorListener = vi.fn(() => {
        throw new Error('Listener error');
      });

      client.on('login', errorListener);
      client.on('login', workingListener);

      // Should not throw even though listener throws
      await expect(client.login('testuser', 'testpass')).resolves.toBeDefined();

      expect(workingListener).toHaveBeenCalled();
    });
  });

  describe('Event Timing', () => {
    it('should emit login event after tokens are stored', async () => {
      let tokenAtEventTime: string | null = null;

      client.on('login', async () => {
        tokenAtEventTime = await client.token();
      });

      await client.login('testuser', 'testpass');

      expect(tokenAtEventTime).toBe('test-access-token');
    });

    it('should emit logout event after tokens are cleared', async () => {
      let tokenAtEventTime: string | null = 'not-null';

      client.on('logout', async () => {
        tokenAtEventTime = await client.token();
      });

      await client.login('testuser', 'testpass');
      await client.logout();

      expect(tokenAtEventTime).toBeNull();
    });

    it('should emit tokenRefresh event after new tokens are stored', async () => {
      let tokenAtEventTime: string | null = null;

      client.on('tokenRefresh', async () => {
        tokenAtEventTime = await client.token();
      });

      await client.login('testuser', 'testpass');
      await client.refresh();

      expect(tokenAtEventTime).toBe('new-access-token');
    });
  });

  describe('Cross-Instance Event Isolation', () => {
    it('should not emit events across different client instances', async () => {
      const client1 = new JWTPro({ baseUrl, tokenStorageStrategy: 'memory' });
      const client2 = new JWTPro({ baseUrl, tokenStorageStrategy: 'memory' });

      const client1Listener = vi.fn();
      const client2Listener = vi.fn();

      client1.on('login', client1Listener);
      client2.on('login', client2Listener);

      await client1.login('testuser', 'testpass');

      expect(client1Listener).toHaveBeenCalledTimes(1);
      expect(client2Listener).not.toHaveBeenCalled();
    });
  });

  describe('Event Cleanup', () => {
    it('should remove all listeners on destroy', async () => {
      const listener = vi.fn();

      client.on('login', listener);
      client.destroy();

      // Manually emit event to test (since destroy should remove listeners)
      client.events.emit({
        type: 'login',
        data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
      });

      expect(listener).not.toHaveBeenCalled();
    });
  });

  describe('API Error Events', () => {
    it('should emit error events for API failures', async () => {
      const errorEvents: any[] = [];

      client.on('error', (event) => errorEvents.push(event));

      try {
        // Try to make API call without authentication
        await client.api('GET', 'wp/v2/posts');
      } catch (error) {
        // Expected to throw
      }

      expect(errorEvents).toHaveLength(1);
      expect(errorEvents[0].type).toBe('error');
    });
  });

  describe('Complex Event Scenarios', () => {
    it('should handle rapid successive authentication operations', async () => {
      const events: string[] = [];

      client.on('login', () => events.push('login'));
      client.on('logout', () => events.push('logout'));
      client.on('tokenRefresh', () => events.push('tokenRefresh'));
      client.on('error', () => events.push('error'));

      // Rapid operations
      await client.login('testuser', 'testpass');
      await client.refresh();
      await client.logout();

      expect(events).toEqual(['login', 'tokenRefresh', 'logout']);
    });

    it('should maintain event order during error scenarios', async () => {
      const events: string[] = [];

      client.on('login', () => events.push('login'));
      client.on('error', () => events.push('error'));

      // Valid login
      await client.login('testuser', 'testpass');

      // Invalid login
      try {
        await client.login('invalid', 'invalid');
      } catch (error) {
        // Expected to throw
      }

      expect(events).toEqual(['login', 'error']);
    });
  });
});

