/**
 * Event types that JWTPro can emit
 */
export type JWTProEventType =
  | 'login'        // Emitted when a login is successful
  | 'logout'       // Emitted when logout is called
  | 'tokenRefresh' // Emitted when a token is refreshed
  | 'error'        // Emitted when an error occurs
  | 'tokenExpiring'; // Emitted when a token is about to expire

/**
 * Event payload interface
 */
export interface JWTProEvent {
  type: JWTProEventType;
  data?: any;
  error?: Error;
  timestamp: number;
}

/**
 * Event listener function type
 */
export type EventListener = (event: JWTProEvent) => void;

/**
 * Simple event emitter for JWTPro
 */
export class JWTProEventEmitter {
  private listeners: Map<JWTProEventType, EventListener[]> = new Map();

  /**
   * Add an event listener for a specific event type
   * @param type The event type to listen for
   * @param listener The function to call when the event occurs
   * @returns A function to remove this specific listener
   */
  on(type: JWTProEventType, listener: EventListener): () => void {
    if (!this.listeners.has(type)) {
      this.listeners.set(type, []);
    }

    const typeListeners = this.listeners.get(type)!;
    typeListeners.push(listener);

    // Return a function to remove this specific listener
    return () => {
      const index = typeListeners.indexOf(listener);
      if (index !== -1) {
        typeListeners.splice(index, 1);
      }
    };
  }

  /**
   * Remove an event listener
   * @param type The event type
   * @param listener The listener to remove
   */
  off(type: JWTProEventType, listener: EventListener): void {
    if (!this.listeners.has(type)) {
      return;
    }

    const typeListeners = this.listeners.get(type)!;
    const index = typeListeners.indexOf(listener);
    if (index !== -1) {
      typeListeners.splice(index, 1);
    }
  }

  /**
   * Emit an event to all listeners of that type
   * @param event The event to emit
   */
  emit(event: Omit<JWTProEvent, 'timestamp'>): void {
    const fullEvent: JWTProEvent = {
      ...event,
      timestamp: Date.now()
    };

    const listeners = this.listeners.get(event.type) || [];
    listeners.forEach(listener => {
      try {
        listener(fullEvent);
      } catch (error) {
        console.error('Error in event listener:', error);
      }
    });
  }

  /**
   * Remove all listeners or all listeners of a specific type
   * @param type Optional event type. If not provided, all listeners will be removed.
   */
  removeAllListeners(type?: JWTProEventType): void {
    if (type) {
      this.listeners.delete(type);
    } else {
      this.listeners.clear();
    }
  }
}

