    describe('Basic Event Operations', () => {
      it('should create instance without errors', () => {
        expect(eventEmitter).toBeInstanceOf(JWTProEventEmitter);
      });

      it('should register and call event listeners', () => {
        const listener = vi.fn();
        const removeListener = eventEmitter.on('login', listener);

        const event = {
          type: 'login' as JWTProEventType,
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        };

        eventEmitter.emit(event);

        expect(listener).toHaveBeenCalledTimes(1);
        const calledEvent = listener.mock.calls[0][0];
        expect(calledEvent.type).toBe('login');
        expect(calledEvent.data).toEqual(event.data);
        expect(calledEvent.timestamp).toBeTypeOf('number');
        expect(typeof removeListener).toBe('function');
      });

      it('should remove event listeners correctly', () => {
        const listener = vi.fn();
        const removeListener = eventEmitter.on('login', listener);

        const event = {
          type: 'login' as JWTProEventType,
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        };

        eventEmitter.emit(event);
        expect(listener).toHaveBeenCalledTimes(1);

        removeListener();
        eventEmitter.emit(event);
        expect(listener).toHaveBeenCalledTimes(1); // Should not be called again
      });

      it('should handle multiple listeners for same event type', () => {
        const listener1 = vi.fn();
        const listener2 = vi.fn();

        eventEmitter.on('login', listener1);
        eventEmitter.on('login', listener2);

        const event = {
          type: 'login' as JWTProEventType,
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        };

        eventEmitter.emit(event);

        expect(listener1).toHaveBeenCalledTimes(1);
        expect(listener2).toHaveBeenCalledTimes(1);
      });

      it('should handle different event types separately', () => {
        const loginListener = vi.fn();
        const logoutListener = vi.fn();

        eventEmitter.on('login', loginListener);
        eventEmitter.on('logout', logoutListener);

        const loginEvent = {
          type: 'login' as JWTProEventType,
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        };

        const logoutEvent = {
          type: 'logout' as JWTProEventType
        };

        eventEmitter.emit(loginEvent);
        expect(loginListener).toHaveBeenCalledTimes(1);
        expect(logoutListener).not.toHaveBeenCalled();

        eventEmitter.emit(logoutEvent);
        expect(logoutListener).toHaveBeenCalledTimes(1);
        expect(loginListener).toHaveBeenCalledTimes(1);
      });
    });

    describe('Event Types', () => {
      it('should handle login events', () => {
        const listener = vi.fn();
        eventEmitter.on('login', listener);

        eventEmitter.emit({
          type: 'login',
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        });

        expect(listener).toHaveBeenCalledTimes(1);
        expect(listener.mock.calls[0][0].type).toBe('login');
      });

      it('should handle logout events', () => {
        const listener = vi.fn();
        eventEmitter.on('logout', listener);

        eventEmitter.emit({ type: 'logout' });

        expect(listener).toHaveBeenCalledTimes(1);
        expect(listener.mock.calls[0][0].type).toBe('logout');
      });

      it('should handle tokenRefresh events', () => {
        const listener = vi.fn();
        eventEmitter.on('tokenRefresh', listener);

        eventEmitter.emit({
          type: 'tokenRefresh',
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        });

        expect(listener).toHaveBeenCalledTimes(1);
        expect(listener.mock.calls[0][0].type).toBe('tokenRefresh');
      });

      it('should handle error events', () => {
        const listener = vi.fn();
        eventEmitter.on('error', listener);

        eventEmitter.emit({
          type: 'error',
          error: new Error('Test error'),
          data: { status: 401, message: 'Unauthorized' }
        });

        expect(listener).toHaveBeenCalledTimes(1);
        expect(listener.mock.calls[0][0].type).toBe('error');
        expect(listener.mock.calls[0][0].error).toBeInstanceOf(Error);
      });
    });

    describe('Listener Management', () => {
      it('should return different remove functions for different listeners', () => {
        const listener1 = vi.fn();
        const listener2 = vi.fn();

        const remove1 = eventEmitter.on('login', listener1);
        const remove2 = eventEmitter.on('login', listener2);

        expect(remove1).not.toBe(remove2);

        const event = {
          type: 'login' as JWTProEventType,
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        };

        // Both should be called initially
        eventEmitter.emit(event);
        expect(listener1).toHaveBeenCalledTimes(1);
        expect(listener2).toHaveBeenCalledTimes(1);

        // Remove first listener
        remove1();
        eventEmitter.emit(event);
        expect(listener1).toHaveBeenCalledTimes(1); // Still 1
        expect(listener2).toHaveBeenCalledTimes(2); // Now 2
      });

      it('should handle removing non-existent listeners gracefully', () => {
        const listener = vi.fn();
        const removeListener = eventEmitter.on('login', listener);

        // Remove twice
        removeListener();
        expect(() => removeListener()).not.toThrow();
      });

      it('should remove all listeners', () => {
        const listener1 = vi.fn();
        const listener2 = vi.fn();
        const listener3 = vi.fn();

        eventEmitter.on('login', listener1);
        eventEmitter.on('logout', listener2);
        eventEmitter.on('error', listener3);

        eventEmitter.removeAllListeners();

        // Emit events and verify no listeners are called
        eventEmitter.emit({ type: 'login', data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } } });
        eventEmitter.emit({ type: 'logout' });
        eventEmitter.emit({ type: 'error', error: new Error('test') });

        expect(listener1).not.toHaveBeenCalled();
        expect(listener2).not.toHaveBeenCalled();
        expect(listener3).not.toHaveBeenCalled();
      });
    });

    describe('Error Handling', () => {
      it('should handle listener errors gracefully', () => {
        const errorListener = vi.fn(() => {
          throw new Error('Listener error');
        });
        const normalListener = vi.fn();

        eventEmitter.on('login', errorListener);
        eventEmitter.on('login', normalListener);

        const event = {
          type: 'login' as JWTProEventType,
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        };

        // Should not throw even if listener throws
        expect(() => eventEmitter.emit(event)).not.toThrow();

        // Normal listener should still be called
        expect(normalListener).toHaveBeenCalledTimes(1);
      });

      it('should handle emitting events with no listeners', () => {
        const event = {
          type: 'login' as JWTProEventType,
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        };

        expect(() => eventEmitter.emit(event)).not.toThrow();
      });
    });

    describe('Event Data Integrity', () => {
      it('should add timestamp to events', () => {
        const listener = vi.fn();
        eventEmitter.on('login', listener);

        const beforeTime = Date.now();
        eventEmitter.emit({
          type: 'login',
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        });
        const afterTime = Date.now();

        expect(listener).toHaveBeenCalledTimes(1);
        const calledEvent = listener.mock.calls[0][0];
        expect(calledEvent.timestamp).toBeGreaterThanOrEqual(beforeTime);
        expect(calledEvent.timestamp).toBeLessThanOrEqual(afterTime);
      });

      it('should preserve event data without modification', () => {
        const listener = vi.fn();
        eventEmitter.on('login', listener);

        const userData = { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' };
        const event = {
          type: 'login' as JWTProEventType,
          data: { userData }
        };

        eventEmitter.emit(event);

        expect(listener).toHaveBeenCalledTimes(1);
        const calledEvent = listener.mock.calls[0][0];
        expect(calledEvent.data.userData).toEqual(userData);
        expect(calledEvent.type).toBe('login');
      });

      it('should handle events without data', () => {
        const listener = vi.fn();
        eventEmitter.on('logout', listener);

        eventEmitter.emit({ type: 'logout' });

        expect(listener).toHaveBeenCalledTimes(1);
        const calledEvent = listener.mock.calls[0][0];
        expect(calledEvent.type).toBe('logout');
        expect(calledEvent.data).toBeUndefined();
      });
    });

    describe('Memory Management', () => {
      it('should not leak memory when adding and removing many listeners', () => {
        const listeners: (() => void)[] = [];

        // Add many listeners
        for (let i = 0; i < 100; i++) {
          const listener = vi.fn();
          const remove = eventEmitter.on('login', listener);
          listeners.push(remove);
        }

        // Remove all listeners
        listeners.forEach(remove => remove());

        // Should be able to emit without issues
        expect(() => eventEmitter.emit({
          type: 'login',
          data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
        })).not.toThrow();
      });
    });
  });
});

