/**
 * Request interceptor function type
 */
export type RequestInterceptor = (request: RequestInit & { url: string }) => RequestInit & { url: string };

/**
 * Response interceptor function type
 */
export type ResponseInterceptor = (response: Response, request: RequestInit & { url: string }) => Promise<Response> | Response;

/**
 * Error interceptor function type
 */
export type ErrorInterceptor = (error: any, request: RequestInit & { url: string }) => Promise<any> | any;

/**
 * Interceptors manager class
 */
export class InterceptorManager {
  private requestInterceptors: RequestInterceptor[] = [];
  private responseInterceptors: ResponseInterceptor[] = [];
  private errorInterceptors: ErrorInterceptor[] = [];

  /**
   * Add a request interceptor
   * @param interceptor Function to modify the request
   * @returns A function to remove this interceptor
   */
  addRequestInterceptor(interceptor: RequestInterceptor): () => void {
    this.requestInterceptors.push(interceptor);
    return () => {
      const index = this.requestInterceptors.indexOf(interceptor);
      if (index !== -1) {
        this.requestInterceptors.splice(index, 1);
      }
    };
  }

  /**
   * Add a response interceptor
   * @param interceptor Function to process the response
   * @returns A function to remove this interceptor
   */
  addResponseInterceptor(interceptor: ResponseInterceptor): () => void {
    this.responseInterceptors.push(interceptor);
    return () => {
      const index = this.responseInterceptors.indexOf(interceptor);
      if (index !== -1) {
        this.responseInterceptors.splice(index, 1);
      }
    };
  }

  /**
   * Add an error interceptor
   * @param interceptor Function to process or transform errors
   * @returns A function to remove this interceptor
   */
  addErrorInterceptor(interceptor: ErrorInterceptor): () => void {
    this.errorInterceptors.push(interceptor);
    return () => {
      const index = this.errorInterceptors.indexOf(interceptor);
      if (index !== -1) {
        this.errorInterceptors.splice(index, 1);
      }
    };
  }

  /**
   * Apply all request interceptors to a request
   * @param request The original request
   * @returns The modified request
   */
  async processRequest(request: RequestInit & { url: string }): Promise<RequestInit & { url: string }> {
    let processedRequest = { ...request };

    for (const interceptor of this.requestInterceptors) {
      processedRequest = interceptor(processedRequest);
    }

    return processedRequest;
  }

  /**
   * Apply all response interceptors to a response
   * @param response The original response
   * @param request The request that generated this response
   * @returns The processed response
   */
  async processResponse(response: Response, request: RequestInit & { url: string }): Promise<Response> {
    let processedResponse = response;

    for (const interceptor of this.responseInterceptors) {
      processedResponse = await interceptor(processedResponse, request);
    }

    return processedResponse;
  }

  /**
   * Apply all error interceptors to an error
   * @param error The original error
   * @param request The request that generated this error
   * @returns The processed error
   */
  async processError(error: any, request: RequestInit & { url: string }): Promise<any> {
    let processedError = error;

    for (const interceptor of this.errorInterceptors) {
      try {
        processedError = await interceptor(processedError, request);
      } catch (innerError) {
        console.error('Error in error interceptor:', innerError);
      }
    }

    return processedError;
  }
}

