import { describe, it, expect, vi, beforeEach } from 'vitest';
import { InterceptorManager } from '../../../src/utils/interceptors';

describe('Interceptor System', () => {
  let interceptorManager: InterceptorManager;

  beforeEach(() => {
    interceptorManager = new InterceptorManager();
  });

  describe('InterceptorManager', () => {
    describe('Request Interceptors', () => {
      it('should add and execute request interceptors', async () => {
        const interceptor = vi.fn((request) => ({
          ...request,
          headers: { ...request.headers, 'X-Custom': 'test' }
        }));

        interceptorManager.addRequestInterceptor(interceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET',
          headers: { 'Content-Type': 'application/json' }
        };

        const processedRequest = await interceptorManager.processRequest(originalRequest);

        expect(interceptor).toHaveBeenCalledWith(originalRequest);
        expect(processedRequest.headers).toEqual({
          'Content-Type': 'application/json',
          'X-Custom': 'test'
        });
      });

      it('should execute multiple request interceptors in order', async () => {
        const interceptor1 = vi.fn((request) => ({
          ...request,
          headers: { ...request.headers, 'X-First': 'first' }
        }));

        const interceptor2 = vi.fn((request) => ({
          ...request,
          headers: { ...request.headers, 'X-Second': 'second' }
        }));

        interceptorManager.addRequestInterceptor(interceptor1);
        interceptorManager.addRequestInterceptor(interceptor2);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET',
          headers: {}
        };

        const processedRequest = await interceptorManager.processRequest(originalRequest);

        expect(interceptor1).toHaveBeenCalledWith(originalRequest);
        expect(interceptor2).toHaveBeenCalled();
        expect(processedRequest.headers).toEqual({
          'X-First': 'first',
          'X-Second': 'second'
        });
      });

      it('should remove request interceptors correctly', async () => {
        const interceptor = vi.fn((request) => ({
          ...request,
          headers: { ...request.headers, 'X-Custom': 'test' }
        }));

        const removeInterceptor = interceptorManager.addRequestInterceptor(interceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET',
          headers: {}
        };

        // Should be called before removal
        await interceptorManager.processRequest(originalRequest);
        expect(interceptor).toHaveBeenCalledTimes(1);

        // Remove interceptor
        removeInterceptor();

        // Should not be called after removal
        await interceptorManager.processRequest(originalRequest);
        expect(interceptor).toHaveBeenCalledTimes(1);
      });

      it('should handle request interceptor errors gracefully', async () => {
        const errorInterceptor = vi.fn(() => {
          throw new Error('Interceptor error');
        });

        interceptorManager.addRequestInterceptor(errorInterceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET',
          headers: {}
        };

        await expect(interceptorManager.processRequest(originalRequest)).rejects.toThrow('Interceptor error');
      });
    });

    describe('Response Interceptors', () => {
      it('should add and execute response interceptors', async () => {
        const mockResponse = {
          ok: true,
          status: 200,
          json: vi.fn().mockResolvedValue({ data: 'test' })
        } as any;

        const interceptor = vi.fn().mockReturnValue({
          ...mockResponse,
          status: 201
        });

        interceptorManager.addResponseInterceptor(interceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        const processedResponse = await interceptorManager.processResponse(mockResponse, originalRequest);

        expect(interceptor).toHaveBeenCalledWith(mockResponse, originalRequest);
        expect(processedResponse.status).toBe(201);
      });

      it('should execute multiple response interceptors in order', async () => {
        const mockResponse = {
          ok: true,
          status: 200,
          headers: new Map()
        } as any;

        const interceptor1 = vi.fn((response) => ({
          ...response,
          customField1: 'first'
        }));

        const interceptor2 = vi.fn((response) => ({
          ...response,
          customField2: 'second'
        }));

        interceptorManager.addResponseInterceptor(interceptor1);
        interceptorManager.addResponseInterceptor(interceptor2);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        const processedResponse = await interceptorManager.processResponse(mockResponse, originalRequest);

        expect(processedResponse.customField1).toBe('first');
        expect(processedResponse.customField2).toBe('second');
      });

      it('should handle async response interceptors', async () => {
        const mockResponse = {
          ok: true,
          status: 200
        } as any;

        const asyncInterceptor = vi.fn().mockResolvedValue({
          ...mockResponse,
          async: true
        });

        interceptorManager.addResponseInterceptor(asyncInterceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        const processedResponse = await interceptorManager.processResponse(mockResponse, originalRequest);

        expect(processedResponse.async).toBe(true);
      });

      it('should remove response interceptors correctly', async () => {
        const mockResponse = {
          ok: true,
          status: 200
        } as any;

        const interceptor = vi.fn().mockReturnValue(mockResponse);

        const removeInterceptor = interceptorManager.addResponseInterceptor(interceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        // Should be called before removal
        await interceptorManager.processResponse(mockResponse, originalRequest);
        expect(interceptor).toHaveBeenCalledTimes(1);

        // Remove interceptor
        removeInterceptor();

        // Should not be called after removal
        await interceptorManager.processResponse(mockResponse, originalRequest);
        expect(interceptor).toHaveBeenCalledTimes(1);
      });

      it('should handle response interceptor errors gracefully', async () => {
        const mockResponse = {
          ok: true,
          status: 200
        } as any;

        const errorInterceptor = vi.fn(() => {
          throw new Error('Response interceptor error');
        });

        interceptorManager.addResponseInterceptor(errorInterceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        await expect(interceptorManager.processResponse(mockResponse, originalRequest)).rejects.toThrow('Response interceptor error');
      });
    });

    describe('Error Interceptors', () => {
      it('should add and execute error interceptors', async () => {
        const originalError = new Error('Original error');
        const modifiedError = new Error('Modified error');

        const interceptor = vi.fn().mockReturnValue(modifiedError);

        interceptorManager.addErrorInterceptor(interceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        const processedError = await interceptorManager.processError(originalError, originalRequest);

        expect(interceptor).toHaveBeenCalledWith(originalError, originalRequest);
        expect(processedError).toBe(modifiedError);
      });

      it('should execute multiple error interceptors in order', async () => {
        const originalError = { message: 'Original error' };

        const interceptor1 = vi.fn((error) => ({
          ...error,
          field1: 'first'
        }));

        const interceptor2 = vi.fn((error) => ({
          ...error,
          field2: 'second'
        }));

        interceptorManager.addErrorInterceptor(interceptor1);
        interceptorManager.addErrorInterceptor(interceptor2);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        const processedError = await interceptorManager.processError(originalError, originalRequest);

        expect(processedError.field1).toBe('first');
        expect(processedError.field2).toBe('second');
      });

      it('should handle async error interceptors', async () => {
        const originalError = new Error('Original error');
        const modifiedError = new Error('Async modified error');

        const asyncInterceptor = vi.fn().mockResolvedValue(modifiedError);

        interceptorManager.addErrorInterceptor(asyncInterceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        const processedError = await interceptorManager.processError(originalError, originalRequest);

        expect(processedError).toBe(modifiedError);
      });

      it('should remove error interceptors correctly', async () => {
        const originalError = new Error('Original error');

        const interceptor = vi.fn().mockReturnValue(originalError);

        const removeInterceptor = interceptorManager.addErrorInterceptor(interceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        // Should be called before removal
        await interceptorManager.processError(originalError, originalRequest);
        expect(interceptor).toHaveBeenCalledTimes(1);

        // Remove interceptor
        removeInterceptor();

        // Should not be called after removal
        await interceptorManager.processError(originalError, originalRequest);
        expect(interceptor).toHaveBeenCalledTimes(1);
      });

      it('should handle error interceptor errors gracefully', async () => {
        const originalError = new Error('Original error');

        const errorInterceptor = vi.fn(() => {
          throw new Error('Error interceptor error');
        });

        interceptorManager.addErrorInterceptor(errorInterceptor);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET'
        };

        await expect(interceptorManager.processError(originalError, originalRequest)).rejects.toThrow('Error interceptor error');
      });
    });

    describe('Complex Scenarios', () => {
      it('should handle mixed interceptor types', async () => {
        const requestInterceptor = vi.fn((request) => ({
          ...request,
          headers: { ...request.headers, 'X-Request': 'modified' }
        }));

        const responseInterceptor = vi.fn((response) => ({
          ...response,
          modified: true
        }));

        const errorInterceptor = vi.fn((error) => ({
          ...error,
          handled: true
        }));

        interceptorManager.addRequestInterceptor(requestInterceptor);
        interceptorManager.addResponseInterceptor(responseInterceptor);
        interceptorManager.addErrorInterceptor(errorInterceptor);

        // Test request processing
        const request = { url: 'https://example.com', method: 'GET', headers: {} };
        const processedRequest = await interceptorManager.processRequest(request);
        expect(processedRequest.headers['X-Request']).toBe('modified');

        // Test response processing
        const response = { ok: true, status: 200 } as any;
        const processedResponse = await interceptorManager.processResponse(response, request);
        expect(processedResponse.modified).toBe(true);

        // Test error processing
        const error = new Error('Test error');
        const processedError = await interceptorManager.processError(error, request);
        expect(processedError.handled).toBe(true);
      });

      it('should handle interceptors that return promises', async () => {
        const asyncRequestInterceptor = vi.fn().mockResolvedValue({
          url: 'https://example.com',
          method: 'POST',
          headers: { 'X-Async': 'true' }
        });

        const asyncResponseInterceptor = vi.fn().mockResolvedValue({
          ok: true,
          status: 200,
          async: true
        });

        const asyncErrorInterceptor = vi.fn().mockResolvedValue({
          message: 'Async error handling',
          async: true
        });

        interceptorManager.addRequestInterceptor(asyncRequestInterceptor);
        interceptorManager.addResponseInterceptor(asyncResponseInterceptor);
        interceptorManager.addErrorInterceptor(asyncErrorInterceptor);

        const request = { url: 'https://example.com', method: 'GET', headers: {} };
        const response = { ok: true, status: 200 } as any;
        const error = new Error('Test error');

        const [processedRequest, processedResponse, processedError] = await Promise.all([
          interceptorManager.processRequest(request),
          interceptorManager.processResponse(response, request),
          interceptorManager.processError(error, request)
        ]);

        expect(processedRequest.headers['X-Async']).toBe('true');
        expect(processedResponse.async).toBe(true);
        expect(processedError.async).toBe(true);
      });

      it('should maintain request object through the chain', async () => {
        const interceptor1 = vi.fn((request) => request);
        const interceptor2 = vi.fn((request) => request);

        interceptorManager.addRequestInterceptor(interceptor1);
        interceptorManager.addRequestInterceptor(interceptor2);

        const originalRequest = {
          url: 'https://example.com',
          method: 'GET',
          headers: { 'Content-Type': 'application/json' }
        };

        await interceptorManager.processRequest(originalRequest);

        // Both interceptors should have been called
        expect(interceptor1).toHaveBeenCalled();
        expect(interceptor2).toHaveBeenCalled();

        // Second interceptor should receive the result of the first
        const firstCallResult = interceptor1.mock.results[0].value;
        const secondCallArg = interceptor2.mock.calls[0][0];
        expect(secondCallArg).toEqual(firstCallResult);
      });
    });

    describe('Edge Cases', () => {
      it('should handle empty interceptor arrays', async () => {
        const request = { url: 'https://example.com', method: 'GET', headers: {} };
        const response = { ok: true, status: 200 } as any;
        const error = new Error('Test error');

        const processedRequest = await interceptorManager.processRequest(request);
        const processedResponse = await interceptorManager.processResponse(response, request);
        const processedError = await interceptorManager.processError(error, request);

        expect(processedRequest).toBe(request);
        expect(processedResponse).toBe(response);
        expect(processedError).toBe(error);
      });

      it('should handle removing non-existent interceptors', () => {
        const removeFunction = interceptorManager.addRequestInterceptor(() => ({}));

        // Remove once
        removeFunction();

        // Remove again - should not throw
        expect(() => removeFunction()).not.toThrow();
      });

      it('should handle null/undefined values in interceptors', async () => {
        const nullInterceptor = vi.fn().mockReturnValue(null);
        const undefinedInterceptor = vi.fn().mockReturnValue(undefined);

        interceptorManager.addRequestInterceptor(nullInterceptor);
        interceptorManager.addErrorInterceptor(undefinedInterceptor);

        const request = { url: 'https://example.com', method: 'GET', headers: {} };
