import { describe, it, expect, vi, beforeEach } from 'vitest';
import { SilentLogger, ConsoleLogger, createDefaultLogger } from '../../../src/utils/logger';

describe('Logger Utilities', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  describe('SilentLogger', () => {
    let logger: SilentLogger;

    beforeEach(() => {
      logger = new SilentLogger();
    });

    it('should create instance without errors', () => {
      expect(logger).toBeInstanceOf(SilentLogger);
    });

    it('should have all required methods', () => {
      expect(typeof logger.debug).toBe('function');
      expect(typeof logger.info).toBe('function');
      expect(typeof logger.warn).toBe('function');
      expect(typeof logger.error).toBe('function');
    });

    it('should not output anything for debug calls', () => {
      const consoleSpy = vi.spyOn(console, 'debug');
      logger.debug('test message', { data: 'test' });
      expect(consoleSpy).not.toHaveBeenCalled();
    });

    it('should not output anything for info calls', () => {
      const consoleSpy = vi.spyOn(console, 'info');
      logger.info('test message', { data: 'test' });
      expect(consoleSpy).not.toHaveBeenCalled();
    });

    it('should not output anything for warn calls', () => {
      const consoleSpy = vi.spyOn(console, 'warn');
      logger.warn('test message', { data: 'test' });
      expect(consoleSpy).not.toHaveBeenCalled();
    });

    it('should not output anything for error calls', () => {
      const consoleSpy = vi.spyOn(console, 'error');
      logger.error('test message', { data: 'test' });
      expect(consoleSpy).not.toHaveBeenCalled();
    });

    it('should handle multiple arguments without errors', () => {
      expect(() => {
        logger.debug('message', 'arg1', 'arg2', { object: true });
        logger.info('message', 'arg1', 'arg2', { object: true });
        logger.warn('message', 'arg1', 'arg2', { object: true });
        logger.error('message', 'arg1', 'arg2', { object: true });
      }).not.toThrow();
    });
  });

  describe('ConsoleLogger', () => {
    let logger: ConsoleLogger;

    beforeEach(() => {
      logger = new ConsoleLogger();
    });

    it('should create instance without errors', () => {
      expect(logger).toBeInstanceOf(ConsoleLogger);
    });

    it('should have all required methods', () => {
      expect(typeof logger.debug).toBe('function');
      expect(typeof logger.info).toBe('function');
      expect(typeof logger.warn).toBe('function');
      expect(typeof logger.error).toBe('function');
    });

    it('should call console.debug for debug calls', () => {
      const consoleSpy = vi.spyOn(console, 'debug');
      logger.debug('test message', { data: 'test' });
      expect(consoleSpy).toHaveBeenCalledWith('[JWTPro] DEBUG:', 'test message', { data: 'test' });
    });

    it('should call console.info for info calls', () => {
      const consoleSpy = vi.spyOn(console, 'info');
      logger.info('test message', { data: 'test' });
      expect(consoleSpy).toHaveBeenCalledWith('test message', { data: 'test' });
    });

    it('should call console.warn for warn calls', () => {
      const consoleSpy = vi.spyOn(console, 'warn');
      logger.warn('test message', { data: 'test' });
      expect(consoleSpy).toHaveBeenCalledWith('test message', { data: 'test' });
    });

    it('should call console.error for error calls', () => {
      const consoleSpy = vi.spyOn(console, 'error');
      logger.error('test message', { data: 'test' });
      expect(consoleSpy).toHaveBeenCalledWith('test message', { data: 'test' });
    });

    it('should handle multiple arguments correctly', () => {
      const consoleSpy = vi.spyOn(console, 'log');
      logger.info('message', 'arg1', 'arg2', { object: true });
      expect(consoleSpy).toHaveBeenCalledWith('message', 'arg1', 'arg2', { object: true });
    });

    it('should handle error objects correctly', () => {
      const consoleSpy = vi.spyOn(console, 'error');
      const error = new Error('Test error');
      logger.error('Error occurred', error);
      expect(consoleSpy).toHaveBeenCalledWith('Error occurred', error);
    });

    it('should handle undefined and null values', () => {
      const consoleSpy = vi.spyOn(console, 'info');
      logger.info('test', undefined, null);
      expect(consoleSpy).toHaveBeenCalledWith('test', undefined, null);
    });
  });

  describe('createDefaultLogger', () => {
    it('should return ConsoleLogger in development environment', () => {
      // Mock development environment
      const originalEnv = process.env.NODE_ENV;
      process.env.NODE_ENV = 'development';

      const logger = createDefaultLogger();
      expect(logger).toBeInstanceOf(ConsoleLogger);

      process.env.NODE_ENV = originalEnv;
    });

    it('should return ConsoleLogger in test environment', () => {
      const originalEnv = process.env.NODE_ENV;
      process.env.NODE_ENV = 'test';

      const logger = createDefaultLogger();
      expect(logger).toBeInstanceOf(ConsoleLogger);

      process.env.NODE_ENV = originalEnv;
    });

    it('should return SilentLogger in production environment', () => {
      const originalEnv = process.env.NODE_ENV;
      process.env.NODE_ENV = 'production';

      const logger = createDefaultLogger();
      expect(logger).toBeInstanceOf(SilentLogger);

      process.env.NODE_ENV = originalEnv;
    });

    it('should return ConsoleLogger when NODE_ENV is undefined', () => {
      const originalEnv = process.env.NODE_ENV;
      delete process.env.NODE_ENV;

      const logger = createDefaultLogger();
      expect(logger).toBeInstanceOf(ConsoleLogger);

      process.env.NODE_ENV = originalEnv;
    });

    it('should return ConsoleLogger for unknown environments', () => {
      const originalEnv = process.env.NODE_ENV;
      process.env.NODE_ENV = 'staging';

      const logger = createDefaultLogger();
      expect(logger).toBeInstanceOf(ConsoleLogger);

      process.env.NODE_ENV = originalEnv;
    });
  });

  describe('Logger Interface Compliance', () => {
    const loggers = [
      { name: 'SilentLogger', instance: () => new SilentLogger() },
      { name: 'ConsoleLogger', instance: () => new ConsoleLogger() },
      { name: 'DefaultLogger', instance: () => createDefaultLogger() }
    ];

    loggers.forEach(({ name, instance }) => {
      describe(name, () => {
        let logger: any;

        beforeEach(() => {
          logger = instance();
        });

        it('should implement ILogger interface', () => {
          expect(typeof logger.debug).toBe('function');
          expect(typeof logger.info).toBe('function');
          expect(typeof logger.warn).toBe('function');
          expect(typeof logger.error).toBe('function');
        });

        it('should accept variable arguments', () => {
          expect(() => {
            logger.debug();
            logger.debug('message');
            logger.debug('message', 'arg1');
            logger.debug('message', 'arg1', 'arg2', { data: true });
          }).not.toThrow();
        });

        it('should handle edge cases gracefully', () => {
          expect(() => {
            logger.info(null);
            logger.warn(undefined);
            logger.error(0);
            logger.debug(false);
          }).not.toThrow();
        });
      });
    });
  });
});

