// Type-safe fetch implementation
export type FetchType = typeof fetch;
export type FetchResponse = Awaited<ReturnType<FetchType>>;
export type FetchRequestInit = Parameters<FetchType>[1];

/**
 * Type guard to check if headers is a Headers instance
 */
function isHeadersInstance(headers: any): headers is Headers {
  return headers && typeof headers.entries === 'function';
}

/**
 * Safely converts Headers to plain object
 */
function headersToObject(headers: Headers): Record<string, string> {
  const result: Record<string, string> = {};
  headers.forEach((value, key) => {
    result[key] = value;
  });
  return result;
}

/**
 * Gets the appropriate fetch implementation based on the environment.
 * Uses native fetch in browsers and node-fetch in Node.js.
 */
export const getFetch = async (): Promise<FetchType> => {
  if (typeof fetch !== 'undefined') {
    return fetch;
  }
  const { default: nodeFetch } = await import('node-fetch');
  return nodeFetch as unknown as FetchType;
};

/**
 * Environment-aware fetch implementation that handles type compatibility
 * between browser and Node.js environments.
 */
export async function environmentFetch(url: string, options: RequestInit): Promise<FetchResponse> {
  const fetchFn = await getFetch();
  // Convert browser RequestInit to node-fetch RequestInit if needed
  const fetchOptions: FetchRequestInit = {
    ...options,
    body: options.body ? String(options.body) : undefined,
    headers: options.headers ?
      (typeof options.headers === 'object' && !Array.isArray(options.headers) ?
        (isHeadersInstance(options.headers) ?
          headersToObject(options.headers) :
          options.headers) :
        options.headers) :
      undefined
  };
  return fetchFn(url, fetchOptions);
}

