     1‚Üíimport {
     2‚Üí  JWTProConfig,
     3‚Üí  ITokenStorageStrategy,
     4‚Üí  TokenResponse,
     5‚Üí  ValidateApiResponse,
     6‚Üí  RefreshResponse,
     7‚Üí  UserData,
     8‚Üí  ILogger,
     9‚Üí  StorageKeys,
    10‚Üí  RateLimitInfo
    11‚Üí} from './types';
    12‚Üíimport { initializeTokenStorage } from './storage';
    13‚Üíimport { environmentFetch, getFetch, FetchType, FetchResponse, FetchRequestInit } from './utils/fetch';
    14‚Üíimport { isBrowser } from './utils/environment';
    15‚Üíimport { JWTProEventEmitter, JWTProEventType } from './utils/events';
    16‚Üíimport { InterceptorManager } from './utils/interceptors';
    17‚Üíimport { createDefaultLogger } from './utils/logger';
    18‚Üí
    19‚Üí/**
    20‚Üí * JWTPro class for interacting with WordPress JWT Authentication and REST API.
    21‚Üí */
    22‚Üíexport class JWTPro {
    23‚Üí  private readonly config: Required<Omit<JWTProConfig, 'tokenStorageStrategy' | 'storageKeys' | 'logger'>> & {
    24‚Üí    tokenStorageStrategy: ITokenStorageStrategy | 'localStorage' | 'sessionStorage' | 'memory';
    25‚Üí    storageKeys: StorageKeys;
    26‚Üí    logger: ILogger;
    27‚Üí  };
    28‚Üí  private accessToken: string | null = null;
    29‚Üí  private refreshTokenVal: string | null = null;
    30‚Üí  private userData: UserData | null = null;
    31‚Üí  private tokenStorage!: ITokenStorageStrategy;
    32‚Üí  private isTokensLoaded = false;
    33‚Üí  private rateLimitInfo: RateLimitInfo | null = null;
    34‚Üí
    35‚Üí  /**
    36‚Üí   * Event emitter for state changes
    37‚Üí   */
    38‚Üí  public events: JWTProEventEmitter = new JWTProEventEmitter();
    39‚Üí
    40‚Üí  /**
    41‚Üí   * Interceptors for requests and responses
    42‚Üí   */
    43‚Üí  public interceptors: InterceptorManager = new InterceptorManager();
    44‚Üí
    45‚Üí  /**
    46‚Üí   * Creates an instance of JWTPro.
    47‚Üí   * @param {JWTProConfig} config - The configuration options for the client.
    48‚Üí   */
    49‚Üí  constructor(config: JWTProConfig) {
    50‚Üí    const defaultStorageKeys: StorageKeys = {
    51‚Üí      accessToken: 'jwt_access_token',
    52‚Üí      refreshToken: 'jwt_refresh_token',
    53‚Üí      userData: 'jwt_user_data'
    54‚Üí    };
    55‚Üí
    56‚Üí    const mergedStorageKeys: StorageKeys = {
    57‚Üí      accessToken: config.storageKeys?.accessToken || defaultStorageKeys.accessToken,
    58‚Üí      refreshToken: config.storageKeys?.refreshToken || defaultStorageKeys.refreshToken,
    59‚Üí      userData: config.storageKeys?.userData || defaultStorageKeys.userData
    60‚Üí    };
    61‚Üí
    62‚Üí    const { storageKeys: _, logger: __, ...restConfig } = config;
    63‚Üí
    64‚Üí    this.config = {
    65‚Üí      authNamespace: 'jwt-auth/v1',
    66‚Üí      wpApiNamespace: 'wp/v2',
    67‚Üí      tokenStorageStrategy: isBrowser ? 'localStorage' : 'memory',
    68‚Üí      autoRefreshToken: false,
    69‚Üí      refreshThreshold: 300,
    70‚Üí      storageKeys: mergedStorageKeys,
    71‚Üí      logger: config.logger || createDefaultLogger(),
    72‚Üí      ...restConfig,
    73‚Üí    };
    74‚Üí    this.initializeTokenStorage();
    75‚Üí  }
    76‚Üí
    77‚Üí  private initializeTokenStorage(): void {
    78‚Üí    this.tokenStorage = initializeTokenStorage(this.config.tokenStorageStrategy, this.config.storageKeys);
    79‚Üí  }
    80‚Üí
    81‚Üí  private async saveTokensToStorage(accessToken: string, refreshToken: string, userData: UserData): Promise<void> {
    82‚Üí    this.accessToken = accessToken;
    83‚Üí    this.refreshTokenVal = refreshToken;
    84‚Üí    this.userData = userData;
    85‚Üí    this.isTokensLoaded = true;
    86‚Üí
    87‚Üí    try {
    88‚Üí      await this.tokenStorage.setItem(this.config.storageKeys.accessToken, accessToken);
    89‚Üí      await this.tokenStorage.setItem(this.config.storageKeys.refreshToken, refreshToken);
    90‚Üí      await this.tokenStorage.setItem(this.config.storageKeys.userData, JSON.stringify(userData));
    91‚Üí    } catch (error) {
    92‚Üí      this.config.logger.error('Failed to save tokens to storage', error);
    93‚Üí      throw new Error('Failed to save authentication tokens');
    94‚Üí    }
    95‚Üí  }
    96‚Üí
    97‚Üí  private async loadTokensFromStorage(): Promise<void> {
    98‚Üí    if (this.isTokensLoaded) return;
    99‚Üí
   100‚Üí    try {
   101‚Üí      this.accessToken = await this.tokenStorage.getItem(this.config.storageKeys.accessToken);
   102‚Üí      this.refreshTokenVal = await this.tokenStorage.getItem(this.config.storageKeys.refreshToken);
   103‚Üí      const userDataStr = await this.tokenStorage.getItem(this.config.storageKeys.userData);
   104‚Üí
   105‚Üí      if (userDataStr) {
   106‚Üí        try {
   107‚Üí          this.userData = JSON.parse(userDataStr);
   108‚Üí        } catch (e) {
   109‚Üí          this.config.logger.error('Error parsing user data from storage', e);
   110‚Üí          this.userData = null;
   111‚Üí          await this.tokenStorage.removeItem(this.config.storageKeys.userData);
   112‚Üí          this.events.emit({
   113‚Üí            type: 'error',
   114‚Üí            error: new Error('Failed to parse user data from storage')
   115‚Üí          });
   116‚Üí        }
   117‚Üí      }
   118‚Üí
   119‚Üí      this.isTokensLoaded = true;
   120‚Üí    } catch (error) {
   121‚Üí      this.config.logger.error('Failed to load tokens from storage', error);
   122‚Üí      throw new Error('Failed to load authentication tokens');
   123‚Üí    }
   124‚Üí  }
   125‚Üí
   126‚Üí  private async clearTokensFromStorage(): Promise<void> {
   127‚Üí    this.accessToken = null;
   128‚Üí    this.refreshTokenVal = null;
   129‚Üí    this.userData = null;
   130‚Üí    this.isTokensLoaded = false;
   131‚Üí
   132‚Üí    try {
   133‚Üí      if (this.tokenStorage.clearAll) {
   134‚Üí        await this.tokenStorage.clearAll();
   135‚Üí      } else {
   136‚Üí        await this.tokenStorage.removeItem(this.config.storageKeys.accessToken);
   137‚Üí        await this.tokenStorage.removeItem(this.config.storageKeys.refreshToken);
   138‚Üí        await this.tokenStorage.removeItem(this.config.storageKeys.userData);
   139‚Üí      }
   140‚Üí    } catch (error) {
   141‚Üí      this.config.logger.error('Failed to clear tokens from storage', error);
   142‚Üí      throw new Error('Failed to clear authentication tokens');
   143‚Üí    }
   144‚Üí  }
   145‚Üí
   146‚Üí  private parseRateLimitHeaders(response: FetchResponse): RateLimitInfo | null {
   147‚Üí    const limit = response.headers.get('X-RateLimit-Limit');
   148‚Üí    const remaining = response.headers.get('X-RateLimit-Remaining');
   149‚Üí    const reset = response.headers.get('X-RateLimit-Reset');
   150‚Üí    const retryAfter = response.headers.get('Retry-After');
   151‚Üí
   152‚Üí    if (limit && remaining && reset) {
   153‚Üí      return {
   154‚Üí        limit: parseInt(limit, 10),
   155‚Üí        remaining: parseInt(remaining, 10),
   156‚Üí        reset: parseInt(reset, 10),
   157‚Üí        retryAfter: retryAfter ? parseInt(retryAfter, 10) : undefined
   158‚Üí      };
   159‚Üí    }
   160‚Üí
   161‚Üí    return null;
   162‚Üí  }
   163‚Üí
   164‚Üí  private async _fetch(url: string, options: RequestInit): Promise<FetchResponse> {
   165‚Üí    const fetchFn = await getFetch();
   166‚Üí
   167‚Üí    // Prepare the request for interceptors
   168‚Üí    const originalRequest = { ...options, url };
   169‚Üí
   170‚Üí    try {
   171‚Üí      // Apply request interceptors
   172‚Üí      const processedRequest = await this.interceptors.processRequest(originalRequest);
   173‚Üí
   174‚Üí      // Convert browser RequestInit to node-fetch RequestInit if needed
   175‚Üí      const fetchOptions: FetchRequestInit = {
   176‚Üí        ...processedRequest,
   177‚Üí        body: processedRequest.body ? String(processedRequest.body) : undefined,
   178‚Üí        headers: processedRequest.headers
   179‚Üí      };
   180‚Üí
   181‚Üí      // Extract URL from processed request
   182‚Üí      const processedUrl = processedRequest.url;
   183‚Üí
   184‚Üí      // Execute the fetch request
   185‚Üí      const response = await fetchFn(processedUrl, fetchOptions);
   186‚Üí
   187‚Üí      // Parse and store rate limit information
   188‚Üí      this.rateLimitInfo = this.parseRateLimitHeaders(response);
   189‚Üí
   190‚Üí      // Log rate limit information if available
   191‚Üí      if (this.rateLimitInfo) {
   192‚Üí        this.config.logger.debug('Rate limit info:', this.rateLimitInfo);
   193‚Üí      }
   194‚Üí
   195‚Üí      // Apply response interceptors
   196‚Üí      return await this.interceptors.processResponse(response, originalRequest);
   197‚Üí    } catch (error) {
   198‚Üí      // Apply error interceptors
   199‚Üí      const processedError = await this.interceptors.processError(error, originalRequest);
   200‚Üí      throw processedError;
   201‚Üí    }
   202‚Üí  }
   203‚Üí
   204‚Üí  /**
   205‚Üí   * Logs in a user with username and password.
   206‚Üí   * @param {string} username - The user's username.
   207‚Üí   * @param {string} password - The user's password.
   208‚Üí   * @returns {Promise<UserData>} The user data upon successful login.
   209‚Üí   */
   210‚Üí  async login(username: string, password: string): Promise<UserData> {
   211‚Üí    const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/token`;
   212‚Üí    try {
   213‚Üí      const response = await this._fetch(endpoint, {
   214‚Üí        method: 'POST',
   215‚Üí        headers: {
   216‚Üí          'Content-Type': 'application/json',
   217‚Üí        },
   218‚Üí        body: JSON.stringify({ username, password }),
   219‚Üí      });
   220‚Üí      if (!response.ok) {
   221‚Üí        let errorData;
   222‚Üí        try {
   223‚Üí          errorData = await response.json();
   224‚Üí        } catch (e) {
   225‚Üí          errorData = { message: `Request failed with status ${response.status}` };
   226‚Üí        }
   227‚Üí
   228‚Üí        // Enhanced error message for rate limiting
   229‚Üí        let errorMessage = `API Error: ${response.status} - ${response.statusText}`;
   230‚Üí        if (response.status === 429 && this.rateLimitInfo?.retryAfter) {
   231‚Üí          errorMessage += ` (Rate limited - retry after ${this.rateLimitInfo.retryAfter} seconds)`;
   232‚Üí        }
   233‚Üí
   234‚Üí        const error = {
   235‚Üí            message: errorMessage,
   236‚Üí            status: response.status,
   237‚Üí            data: errorData,
   238‚Üí            rateLimitInfo: this.rateLimitInfo
   239‚Üí        };
   240‚Üí
   241‚Üí        this.config.logger.error('Login failed', error);
   242‚Üí        this.events.emit({
   243‚Üí          type: 'error',
   244‚Üí          error: new Error(error.message),
   245‚Üí          data: error
   246‚Üí        });
   247‚Üí
   248‚Üí        throw error;
   249‚Üí      }
   250‚Üí      const data = await response.json() as TokenResponse;
   251‚Üí
   252‚Üí      // Validate required fields in response
   253‚Üí      if (!data.token || !data.refresh_token || !data.user_id || !data.user_email) {
   254‚Üí        const error = {
   255‚Üí          message: 'Invalid server response: missing required authentication data',
   256‚Üí          status: response.status,
   257‚Üí          data: data
   258‚Üí        };
   259‚Üí
   260‚Üí        this.config.logger.error('Login failed: malformed response', error);
   261‚Üí        this.events.emit({
   262‚Üí          type: 'error',
   263‚Üí          error: new Error(error.message),
   264‚Üí          data: error
   265‚Üí        });
   266‚Üí
   267‚Üí        throw error;
   268‚Üí      }
   269‚Üí
   270‚Üí      const userData: UserData = {
   271‚Üí        id: data.user_id,
   272‚Üí        email: data.user_email,
   273‚Üí        nicename: data.user_nicename,
   274‚Üí        displayName: data.user_display_name,
   275‚Üí      };
   276‚Üí      await this.saveTokensToStorage(data.token, data.refresh_token, userData);
   277‚Üí
   278‚Üí      this.events.emit({
   279‚Üí        type: 'login',
   280‚Üí        data: { userData }
   281‚Üí      });
   282‚Üí
   283‚Üí      return userData;
   284‚Üí    } catch (error) {
   285‚Üí      this.config.logger.error('Login failed', error);
   286‚Üí      throw error;
   287‚Üí    }
   288‚Üí  }
   289‚Üí
   290‚Üí  /**
   291‚Üí   * Logs out the current user by clearing stored tokens.
   292‚Üí   * @returns {Promise<void>}
   293‚Üí   */
   294‚Üí  async logout(): Promise<void> {
   295‚Üí    await this.clearTokensFromStorage();
   296‚Üí
   297‚Üí    this.events.emit({
   298‚Üí      type: 'logout'
   299‚Üí    });
   300‚Üí
   301‚Üí    this.config.logger.info('Logout successful. Tokens cleared.');
   302‚Üí  }
   303‚Üí
   304‚Üí
   305‚Üí
   306‚Üí  /**
   307‚Üí   * Retrieves the current refresh token.
   308‚Üí   * @returns {Promise<string | null>} The refresh token or null if not available.
   309‚Üí   */
   310‚Üí  async getRefreshToken(): Promise<string | null> {
   311‚Üí    if (!this.refreshTokenVal) {
   312‚Üí        await this.loadTokensFromStorage();
   313‚Üí    }
   314‚Üí    return this.refreshTokenVal;
   315‚Üí  }
   316‚Üí
   317‚Üí
   318‚Üí
   319‚Üí
   320‚Üí  /**
   321‚Üí   * Attaches event listeners. Convenience method for events.on().
   322‚Üí   * @param eventType Event type to listen for
   323‚Üí   * @param listener Callback function
   324‚Üí   * @returns Function to remove the listener
   325‚Üí   */
   326‚Üí  on(eventType: JWTProEventType, listener: (event: any) => void): () => void {
   327‚Üí    return this.events.on(eventType, listener);
   328‚Üí  }
   329‚Üí
   330‚Üí  /**
   331‚Üí   * Clean up resources when the client is no longer needed
   332‚Üí   */
   333‚Üí  destroy(): void {
   334‚Üí    this.events.removeAllListeners();
   335‚Üí    this.clearTokensFromStorage(); // Clear tokens from storage as well
   336‚Üí    this.isTokensLoaded = false;
   337‚Üí    this.accessToken = null;
   338‚Üí    this.refreshTokenVal = null;
   339‚Üí    this.userData = null;
   340‚Üí  }
   341‚Üí
   342‚Üí  // Modern API methods - shorter, cleaner interface
   343‚Üí
   344‚Üí  /**
   345‚Üí   * Checks if user is currently authenticated
   346‚Üí   * @returns {Promise<boolean>} True if user is authenticated
   347‚Üí   */
   348‚Üí  async check(): Promise<boolean> {
   349‚Üí    if (!this.accessToken) {
   350‚Üí      await this.loadTokensFromStorage();
   351‚Üí    }
   352‚Üí    return !!this.accessToken;
   353‚Üí  }
   354‚Üí
   355‚Üí  /**
   356‚Üí   * Validates the current token with the server
   357‚Üí   * @returns {Promise<boolean>} True if token is valid
   358‚Üí   */
   359‚Üí  async validate(): Promise<boolean> {
   360‚Üí    if (!this.accessToken) {
   361‚Üí      await this.loadTokensFromStorage();
   362‚Üí      if (!this.accessToken) {
   363‚Üí        this.config.logger.warn('No access token found to validate');
   364‚Üí        return false;
   365‚Üí      }
   366‚Üí    }
   367‚Üí    try {
   368‚Üí      const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/validate`;
   369‚Üí      const response = await this._fetch(endpoint, {
   370‚Üí        method: 'POST',
   371‚Üí        headers: {
   372‚Üí          'Content-Type': 'application/json',
   373‚Üí          'Authorization': `Bearer ${this.accessToken}`
   374‚Üí        },
   375‚Üí      });
   376‚Üí      if (!response.ok) {
   377‚Üí        this.config.logger.warn(`Token validation failed with status ${response.status}: ${response.statusText}`);
   378‚Üí        return false;
   379‚Üí      }
   380‚Üí      const data = await response.json() as ValidateApiResponse;
   381‚Üí      return data.code === 'jwt_auth_valid_token' && data.data.status === 200;
   382‚Üí    } catch (error) {
   383‚Üí      // If validation fails due to an API error (e.g. 401, 403), it's an invalid token.
   384‚Üí      this.config.logger.warn('Token validation failed via API', error);
   385‚Üí      return false;
   386‚Üí    }
   387‚Üí  }
   388‚Üí
   389‚Üí  /**
   390‚Üí   * Refreshes the current access token
   391‚Üí   * @returns {Promise<TokenResponse>} New token response
   392‚Üí   */
   393‚Üí  async refresh(): Promise<TokenResponse> {
   394‚Üí    if (!this.refreshTokenVal) {
   395‚Üí      await this.loadTokensFromStorage();
   396‚Üí      if (!this.refreshTokenVal) {
   397‚Üí        const error = new Error('No refresh token available for refreshing.');
   398‚Üí        this.events.emit({
   399‚Üí          type: 'error',
   400‚Üí          error
   401‚Üí        });
   402‚Üí        throw error;
   403‚Üí      }
   404‚Üí    }
   405‚Üí    const endpoint = `${this.config.baseUrl}/${this.config.authNamespace}/refresh`;
   406‚Üí    try {
   407‚Üí      const response = await this._fetch(endpoint, {
   408‚Üí        method: 'POST',
   409‚Üí        headers: {
   410‚Üí          'Content-Type': 'application/json',
   411‚Üí        },
   412‚Üí        body: JSON.stringify({ refresh_token: this.refreshTokenVal }),
   413‚Üí      });
   414‚Üí      if (!response.ok) {
   415‚Üí        this.config.logger.warn(`Token refresh failed with status ${response.status}: ${response.statusText}`);
   416‚Üí        await this.clearTokensFromStorage(); // Clear tokens if refresh fails, forcing re-login
   417‚Üí
   418‚Üí        const error = new Error('Token refresh failed.');
   419‚Üí        this.events.emit({
   420‚Üí          type: 'error',
   421‚Üí          error,
   422‚Üí          data: { status: response.status, statusText: response.statusText }
   423‚Üí        });
   424‚Üí
   425‚Üí        throw error;
   426‚Üí      }
   427‚Üí      const data = await response.json() as TokenResponse;
   428‚Üí      const userData: UserData = {
   429‚Üí        id: data.user_id,
   430‚Üí        email: data.user_email,
   431‚Üí        nicename: data.user_nicename,
   432‚Üí        displayName: data.user_display_name,
   433‚Üí      };
   434‚Üí      await this.saveTokensToStorage(data.token, data.refresh_token, userData);
   435‚Üí
   436‚Üí      this.events.emit({
   437‚Üí        type: 'tokenRefresh',
   438‚Üí        data: { userData }
   439‚Üí      });
   440‚Üí
   441‚Üí      this.config.logger.info('Token refreshed successfully');
   442‚Üí      return data;
   443‚Üí    } catch (error) {
   444‚Üí      this.config.logger.error('Token refresh failed', error);
   445‚Üí      await this.clearTokensFromStorage(); // Clear tokens if refresh fails, forcing re-login
   446‚Üí      throw error;
   447‚Üí    }
   448‚Üí  }
   449‚Üí
   450‚Üí  /**
   451‚Üí   * Retrieves the current access token
   452‚Üí   * @returns {Promise<string | null>} The access token
   453‚Üí   */
   454‚Üí  async token(): Promise<string | null> {
   455‚Üí    if (!this.accessToken) {
   456‚Üí      await this.loadTokensFromStorage();
   457‚Üí    }
   458‚Üí    return this.accessToken;
   459‚Üí  }
   460‚Üí
   461‚Üí  /**
   462‚Üí   * Retrieves the current user data
   463‚Üí   * @returns {Promise<UserData | null>} The user data
   464‚Üí   */
   465‚Üí  async user(): Promise<UserData | null> {
   466‚Üí    if (!this.userData) {
   467‚Üí      await this.loadTokensFromStorage();
   468‚Üí    }
   469‚Üí    return this.userData;
   470‚Üí  }
   471‚Üí
   472‚Üí  /**
   473‚Üí   * Gets the current rate limit information from the last API request
   474‚Üí   * @returns {RateLimitInfo | null} Rate limit information or null if not available
   475‚Üí   */
   476‚Üí  getRateLimitInfo(): RateLimitInfo | null {
   477‚Üí    return this.rateLimitInfo;
   478‚Üí  }
   479‚Üí
   480‚Üí  /**
