          errorMessage += ` (Rate limited - retry after ${this.rateLimitInfo.retryAfter} seconds)`;
        }

        const error = {
            message: errorMessage,
            status: response.status,
            data: errorData,
            rateLimitInfo: this.rateLimitInfo
        };

        this.config.logger.error('Login failed', error);
        this.events.emit({
          type: 'error',
          error: new Error(error.message),
          data: error
        });

